'use client';
import { useState } from 'react';
import { json } from '@/lib/api/json';

export default function NewCampaign() {
  const [slug, setSlug] = useState('');
  const [headline, setHeadline] = useState('');
  const [goal, setGoal] = useState(10);
  const [action, setAction] = useState('cheer');
  const [blockId, setBlockId] = useState('');
  const [message, setMessage] = useState('');

  const submit = async () => {
    const res = await fetch('/api/create-campaign', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${localStorage.getItem('supabase.auth.token')}`,
      },
      body: JSON.stringify({
        slug,
        headline,
        goal_count: goal,
        target_action: action,
        block_id: blockId,
      }),
    });

    const json = await res.json();
    setMessage(json.error ? 'Error: ' + json.error : '‚úÖ Campaign created!');
  };

  return (
    <div className="max-w-xl mx-auto p-6 text-white space-y-4">
      <h1 className="text-2xl font-bold">üéØ Create Campaign</h1>
      <input
        value={slug}
        onChange={(e) => setSlug(e.target.value)}
        placeholder="Slug (e.g. floss)"
        className="p-2 text-black w-full rounded"
      />
      <input
        value={headline}
        onChange={(e) => setHeadline(e.target.value)}
        placeholder="Headline"
        className="p-2 text-black w-full rounded"
      />
      <input
        type="number"
        value={goal}
        onChange={(e) => setGoal(Number(e.target.value))}
        placeholder="Goal"
        className="p-2 text-black w-full rounded"
      />
      <select
        value={action}
        onChange={(e) => setAction(e.target.value)}
        className="p-2 text-black w-full rounded"
      >
        <option value="cheer">üéâ Cheer</option>
        <option value="echo">üîÅ Echo</option>
        <option value="reflect">ü™û Reflect</option>
        <option value="checkin">‚úÖ Check-in</option>
      </select>
      <input
        value={blockId}
        onChange={(e) => setBlockId(e.target.value)}
        placeholder="Block ID"
        className="p-2 text-black w-full rounded"
      />
      <button onClick={submit} className="bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded">
        Create
      </button>
      {message && <p className="text-green-300 text-sm">{message}</p>}
    </div>
  );
}

