        {supporters.map((s: any, i) => (
          <li key={i} className="bg-zinc-800 p-3 rounded">
            <div className="text-sm text-green-400">User: {s.user_id.slice(0, 8)}</div>
            <div className="text-xs text-zinc-300">
              üéâ {s.cheer || 0} ‚Ä¢ üîÅ {s.echo || 0} ‚Ä¢ ü™û {s.reflect || 0}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

