      </div>

      <section>
        <h2 className="text-lg font-semibold mb-2">Views per Day</h2>
        <ResponsiveContainer width="100%" height={300}>
          <BarChart data={Object.entries(byDay).map(([day, count]) => ({ day, count }))}>
            <XAxis dataKey="day" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar dataKey="count" fill="#6366f1" />
          </BarChart>
        </ResponsiveContainer>
      </section>

