      });
      setProfiles(profileMap);

      if (DEBUG) console.log('[‚úÖ Profiles Loaded]', profileMap);
    };

    fetchData();
  }, [role]);

  const referrers = users.filter((u) => profiles[u.id]?.role === 'affiliate_referrer');

