'use client';

import { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';
import { useCanonicalRole } from '@/hooks/useCanonicalRole';
import { supabase } from '@/admin/lib/supabaseClient';

export default function UsersPage() {
  const router = useRouter();
  const [users, setUsers] = useState<any[]>([]);
  const [profiles, setProfiles] = useState<Record<string, string>>({});
  const [error, setError] = useState('');
  const { role } = useCanonicalRole();

  useEffect(() => {
    if (role === null) return;

    if (role !== 'admin') {
      console.log('üîê [users] Redirecting to login (Reason: Unauthorized)');
      router.push('/login?error=unauthorized');
      return;
    }

    const loadUsersAndRoles = async () => {
      try {
        const { data: userList, error: userError } = await supabase.auth.admin.listUsers();
        if (userError) {
          setError(userError.message);
          return;
        }
        setUsers(userList.users || []);

        const { data: profileList } = await supabase.from('user_profiles').select('user_id, role');
        const profileMap: Record<string, string> = {};
        profileList?.forEach((p) => {
          profileMap[p.user_id] = p.role;
        });
        setProfiles(profileMap);
      } catch (err) {
        console.error('‚ùå Failed to load users or profiles', err);
        setError('Failed to load users');
      }
    };

    loadUsersAndRoles();
  }, [role]);

  const updateRole = async (userId: string, newRole: string) => {
    await supabase
      .from('user_profiles')
      .upsert({ user_id: userId, role: newRole }, { onConflict: 'user_id' });

    setProfiles((prev) => ({ ...prev, [userId]: newRole }));
  };

  return (
    <div className="p-6 text-white">
      <h1 className="text-2xl font-bold mb-4">Users</h1>
      {error && <p className="text-red-500">{error}</p>}
      <table className="w-full text-sm text-left text-gray-300 bg-gray-800 rounded overflow-hidden">
        <thead className="bg-gray-700 text-gray-100 uppercase text-xs tracking-wide">
          <tr>
            <th className="px-4 py-2">Email</th>
            <th className="px-4 py-2">Role</th>
            <th className="px-4 py-2">Created</th>
          </tr>
        </thead>
        <tbody>
          {users.map((u, i) => (
            <tr key={u.id} className={i % 2 === 0 ? 'bg-gray-800' : 'bg-gray-900'}>
              <td className="px-4 py-2">{u.email}</td>
              <td className="px-4 py-2">
                <select
                  value={profiles[u.id] || 'viewer'}
                  onChange={(e) => updateRole(u.id, e.target.value)}
                  className="bg-gray-700 border border-gray-600 rounded px-2 py-1 text-xs"
                >
                  <option value="admin">admin</option>
                  <option value="reseller">reseller</option>
                  <option value="affiliate_referrer">affiliate_referrer</option>
                  <option value="viewer">viewer</option>
                </select>
              </td>
              <td className="px-4 py-2 text-xs">{new Date(u.created_at).toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

'use client';

import { useEffect, useState } from 'react';

export default function VectorExplorerPage() {
  const [summary, setSummary] = useState<any>(null);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    async function fetchSummary() {
      try {
        const res = await fetch('/api/vector/summary');
        if (!res.ok) throw new Error('Failed to load vector summary');
        const data = await res.json();
        setSummary(data);
      } catch (err: any) {
        setError(err.message);
      }
    }
    fetchSummary();
  }, []);

  return (
    <div className="max-w-4xl mx-auto px-4 py-8">
      <h1 className="text-2xl font-bold mb-4">üß† Vector Explorer</h1>

      {error && <p className="text-red-500">Error: {error}</p>}
      {!summary && !error && <p className="text-muted-foreground">Loading...</p>}

      {summary && (
        <div className="space-y-4">
          <div className="border p-4 rounded bg-white/5">
            <p><strong>Collection:</strong> {summary.name}</p>
            <p><strong>Status:</strong> {summary.status}</p>
