// app/api/echo/route.ts
// Use echo() when you need to echo a message
// Use getUserFromRequest() when you need the user context

export const runtime = 'nodejs';

import { createClient } from '@supabase/supabase-js';

const supabase = createClient(
  process.env.NEXT_PUBLIC_SUPABASE_URL!,
  process.env.SUPABASE_SERVICE_ROLE_KEY!
);

const MAX_ECHOES_PER_HOUR = 3;

export async function POST(req: Request) {
  try {
    const token = req.headers.get('Authorization')?.replace('Bearer ', '')?.trim();

    if (!token) {
      return Response.json({ error: 'Missing auth token' }, {
        status: 401,
        headers: { 'Content-Type': 'application/json' },
      });
    }

    const { block_id, message } = await req.json();

    if (!block_id) {
      return Response.json({ error: 'Missing block_id' }, {
        status: 400,
        headers: { 'Content-Type': 'application/json' },
      });
    }

    const {
      data: { user },
      error: userError,
    } = await supabase.auth.getUser(token);

    if (userError || !user?.id) {
      return Response.json({ error: 'Unauthorized' }, {
        status: 401,
        headers: { 'Content-Type': 'application/json' },
      });
    }

    const oneHourAgo = new Date(Date.now() - 60 * 60 * 1000).toISOString();

    const { count, error: rateError } = await supabase
      .from('block_feedback')
      .select('id', { count: 'exact', head: true })
      .eq('block_id', block_id)
      .eq('user_id', user.id)
      .eq('action', 'echo')
      .gte('created_at', oneHourAgo);

    if (rateError) {
      console.error('[‚ö†Ô∏è Rate check error]', rateError.message);
      return Response.json({ error: 'Rate check failed' }, {
        status: 500,
        headers: { 'Content-Type': 'application/json' },
      });
    }

    if ((count ?? 0) >= MAX_ECHOES_PER_HOUR) {
      return Response.json({ error: 'Rate limit exceeded. Try again later.' }, {
        status: 429,
        headers: { 'Content-Type': 'application/json' },
      });
    }

    const { error: insertError } = await supabase
      .from('block_feedback')
      .insert({
        block_id,
        user_id: user.id,
        action: 'echo',
        message: message || null,
      });

    if (insertError) {
      console.error('[‚ùå Insert error]', insertError.message);
      return Response.json({ error: insertError.message }, {
        status: 500,
        headers: { 'Content-Type': 'application/json' },
      });
    }

    return Response.json({ success: true }, {
      status: 200,
      headers: { 'Content-Type': 'application/json' },
    });

  } catch (err: any) {
    console.error('[‚ùå Unexpected echo error]', err);
    return Response.json({ error: err?.message ?? 'Unknown error' }, {
      status: 500,
      headers: { 'Content-Type': 'application/json' },
    });
  }
}

export const runtime = 'nodejs';

import { supabase } from '@/admin/lib/supabaseClient';
import { json } from '@/lib/api/json';
import { NextRequest } from 'next/server';

export async function GET(req: NextRequest) {
  const { searchParams } = new URL(req.url);
  const city = searchParams.get('city');
  const state = searchParams.get('state');

  if (!city || !state) {
    return json({ error: 'Missing city or state' }, { status: 400 });
  }

  const cleanCity = city.trim();
  const cleanState = state.trim();

  // 1. Try cache
  const { data: cached, error: fetchError } = await supabase
    .from('geo_cache')
    .select('lat, lon')
    .eq('city', cleanCity)
    .eq('state', cleanState)
    .maybeSingle();

  if (cached) {
    console.log(`üåç Cache hit for ${cleanCity}, ${cleanState}`);
    return json({ lat: Number(cached.lat), lon: Number(cached.lon) });
  }

  // 2. Fallback to OpenStreetMap's Nominatim
  const query = `${cleanCity}, ${cleanState}, USA`;
  const url = `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(query)}`;

  try {
    const geoRes = await fetch(url, {
      headers: {
        'User-Agent': 'QuickSites/1.0 (admin@quicksites.ai)',
      },
    });

    const results = await geoRes.json();

    if (!Array.isArray(results) || results.length === 0) {
      return json({ lat: 0, lon: 0, error: 'Not found' }, { status: 404 });
    }

    const { lat, lon } = results[0];
    const parsedLat = parseFloat(lat);
    const parsedLon = parseFloat(lon);

    // 3. Cache result
    await supabase.from('geo_cache').insert([
      {
        city: cleanCity,
        state: cleanState,
        lat: parsedLat,
        lon: parsedLon,
      },
    ]);

    console.log(`üåç Cached ${cleanCity}, ${cleanState} ‚Üí ${parsedLat}, ${parsedLon}`);
    return json({ lat: parsedLat, lon: parsedLon });
  } catch (err) {
    console.error('üåç Geocode error:', err);
    return json({ lat: 0, lon: 0, error: 'Internal error' }, { status: 500 });
  }
}

