'use client';

import { useEffect, useRef, useState } from 'react';
import ReCAPTCHA from 'react-google-recaptcha';
import { supabase } from '@/admin/lib/supabaseClient';

export default function LoginClient() {
  const recaptchaRef = useRef<ReCAPTCHA | null>(null);

  const [email, setEmail] = useState('');
  const [message, setMessage] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);

  // ‚úÖ Autofill in dev mode
  useEffect(() => {
    if (process.env.NODE_ENV === 'development') {
      setEmail('sandon@quicksites.ai');
    }
  }, []);

  // ‚úÖ Check for active session on mount
  useEffect(() => {
    supabase.auth.getSession().then(({ data }) => {
      const session = data.session;
      if (session?.user) {
        fetch('/api/session-log', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ referer: document.referrer }),
        })
          .catch((err) => console.error('Error logging session:', err))
          .finally(() => {
            window.location.href = '/auth/callback';
          });
      }
    });
  }, []);

  // ‚úÖ Listen for successful login
  useEffect(() => {
    const {
      data: { subscription },
    } = supabase.auth.onAuthStateChange((event, session) => {
      if (event === 'SIGNED_IN' && session?.user) {
        fetch('/api/session-log', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ referer: document.referrer }),
        })
          .catch((err) => console.error('Error logging session:', err))
          .finally(() => {
            window.location.href = '/auth/callback';
          });
      }
    });

    return () => subscription?.unsubscribe?.();
  }, []);

  const handleLogin = async () => {
    setLoading(true);
    const token = await recaptchaRef.current?.executeAsync();
    recaptchaRef.current?.reset();

    if (!token) {
      setMessage('‚ùå reCAPTCHA failed. Please try again.');
      setLoading(false);
      return;
    }

    const res = await fetch('/api/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email, recaptchaToken: token }),
    });

    const data = await res.json();
    setMessage(
      res.ok
        ? '‚úÖ Check your email for the magic link.'
        : `‚ùå ${data.error || 'Login failed.'}`
    );
    setLoading(false);
  };

  return (
    <main className="min-h-screen flex items-center justify-center bg-background text-foreground px-4">
      <div className="w-full max-w-md bg-zinc-900 p-8 rounded-xl shadow-lg space-y-6">
        <h1 className="text-2xl font-extrabold text-center">Login</h1>

        <input
          id="email"
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          onKeyDown={(e) => {
            if (e.key === 'Enter') {
              e.preventDefault();
              handleLogin();
            }
          }}
          placeholder="you@example.com"
          className="w-full px-4 py-2 rounded-md bg-zinc-800 text-white border border-zinc-700 placeholder-zinc-400 focus:outline-none focus:ring-2 focus:ring-indigo-500"
        />

        <button
          onClick={handleLogin}
          disabled={loading}
          className="w-full bg-indigo-600 hover:bg-indigo-700 text-white py-2 rounded-md font-medium transition disabled:opacity-50"
        >
          {loading ? 'Sending...' : 'Send Magic Link'}
        </button>

        {message && (
          <p
            className={`text-sm text-center ${
              message.startsWith('‚úÖ') ? 'text-green-400' : 'text-red-400'
            }`}
          >
            {message}
          </p>
        )}

        <ReCAPTCHA
          sitekey={process.env.NEXT_PUBLIC_RECAPTCHA_SITE_KEY!}
          size="invisible"
          ref={recaptchaRef}
        />
      </div>
    </main>
  );
}

