'use client';

import { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';
import { useAutoRedirectByRole } from '@/hooks/useAutoRedirectByRole';
import { useSafeAuth } from '@/hooks/useSafeAuth';
import { NavSections } from '@/components/admin/AppHeader/nav-sections';
import UserMenu from '@/components/auth/user-menu';

export default function ViewerHomeRedirect() {
  const [cancelled, setCancelled] = useState(false);
  const [showNav, setShowNav] = useState(false);
  const router = useRouter();
  const { user, role, isLoggedIn } = useSafeAuth();

  useAutoRedirectByRole({
    roleRoutes: {
      viewer: '/viewer/dashboard',
      admin: '/admin/dashboard',
      owner: '/admin/dashboard',
      reseller: '/admin/dashboard',
    },
    fallbackRoute: '/unauthorized',
    enableTestBypass: true,
  });

  useEffect(() => {
    if (typeof window !== 'undefined' && window.location.hostname.includes('quicksites.ai')) {
      if (isLoggedIn && user) setShowNav(true);
    }

    const timeout = setTimeout(() => {
      if (!cancelled) {
        console.warn('[üïí Timeout] Redirect did not resolve in time');
        router.replace('/login?timeout=true');
      }
    }, 10000);

    return () => clearTimeout(timeout);
  }, [cancelled, isLoggedIn, user, router]);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-black text-white">
      <div className="animate-spin rounded-full h-10 w-10 border-b-2 border-white mb-4" />
      <p className="text-sm text-gray-400 mb-2">Redirecting based on your role...</p>
      <p className="text-xs text-gray-500 mb-4">
        Host: {typeof window !== 'undefined' ? window.location.hostname : '...'}
      </p>

      {showNav && (
        <div className="animate-fade-in transition-opacity duration-700 ease-in opacity-100">
          <NavSections />
        </div>
      )}

      {/* {isLoggedIn && user && (
        <div className="animate-fade-in transition-opacity duration-700 ease-in mt-4 opacity-100">
          <UserMenu />
        </div>
      )} */}

      <button
        className="text-xs text-red-400 underline mt-6"
        onClick={() => setCancelled(true)}
      >
        Cancel and go back
      </button>
    </div>
  );
}

'use client';

import { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';
import SafeLink from '../../ui/safe-link';
import { useSafeAuth } from '@/hooks/useSafeAuth';
import UserMenu from '@/components/auth/user-menu';
import { useRequestMeta } from '@/hooks/useRequestMeta';
// import { AdminNavSections } from './AdminNavSections';
import InspirationalQuote from '@/components/ui/inspirational-quote';
import { AvatarMenu } from './avatar-menu';

export default function AppHeader() {
  useEffect(() => {
    let lastScrollY = window.scrollY;
    let ticking = false;
    const header = document.querySelector('header');
    let hideTimeout: NodeJS.Timeout | null = null;

    const handleScroll = () => {
      if (!header) return;

      const currentScrollY = window.scrollY;

      if (currentScrollY > lastScrollY && currentScrollY > 48) {
        if (!header.classList.contains('translate-y-[-100%]')) {
          header.classList.add('translate-y-[-100%]', 'transition-transform', 'duration-300');
        }
      } else {
        header.classList.remove('translate-y-[-100%]');
      }

      if (currentScrollY > 10) {
        header.classList.add('opacity-90', 'backdrop-blur-md');
      } else {
        header.classList.remove('opacity-90', 'backdrop-blur-md');
      }

      lastScrollY = currentScrollY;
    };
    window.addEventListener('scroll', () => {
      if (!ticking) {
        window.requestAnimationFrame(() => {
          handleScroll();
          ticking = false;
        });
        ticking = true;
      }
    });

    header?.addEventListener('mouseenter', () => {
      if (header.classList.contains('translate-y-[-100%]')) {
        header.classList.remove('translate-y-[-100%]');
      }
      if (hideTimeout) clearTimeout(hideTimeout);
    });

    header?.addEventListener('mouseleave', () => {
      hideTimeout = setTimeout(() => {
        if (window.scrollY > 48) {
          header.classList.add('translate-y-[-100%]');
        }
      }, 1200);
    });


return () => window.removeEventListener('scroll', handleScroll);
  }, []);
  const router = useRouter();
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);
  const { user, role, isLoggedIn } = useSafeAuth();
  const { traceId, sessionId } = useRequestMeta();

  useEffect(() => {
    console.debug('[üß≠ AppHeader Role Info]', {
      email: user?.email,
      role,
      traceId,
      sessionId,
    });
  }, [user?.email, role, traceId, sessionId]);

  if (!isLoggedIn || !user) {
    return (
      <header className="px-4 py-2 text-zinc-400 text-sm shadow-sm border-b border-zinc-800">
  <div className="max-w-screen-lg mx-auto flex justify-between items-center">
    <div className="flex items-center gap-4">
      <span>QuickSites</span>
      <div className="text-xs text-cyan-300 max-w-xs">
       <InspirationalQuote tags={["small-business", "seo", "persistence"]} />
      </div>
    </div>
    <a href="/login" className="text-blue-400 hover:underline">
      Log In
    </a>
  </div>
</header>
    );
  }

  return (
    <header className="bg-gray-800 text-white sticky top-0 z-50 px-2 py-[6px] shadow-sm border-b border-zinc-700 min-h-[48px] transition-transform duration-300">
      <div className="flex justify-between items-center max-w-screen-xl mx-auto relative">
        <div className="flex items-center gap-4 overflow-x-auto whitespace-nowrap max-w-full flex-1">
          <SafeLink href="/" className="text-blue-400 hover:underline">
            <img src="/logo_v1.png" alt="QuickSites" className="h-12 w-auto" />
          </SafeLink>

          {/* ‚úÖ Always show inspirational quote */}
          <div className="text-xs text-cyan-300 max-w-xs">
            <InspirationalQuote tags={['small-business', 'seo', 'persistence']} />
          </div>
        </div>

        <div className="ml-2 flex items-center gap-2">
          {isLoggedIn && user ? (
            <div className="flex items-center gap-2">
              <AvatarMenu />
              <div>
                <div>{user.email}</div>
                <div className="text-zinc-500">role: {role}</div>
              </div>
            </div>
          ) : (
            <a href="/login" className="text-blue-400 hover:underline">
              Log In
            </a>
          )}
        </div>
      </div>
    </header>
  );
}

'use client';

import { useState, useEffect, useRef } from 'react';
import { useRouter } from 'next/navigation';
import { motion, AnimatePresence } from 'framer-motion';
import { LogOut, Loader2, UserCircle, Settings, User } from 'lucide-react';
import md5 from 'blueimp-md5';

import { RoleBadge } from './role-badge';
import { useSafeAuth } from '@/hooks/useSafeAuth';
import { createClient } from '@supabase/supabase-js';

export function AvatarMenu() {
  const { user, role, isLoggedIn } = useSafeAuth();
  const router = useRouter();
  const [open, setOpen] = useState(false);
  const [loggingOut, setLoggingOut] = useState(false);
  const menuRef = useRef<HTMLDivElement>(null);

  const avatarUrl =
    user?.avatar_url ||
    `https://gravatar.com/avatar/${md5(user?.email?.trim().toLowerCase() ?? '')}?d=identicon`;

  const isAdmin = ['admin', 'owner', 'reseller'].includes(role);

  useEffect(() => {
    function handleClickOutside(e: MouseEvent) {
      if (menuRef.current && !menuRef.current.contains(e.target as Node)) {
        setOpen(false);
      }
    }
    if (open) {
      document.addEventListener('mousedown', handleClickOutside);
    }
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [open]);

  const handleLogout = async () => {
    setLoggingOut(true);
    try {
      const supabase = createClient(process.env.NEXT_PUBLIC_SUPABASE_URL!, process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!);
      await supabase.auth.signOut();
      router.push('/login?logout=1');
      setTimeout(() => window.location.reload(), 300);
    } finally {
      setLoggingOut(false);
    }
  };

  if (!isLoggedIn || !user) return null;

  return (
    <div className="relative" ref={menuRef}>
      <button
        onClick={() => setOpen(!open)}
        className="focus:outline-none"
        aria-label="Avatar menu"
      >
        <div className="w-6 h-6 sm:w-5 sm:h-5 rounded-full overflow-hidden border border-white">
          <img src={avatarUrl} alt="avatar" className="w-full h-full object-cover" />
        </div>
      </button>

      <AnimatePresence>
        {open && (
          <motion.div
            initial={{ opacity: 0, scale: 0.95, y: -5 }}
            animate={{ opacity: 1, scale: 1, y: 0 }}
            exit={{ opacity: 0, scale: 0.95, y: -5 }}
            transition={{ duration: 0.15 }}
            className="absolute right-0 mt-2 w-56 bg-zinc-900 border border-zinc-700 rounded shadow-xl text-xs z-50 p-3 space-y-3"
          >
            <div className="flex items-center gap-2 text-gray-300">
              <UserCircle size={14} />
              <span className="truncate">{user.email}</span>
            </div>

            <RoleBadge role={role} />

            <div className="border-t border-zinc-700 pt-2 space-y-2">
              <button
                onClick={() => router.push('/profile')}
                className="flex items-center gap-2 hover:underline text-left text-gray-300 w-full"
              >
                <User size={14} />
                My Profile
              </button>

              {isAdmin && (
                <button
                  onClick={() => router.push('/admin/settings')}
                  className="flex items-center gap-2 hover:underline text-left text-gray-300 w-full"
                >
                  <Settings size={14} />
                  Admin Settings
                </button>
              )}
            </div>

            <button
              onClick={handleLogout}
              disabled={loggingOut}
              className={`flex items-center gap-2 text-red-400 hover:underline text-left w-full ${
                loggingOut ? 'opacity-50 cursor-not-allowed' : ''
              }`}
            >
              {loggingOut ? (
                <>
                  <Loader2 size={14} className="animate-spin" />
                  Logging out...
                </>
              ) : (
                <>
                  <LogOut size={14} />
                  Log Out
                </>
              )}
            </button>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

