'use client';

import { usePathname } from 'next/navigation';
import { useSmartNav } from '../../../hooks/useSmartNav';
import SafeLink from '../../ui/safe-link';
import { NavBadge } from '../../ui/nav-badge';
import { useLiveAdminStats } from '../../../hooks/useLiveAdminStats';

export function NavSections() {
  const pathname = usePathname();
  const { nav, loading } = useSmartNav();
  const { unclaimed, errors } = useLiveAdminStats();

  const matches = (prefix: string) => pathname?.startsWith(prefix);

  if (loading) return null;

  return (
    <nav className="flex flex-col gap-6 text-sm">
      {/* üñ• Desktop */}
      <div className="hidden md:flex flex-wrap gap-4 items-center">
        {nav.map((section) => (
          <details
            key={section.label}
            className="group"
            open={section.routes.some((r) => matches(r.href))}
          >
            <summary
              className={`cursor-pointer font-semibold ${section.color} group-open:underline`}
            >
              {section.label}
            </summary>
            <div className="ml-2 flex flex-wrap gap-2 mt-1">
              {section.routes.map((r) => {
                const isDynamic = r.href.includes('[slug]') || r.href.includes(':');
                const labelWithCount =
                  r.href === '/admin/leads' && unclaimed && unclaimed > 0
                    ? `${r.label} (${unclaimed})`
                    : r.href === '/admin/drafts' && unclaimed && unclaimed > 0
                      ? `${r.label} (${unclaimed})`
                      : r.label;

                return (
                  <SafeLink
                    key={r.href}
                    href={r.href}
                    target={r.external ? '_blank' : undefined}
                    title={r.title}
                    onClick={() => {
                      fetch('/api/log-event', {
                        method: 'POST',
                        body: JSON.stringify({ href: r.href, type: 'nav_click' }),
                      });
                    }}
                  >
                    {labelWithCount}
                    <NavBadge flag={r.flags?.[0]} />
                  </SafeLink>
                );
              })}
            </div>
          </details>
        ))}
      </div>

      {/* üì± Mobile */}
      <div className="md:hidden">
        <label htmlFor="mobile-nav" className="block mb-1 font-medium text-muted-foreground">
          Navigate:
        </label>
        <select
          id="mobile-nav"
          className="w-full p-2 border rounded text-sm"
          onChange={(e) => {
            if (e.target.value) {
              fetch('/api/log-event', {
                method: 'POST',
                body: JSON.stringify({ href: e.target.value, type: 'nav_click' }),
              });
              window.location.href = e.target.value;
            }
          }}
        >
          <option value="">Select a page</option>
          {nav.flatMap((section) => [
            <optgroup key={section.label} label={section.label}>
              {section.routes.map((r) => (
                <option key={r.href} value={r.href}>
                  {r.label.replace(/^[^\w\s]+ /, '')}
                </option>
              ))}
            </optgroup>,
          ])}
        </select>
      </div>
    </nav>
  );
}

import { useEffect, useState } from 'react';
import { supabase } from '@/admin/lib/supabaseClient';

export default function ActivityFeed({
  domain_id,
  lead_id,
}: {
  domain_id?: string;
  lead_id?: string;
}) {
  const [logs, setLogs] = useState<any[]>([]);

  useEffect(() => {
    const query = supabase
      .from('user_action_logs')
      .select('*')
      .order('timestamp', { ascending: false });
    if (domain_id) {
      query.eq('domain_id', domain_id).then(({ data }) => setLogs(data || []));
    } else if (lead_id) {
      query.eq('lead_id', lead_id).then(({ data }) => setLogs(data || []));
    }
  }, [domain_id, lead_id]);

  return (
    <div className="text-sm text-gray-300 mt-4">
      <h2 className="text-sm font-bold mb-2 text-gray-400">Activity Feed</h2>
      {logs.length === 0 && <p className="text-xs text-gray-500">No activity yet.</p>}
      <ul className="space-y-2">
        {logs.map((log, i) => (
          <li key={log.id} className="bg-gray-900 px-3 py-2 rounded">
            <div className="text-xs text-green-400">{new Date(log.timestamp).toLocaleString()}</div>
            <div className="text-xs">
              {log.action_type.replace(/_/g, ' ')} by <strong>{log.triggered_by || '‚Äî'}</strong>
            </div>
            {log.notes && <div className="text-xs italic text-gray-400 mt-1">{log.notes}</div>}
          </li>
        ))}
      </ul>
    </div>
  );
}

'use client';

import { useEffect, useState } from 'react';
import { ChevronLeft, ChevronRight } from 'lucide-react';
import clsx from 'clsx';
import { AdminNavSections } from '@/components/admin/AppHeader/AdminNavSections';

export default function AdminSidebar({
  onToggle,
}: {
  onToggle?: (collapsed: boolean) => void;
}) {
  const [isCollapsed, setIsCollapsed] = useState(false);

  // Load stored collapsed state and auto-collapse if screen is small
  useEffect(() => {
    const stored = localStorage.getItem('admin-sidebar-collapsed');
    if (stored === 'true') setIsCollapsed(true);

    const handleResize = () => {
      if (window.innerWidth < 768) {
        setIsCollapsed(true);
      }
    };

    handleResize();
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Persist collapsed state and notify parent
  useEffect(() => {
    localStorage.setItem('admin-sidebar-collapsed', String(isCollapsed));
    onToggle?.(isCollapsed);
  }, [isCollapsed, onToggle]);

  return (
    <aside
      className={clsx(
        'group fixed top-0 left-0 z-40 h-screen bg-zinc-900 border-r border-zinc-800 text-white overflow-y-auto transition-all duration-300 flex flex-col',
        isCollapsed ? 'w-14 group-hover:w-72' : 'w-72'
      )}
    >
      <div className="relative">
        <button
          onClick={() => setIsCollapsed(!isCollapsed)}
          className="p-2 m-2 hover:bg-zinc-800 rounded transition relative"
          title={isCollapsed ? 'Expand Sidebar' : 'Collapse Sidebar'}
        >
          {isCollapsed ? <ChevronRight size={20} /> : <ChevronLeft size={20} />}
          {isCollapsed && (
            <span className="absolute left-14 top-1/2 -translate-y-1/2 whitespace-nowrap rounded bg-zinc-800 px-2 py-1 text-xs text-white opacity-0 group-hover:opacity-100 transition-all z-20 shadow pointer-events-none">
              Expand Sidebar
            </span>
          )}
        </button>

        {/* Optional handle zone for hover rescue */}
        {isCollapsed && (
          <div className="absolute top-0 right-0 w-2 h-full bg-transparent group-hover:bg-zinc-700 rounded-r cursor-pointer transition-all duration-200" />
        )}
      </div>

      <div className={clsx('flex-1', isCollapsed && 'overflow-hidden')}>
        <h1>hello</h1>
        <AdminNavSections collapsed={isCollapsed} />
      </div>
    </aside>
  );
}

