import Image from 'next/image';
import QRCode from 'react-qr-code';
import { formatDistanceToNowStrict, parseISO } from 'date-fns';
import { useState } from 'react';
import { createClient } from '@supabase/supabase-js';
import { toast } from 'react-hot-toast';
import { Database } from '@/types/supabase';

export type ClaimPosterProps = {
  domain: string;
  offerEndsAt: string; // ISO string
  leadA: {
    name: string;
    logoUrl?: string;
  };
  leadB: {
    name: string;
    logoUrl?: string;
  };
  qrUrl: string;
  imageSrc: string;
  arcOffsetY?: number; // vertical offset for arc in px
  logoOffsetY?: number; // vertical offset for logo in px
  arcRadius?: number; // arc radius for domain text
  onEditStart?: () => void;
  onEditEnd?: () => void;
  campaignId?: string;
};

export default function ClaimPoster({
  domain,
  offerEndsAt,
  leadA,
  leadB,
  qrUrl,
  imageSrc,
  arcOffsetY = 0,
  logoOffsetY = 0,
  arcRadius = 100,
  onEditStart,
  onEditEnd,
  campaignId,
}: ClaimPosterProps) {
  console.log('[üß© ClaimPoster props]', { campaignId, arcOffsetY, logoOffsetY, arcRadius });

  const supabase = createClient<Database>(process.env.NEXT_PUBLIC_SUPABASE_URL!, process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!);
  const [editing, setEditing] = useState(false);
  const [settings, setSettings] = useState({ arcOffsetY, logoOffsetY, arcRadius });
  const [original, setOriginal] = useState({ arcOffsetY, logoOffsetY, arcRadius });

  const arcPath = `M50,${80 + settings.arcOffsetY} A${settings.arcRadius},${settings.arcRadius} 0 0 1 250,${80 + settings.arcOffsetY}`;
  const timeLeft = formatDistanceToNowStrict(parseISO(offerEndsAt), {
    unit: 'day',
    roundingMethod: 'floor',
  });

  const handleCancel = () => {
    setSettings(original);
    setEditing(false);
    onEditEnd?.();
  };

  const handleSave = async () => {
    setOriginal(settings);
    setEditing(false);
    onEditEnd?.();

    if (campaignId) {
      console.log('[üìù Saving poster settings]', {
        id: campaignId,
        ...settings,
      });

      const { error } = await supabase
        .from('campaigns')
        .update({
          arc_offset_y: settings.arcOffsetY,
          logo_offset_y: settings.logoOffsetY,
          arc_radius: settings.arcRadius,
        })
        .eq('id', campaignId);

      if (error) {
        console.error('Failed to save poster settings:', error);
        toast.error('Failed to save poster settings.');
      } else {
        console.log('[‚úÖ Poster settings saved]');
        toast.success('Poster settings saved.');
      }
    } else {
      console.warn('‚ö†Ô∏è No campaignId provided ‚Äî settings not saved.');
      toast.error('No campaign ID provided.');
    }
  };

  const toggleEdit = () => {
    setEditing(!editing);
    if (!editing) {
      onEditStart?.();
    } else {
      onEditEnd?.();
    }
  };

  return (
    <div
      className="bg-[#080a03] text-white p-6 rounded-lg w-full max-w-md mx-auto text-center font-semibold shadow-lg border border-zinc-700"
      onClick={(e) => e.stopPropagation()}
    >
      <div className="text-xs text-zinc-300 uppercase mb-2 tracking-wide">Towing Website SEO</div>

      <div className="mb-6" style={{ transform: `translateY(${settings.arcOffsetY}px)`, height: `${settings.arcRadius}px` }}>
        <svg width="100%" height="230" viewBox="0 0 300 150">
          <path
            id="arcPath"
            d={arcPath}
            fill="transparent"
          />
          <text fill="white" fontSize="14" fontWeight="600" textAnchor="middle">
            <textPath href="#arcPath" startOffset="50%">
              {domain.toUpperCase()}
            </textPath>
          </text>
        </svg>
      </div>

      <div className="w-28 h-20 mx-auto mb-4" style={{ transform: `translateY(${settings.logoOffsetY}px)` }}>
        <Image src={imageSrc} alt="Tow Truck Logo" width={112} height={80} className="object-contain w-full h-full" />
      </div>

      <div className="flex justify-center items-center my-4 gap-4">
        <LeadCard name={leadA.name} logoUrl={leadA.logoUrl} />
        <div className="text-sm text-zinc-500">or</div>
        <LeadCard name={leadB.name} logoUrl={leadB.logoUrl} />
      </div>

      <div className="w-24 h-24 mx-auto mb-2 bg-white p-2 rounded shadow-md">
        <QRCode value={qrUrl} size={96} />
      </div>

      <div className="text-xs text-zinc-300">Scan to claim it first</div>

      <div className="mt-3 text-yellow-400 text-sm tracking-wider">
        72-HOUR OFFERING
      </div>
      <div className="text-xs text-zinc-400">
        (Time left: {timeLeft})
      </div>

      <div className="mt-4">
        <button
          onClick={toggleEdit}
          className="text-xs underline text-blue-400 hover:text-blue-300"
        >
          {editing ? 'Close Settings' : 'Poster Settings'}
        </button>
      </div>

      {editing && (
        <div className="mt-4 space-y-3 text-left text-sm">
          <div>
            <label className="block mb-1">Arc Offset Y: {settings.arcOffsetY}px</label>
            <input
              type="range"
              min={-40}
              max={80}
              value={settings.arcOffsetY}
              onChange={(e) => setSettings({ ...settings, arcOffsetY: parseInt(e.target.value) })}
              className="w-full"
            />
          </div>
          <div>
            <label className="block mb-1">Logo Offset Y: {settings.logoOffsetY}px</label>
            <input
              type="range"
              min={-40}
              max={80}
              value={settings.logoOffsetY}
              onChange={(e) => setSettings({ ...settings, logoOffsetY: parseInt(e.target.value) })}
              className="w-full"
            />
          </div>
          <div>
            <label className="block mb-1">Arc Radius: {settings.arcRadius}px</label>
            <input
              type="range"
              min={50}
              max={200}
              value={settings.arcRadius}
              onChange={(e) => setSettings({ ...settings, arcRadius: parseInt(e.target.value) })}
              className="w-full"
            />
          </div>

          <div className="flex justify-end gap-3 mt-2">
            <button onClick={handleCancel} className="text-xs px-2 py-1 rounded bg-zinc-700 hover:bg-zinc-600">Cancel</button>
            <button onClick={handleSave} className="text-xs px-2 py-1 rounded bg-green-600 hover:bg-green-500">Save</button>
          </div>
        </div>
      )}
    </div>
  );
}

function LeadCard({ name, logoUrl }: { name: string; logoUrl?: string }) {
  return (
    <div className="w-28 h-20 flex items-center justify-center bg-zinc-800 rounded border border-zinc-600 p-2">
      {logoUrl ? (
        <Image src={logoUrl} alt={name} width={100} height={60} className="object-contain" />
      ) : (
        <span className="text-sm text-white text-center leading-tight">{name}</span>
      )}
    </div>
  );
}

// components/admin/campaigns/edit-campaign-modal.tsx
'use client';

import { Lead } from '@/types/lead.types';
import { Dispatch, SetStateAction, useEffect, useMemo, useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import dayjs from 'dayjs';
import LeadSelectorWithRadius from '@/components/admin/campaigns/lead-selector-with-radius';
import { getDistanceMiles } from '@/lib/utils/distance';
import { CampaignType } from '@/types/campaign.types';
import { getLatLonForCityState } from '@/lib/utils/geocode';

const US_STATES = [
  'AL', 'AK', 'AZ', 'AR', 'CA', 'CO', 'CT', 'DE', 'FL', 'GA', 'HI', 'ID', 'IL', 'IN', 'IA',
  'KS', 'KY', 'LA', 'ME', 'MD', 'MA', 'MI', 'MN', 'MS', 'MO', 'MT', 'NE', 'NV', 'NH', 'NJ',
  'NM', 'NY', 'NC', 'ND', 'OH', 'OK', 'OR', 'PA', 'RI', 'SC', 'SD', 'TN', 'TX', 'UT', 'VT',
  'VA', 'WA', 'WV', 'WI', 'WY'
];

export type Props = {
  campaign: CampaignType;
  allLeads: Lead[];
  selectedLeadIds: string[];
  setSelectedLeadIds: Dispatch<SetStateAction<string[]>>;
  leadsByCampaign: Record<string, Lead[]>;
  setEditingCampaign: (c: CampaignType | null) => void;
  geoCenter?: { lat: number; lon: number } | null;
  currentCampaignId?: string | null;
  currentCampaignExpiresAt?: string | null;
};

export default function EditCampaignModal(props: Props) {
  const {
    campaign,
    allLeads,
    selectedLeadIds,
    setSelectedLeadIds,
    leadsByCampaign,
    setEditingCampaign,
    geoCenter,
    currentCampaignId,
    currentCampaignExpiresAt,
  } = props;

  useEffect(() => {
    const handleKey = (e: KeyboardEvent) => {
      if (e.key === 'Escape') setEditingCampaign(null);
    };
    window.addEventListener('keydown', handleKey);
    return () => window.removeEventListener('keydown', handleKey);
  }, []);
  const [radius, setRadius] = useState(50);
  const [availableIndustries, setAvailableIndustries] = useState<string[]>([]);
  const [campaignIndustry, setCampaignIndustry] = useState(campaign.industry || '');
  const [campaignState, setCampaignState] = useState(campaign.state || '');
  const [campaignCity, setCampaignCity] = useState(campaign.city || '');
  const [campaignStatus, setCampaignStatus] = useState<CampaignType['status']>(campaign.status || 'draft');
  const [cityLat, setCityLat] = useState<number | null>(campaign.city_lat ?? null);
  const [cityLon, setCityLon] = useState<number | null>(campaign.city_lon ?? null);

  // fetch industries
  useEffect(() => {
    async function fetchIndustries() {
      try {
        const res = await fetch('/api/industries');
        const data = await res.json();
        if (Array.isArray(data)) {
          setAvailableIndustries(data.sort());
        }
      } catch (err) {
        console.error('Failed to load industries:', err);
      }
    }
    fetchIndustries();
  }, []);

  // sync lat lon
  useEffect(() => {
    async function syncLatLon() {
      if (campaignCity && campaignState) {
        const coords = await getLatLonForCityState(campaignCity, campaignState);
        if (coords) {
          setCityLat(coords.lat);
          setCityLon(coords.lon);
        }
      }
    }
    syncLatLon();
  }, [campaignCity, campaignState]);

  // sync campaign state
  useEffect(() => {
    setCampaignState(campaign.state || '');
    setCampaignIndustry(campaign.industry || '');
    setCampaignCity(campaign.city || '');
    setCampaignStatus(campaign.status || 'draft');
    setCityLat(campaign.city_lat ?? null);
    setCityLon(campaign.city_lon ?? null);
  }, [campaign]);

  // fetch linked leads
  useEffect(() => {
    async function fetchLinkedLeads() {
      if (!campaign?.id) return;

      const res = await fetch(`/api/campaigns/${campaign.id}/leads`);
      const json = await res.json();

      if (res.ok && Array.isArray(json.lead_ids)) {
        setSelectedLeadIds(json.lead_ids);
      } else {
        console.warn('Failed to fetch campaign-linked leads:', json.error || json);
      }
    }

    fetchLinkedLeads();
  }, [campaign?.id]);

  // filter leads by industry
  const filteredLeads = useMemo(() => {
    const leads = campaignIndustry
      ? allLeads.filter((l) => l.industry?.toLowerCase().trim() === campaignIndustry.toLowerCase().trim())
      : allLeads;
    return leads;
  }, [allLeads, campaignIndustry]);

  // count leads by radius
  const leadCountByRadius = useMemo(() => {
    if (!cityLat || !cityLon) return {};
    return {
      '0‚Äì10 mi': filteredLeads.filter((l) => getDistanceMiles(l.address_lat!, l.address_lon!, cityLat, cityLon) < 10).length,
      '10‚Äì25 mi': filteredLeads.filter((l) => {
        const d = getDistanceMiles(l.address_lat!, l.address_lon!, cityLat, cityLon);
        return d >= 10 && d < 25;
      }).length,
      '25‚Äì50 mi': filteredLeads.filter((l) => {
        const d = getDistanceMiles(l.address_lat!, l.address_lon!, cityLat, cityLon);
        return d >= 25 && d < 50;
      }).length,
    };
  }, [filteredLeads, cityLat, cityLon]);

  return (
    <div className="fixed inset-0 z-50 bg-black bg-opacity-50 flex items-center justify-center">
      <div className="bg-zinc-900 text-white p-6 rounded shadow-xl w-full max-w-md max-h-[90vh] overflow-y-auto relative">
        <h2 className="text-lg font-bold mb-4">Edit Campaign</h2>
        <form
          onSubmit={async (e) => {
            e.preventDefault();
            try {
              const res = await fetch('/api/campaigns/update', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                id: campaign.id,
                name: campaign.name,
                city: campaignCity,
                state: campaignState,
                city_lat: cityLat,
                city_lon: cityLon,
                starts_at: campaign.starts_at,
                ends_at: campaign.ends_at,
                lead_ids: selectedLeadIds,
                industry: campaignIndustry,
                status: campaignStatus,
              }),
              });

              const payload = {
                id: campaign.id,
                name: campaign.name,
                city: campaignCity,
                state: campaignState,
                city_lat: cityLat,
                city_lon: cityLon,
                starts_at: campaign.starts_at,
                ends_at: campaign.ends_at,
                lead_ids: selectedLeadIds,
                industry: campaignIndustry,
                status: campaignStatus,
              };

              console.log('[üì§ Saving Campaign]', payload);
              const json = await res.json();
              if (json.updated) {
                console.log('[‚úÖ Updated from server]', json.updated);
                setCampaignState(json.updated.state || '');
                setCampaignIndustry(json.updated.industry || '');
                setCampaignCity(json.updated.city || '');
                setCampaignStatus(json.updated.status || 'draft');
                setCityLat(json.updated.city_lat ?? null);
                setCityLon(json.updated.city_lon ?? null);
              }

              if (!res.ok || json.error) {
                console.error('‚ùå Update failed:', json.error || 'Unknown error');
                return;
              }

              setEditingCampaign(null);
              location.reload();
            } catch (err) {
              console.error('‚ùå Network error while updating campaign:', err);
            }
          }}
        >
          <label className="block mb-3">
            Name:
            <input
              className="mt-1 w-full px-2 py-1 rounded bg-zinc-800 border border-zinc-700 text-white"
              value={campaign.name}
              onChange={(e) => (campaign.name = e.target.value)}
            />
          </label>
          <label className="block mb-3">
            City:
            <input
              className="mt-1 w-full px-2 py-1 rounded bg-zinc-800 border border-zinc-700 text-white"
              value={campaignCity}
              onChange={(e) => setCampaignCity(e.target.value)}
              autoComplete="off"
            />
          </label>
          <label className="block mb-3">
            State:
            <span className="ml-2 text-xs text-zinc-400">[DB: {campaign.state}]</span>
            <select
              className="mt-1 w-full px-2 py-1 rounded bg-zinc-800 border border-zinc-700 text-white"
              value={campaignState}
              onChange={(e) => setCampaignState(e.target.value)}
            >
              <option value="">Select State</option>
              {US_STATES.map((abbr) => (
                <option key={abbr} value={abbr}>
                  {abbr}
                </option>
              ))}
            </select>
          </label>
          <label className="block mb-3">
            Status:
            <select
              className="mt-1 w-full px-2 py-1 rounded bg-zinc-800 border border-zinc-700 text-white"
              value={campaignStatus}
              onChange={(e) => setCampaignStatus(e.target.value as CampaignType['status'])}
            >
              <option value="draft">Draft</option>
              <option value="published">Published</option>
            </select>
          </label>

          <label className="block mb-3">
            Industry:
            <span className="ml-2 text-xs text-zinc-400">[DB: {campaign.industry}]</span>
            <select
              className="mt-1 w-full px-2 py-1 rounded bg-zinc-800 border border-zinc-700 text-white"
              value={campaignIndustry}
              onChange={(e) => setCampaignIndustry(e.target.value)}
            >
              <option value="">Select Industry</option>
              {availableIndustries.map((ind) => (
                <option key={ind} value={ind}>
                  {ind}
                </option>
              ))}
            </select>
          </label>
          <label className="block mb-3">
            Starts At:
            <DatePicker
              className="mt-1 w-full px-2 py-1 rounded bg-zinc-800 border border-zinc-700 text-white"
              selected={dayjs(campaign.starts_at).toDate()}
              onChange={(date) => (campaign.starts_at = dayjs(date).toISOString())}
              showTimeSelect
              dateFormat="Pp"
            />
          </label>
          <label className="block mb-3">
            Ends At:
            <DatePicker
              className="mt-1 w-full px-2 py-1 rounded bg-zinc-800 border border-zinc-700 text-white"
              selected={dayjs(campaign.ends_at).toDate()}
              onChange={(date) => (campaign.ends_at = dayjs(date).toISOString())}
              showTimeSelect
              dateFormat="Pp"
            />
          </label>

          <div className="mb-4">
            <label className="block text-sm font-medium mb-1">
              Link Leads:
              <div className="text-xs text-zinc-400 mt-1">
