// components/admin/campaigns/lead-selector-with-radius.tsx
'use client';

import { useEffect, useState } from 'react';
import { getDistanceMiles } from '@/lib/utils/distance';

type Lead = {
    id: string;
    business_name: string;
    address_lat?: number | null;
    address_lon?: number | null;
    address_city?: string | null;
    address_state?: string | null;
    industry?: string | null;
    current_campaign_id?: string | null;
    current_campaign_expires_at?: string | null;
  };


type Props = {
  leads: Lead[];
  selectedLeadIds: string[];
  setSelectedLeadIds: (ids: string[]) => void;
  cityLat: number | null | undefined;
  cityLon: number | null | undefined;
  radius: number;
  setRadius: (radius: number) => void;
  geoCenter?: { lat: number; lon: number } | null;
  industry?: string | null;
  state?: string | null;
  currentCampaignId?: string | null;
  currentCampaignExpiresAt?: string | null;
};

export default function LeadSelectorWithRadius({
  leads,
  selectedLeadIds,
  setSelectedLeadIds,
  cityLat,
  cityLon,
  radius,
  setRadius,
  geoCenter,
  industry,
  currentCampaignId,
  currentCampaignExpiresAt,
}: Props) {
    const isLeadDisabled = (lead: Lead): boolean => {
        if (!lead.current_campaign_id) return false;

        const isExpired =
          !lead.current_campaign_expires_at ||
          new Date(lead.current_campaign_expires_at) < new Date();

        const isSameCampaign = lead.current_campaign_id === currentCampaignId;

        return !(isExpired || isSameCampaign);
      };

    const toggle = (id: string) => {
      setSelectedLeadIds(
        selectedLeadIds.includes(id)
          ? selectedLeadIds.filter((x) => x !== id)
          : [...selectedLeadIds, id]
    );
  };

  const resolvedLat = cityLat ?? geoCenter?.lat ?? null;
  const resolvedLon = cityLon ?? geoCenter?.lon ?? null;

  const getDist = (l: Lead) =>
    l.address_lat && l.address_lon && resolvedLat && resolvedLon
      ? getDistanceMiles(l.address_lat, l.address_lon, resolvedLat, resolvedLon)
      : Infinity;

  const filteredLeads = leads.filter((l) =>
    !industry || l.industry?.trim().toLowerCase() === industry.trim().toLowerCase()
  );

  const sortedLeads = filteredLeads
    .filter((l) => getDist(l) <= radius)
    .sort((a, b) => getDist(a) - getDist(b));

  const groupByRange = [
    { label: '0‚Äì10 mi', min: 0, max: 10 },
    { label: '10‚Äì25 mi', min: 10, max: 25 },
    { label: '25‚Äì50 mi', min: 25, max: 50 },
  ];

  const renderLeadLine = (lead: Lead, distanceLabel: string) => {
    const disabled = isLeadDisabled(lead);

    return (
      <div key={lead.id} className="flex flex-col gap-1 mb-2">
        <label
          className={`flex items-center gap-2 ${disabled ? 'opacity-50 cursor-not-allowed' : ''}`}
          title={disabled ? 'Already part of an active campaign' : ''}
        >
          <input
            type="checkbox"
            disabled={disabled}
            checked={selectedLeadIds.includes(lead.id)}
            onChange={() => toggle(lead.id)}
          />
          <div className="text-sm text-white">
            <span className="font-semibold">{lead.business_name}</span>{' '}
            <span className="text-xs text-zinc-400">
              ({lead.address_city}, {lead.address_state} ‚Ä¢ {lead.industry}) ‚Äî {distanceLabel}
            </span>
          </div>
        </label>
      </div>
    );
  };


  const renderGroup = (label: string, min: number, max: number) => {
    const group = sortedLeads.filter((l) => {
      const dist = getDist(l);
      return dist >= min && dist < max;
    });
    if (!group.length) return null;

    return (
      <div key={label} className="pt-2">
        <div className="text-xs font-semibold text-zinc-400 mb-1">{label}</div>
        {group.map((lead) => renderLeadLine(lead, `${getDist(lead).toFixed(1)} mi`))}
      </div>
    );
  };

  const outsideRadius = filteredLeads.filter((l) => getDist(l) > radius);

  return (
    <div className="mb-4">
      <label className="text-sm font-semibold block mb-1">Radius Filter:</label>
      <select
        className="bg-zinc-800 text-white border border-zinc-700 rounded px-2 py-1 mb-3"
        value={radius}
        onChange={(e) => setRadius(Number(e.target.value))}
      >
        {[10, 25, 50, 100].map((r) => (
          <option key={r} value={r}>
            Within {r} miles
          </option>
        ))}
      </select>

      <div className="rounded border border-zinc-700 p-3 bg-zinc-800 divide-y divide-zinc-700 max-h-[320px] overflow-y-auto">
        {groupByRange.map(({ label, min, max }) => renderGroup(label, min, max))}

        {outsideRadius.length > 0 && (
          <div className="pt-4">
            <div className="text-xs font-semibold text-zinc-400 mb-1">Other Leads</div>
            {outsideRadius.map((lead) => renderLeadLine(lead, 'outside radius'))}
          </div>
        )}
      </div>
    </div>
  );
}

// components/admin/city-marker.tsx

'use client';

import { CircleMarker, Popup } from 'react-leaflet';
import { useMemo } from 'react';
import { useRouter } from 'next/navigation';

interface CityMarkerProps {
  point: {
    lat: number;
    lon: number;
    city: string;
    state: string;
    leads: number;
    leadNames: string[];
    leadIds: string[];
    domains: number;
    domainNames: string[];
    industry?: string;
  };
  zoom: number;
  getColor: (point: any) => string;
  router: ReturnType<typeof useRouter>;
  onClick?: () => void;
}

export default function CityMarker({
  point,
  zoom,
  getColor,
  router,
  onClick,
}: CityMarkerProps) {
  const shouldRender = useMemo(() => {
    return !(zoom < 4 && point.leads + point.domains < 3);
  }, [zoom, point]);

  if (!shouldRender) return null;

  return (
    <CircleMarker
      center={[point.lat, point.lon]}
      radius={6 + (point.leads + point.domains) * 0.5}
      pathOptions={{
        color: getColor(point),
        fillColor: getColor(point),
        fillOpacity: 0.7,
      }}
      eventHandlers={{
        click: () => onClick?.(),
      }}
    >
      <Popup className="w-64 p-4 bg-gray-800 text-white rounded-lg shadow-lg">
        <div className="space-y-2">
          <div className="text-lg font-bold">
            <div className="flex justify-between">
              {point.city}, {point.state}
            </div>
            <div className="flex justify-between">
              {point.leads} leads, {point.domains} domains
            </div>
          </div>
          <div className="text-sm">Leads: {point.leads}</div>
          {point.leadNames.length > 0 && (
            <div className="text-xs text-gray-300">
              {point.leadNames.join(', ')}
            </div>
          )}
          <div className="text-sm mt-1">Domains: {point.domains}</div>
          {point.domainNames.length > 0 && (
            <div className="text-xs text-gray-300">
              {point.domainNames.join(', ')}
            </div>
          )}
          {point.industry && (
            <div className="text-xs text-amber-300 italic">
              {point.industry}
            </div>
          )}

          {point.leads >= 0 && (
            <>
            <button
              onClick={() => {
                const query = new URLSearchParams({
                  city: point.city,
                  state: point.state,
                  leadIds: point.leadIds.join(','),
                  industry: point.industry || '',
                });
                router.push(`/admin/start-campaign?${query}`);
              }}
              className="mt-3 px-3 py-2 bg-blue-600 text-white rounded-md text-sm hover:bg-blue-700 w-full"
              >
                üöÄ Start Campaignz
              </button>
            </>
          )}
        </div>
      </Popup>
    </CircleMarker>
  );
}

import { useEffect, useState } from 'react';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';

type Comment = {
  id: string;
  message: string;
  author_email: string;
  created_at: string;
};

export default function CommentThread({ snapshotId }: { snapshotId: string }) {
  const [comments, setComments] = useState<Comment[]>([]);
  const [newComment, setNewComment] = useState('');
  const [author, setAuthor] = useState('');

  const loadComments = async () => {
    const res = await fetch(`/api/comments?snapshot_id=${snapshotId}`);
    const data = await res.json();
    setComments(data);
  };

  useEffect(() => {
    if (snapshotId) loadComments();
  }, [snapshotId]);

  const submitComment = async () => {
    if (!newComment.trim()) return;
    const res = await fetch('/api/comments', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        snapshot_id: snapshotId,
        author_email: author || 'anonymous',
        message: newComment,
      }),
    });
    if (res.ok) {
      setNewComment('');
      loadComments();
    }
  };

  return (
    <div className="p-4 border-t mt-4 bg-muted/20 rounded">
      <h3 className="text-lg font-semibold mb-2">Comments</h3>

      {comments.length === 0 && (
        <p className="text-sm text-muted-foreground mb-4">No comments yet.</p>
      )}

      <ul className="space-y-3 mb-4">
        {comments.map((c) => (
          <li key={c.id} className="bg-white p-3 rounded shadow-sm">
            <p className="text-sm">{c.message}</p>
            <p className="text-xs text-muted-foreground mt-1">
              {c.author_email} ‚Äì {new Date(c.created_at).toLocaleString()}
            </p>
          </li>
        ))}
      </ul>

      <div className="space-y-2">
        <Input
          placeholder="Your email (optional)"
          value={author}
          onChange={(e) => setAuthor(e.target.value)}
        />
        <Textarea
          placeholder="Leave a comment..."
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
        />
        <Button onClick={submitComment} size="sm">
          Post Comment
        </Button>
      </div>
    </div>
  );
}

'use client';

import { createContext, useContext, useEffect, useState, ReactNode } from 'react';
import { supabase } from '@/lib/supabase/client';

import { getSupabase } from '@/lib/supabase/server';

type CurrentUser = {
  id: string;
  email: string;
  name?: string;
  avatar_url?: string;
  role: string;
  bio?: string;
  plan?: string;
};

export type CurrentUserContextType = {
  user: CurrentUser | null;
  ready: boolean;
  role: 'admin' | 'editor' | 'viewer' | 'owner';
  hasRole: (roles: string[]) => boolean;
  refetch: () => void;
};

export const CurrentUserContext = createContext<CurrentUserContextType>({
  user: null,
  ready: false,
  role: 'viewer',
  hasRole: () => false,
  refetch: () => {},
});

export function CurrentUserProvider({ children }: { children: ReactNode }) {
  const [user, setUser] = useState<CurrentUser | null>(null);
  const [ready, setReady] = useState<boolean>(false);

  const load = async () => {
    const { data: sessionData } = await supabase.auth.getSession();
    const u = sessionData?.session?.user;

    if (!u?.id) {
      setUser(null);
      setReady(true);
      return;
    }

    const { data: profile, error } = await supabase
      .from('user_profiles')
      .select('*')
      .eq('user_id', u.id)
      .single();

    if (error) {
      console.error('‚ùå Failed to fetch user_profiles:', error.message);
      setUser(null);
      setReady(true);
      return;
    }

    const normalizedRole = (profile.role || 'viewer').toLowerCase() as CurrentUser['role'];

    const user: CurrentUser = {
      id: u.id,
      email: u.email ?? profile.email,
      name: profile.name ?? '',
      avatar_url: profile.avatar_url ?? '',
      role: normalizedRole,
      bio: profile.bio ?? '',
      plan: profile.plan ?? 'free',
    };

    console.log('‚úÖ [User loaded]', {
      email: user.email,
      role: user.role,
      plan: user.plan,
    });

    setUser(user);
    setReady(true);
  };

  useEffect(() => {
    load();
  }, []);

  const hasRole = (roles: string[]) =>
    user?.role ? roles.map((r) => r.toLowerCase()).includes(user.role.toLowerCase()) : false;

  return (
    <CurrentUserContext.Provider
      value={{
        user,
        ready,
        role: (user?.role as 'admin' | 'editor' | 'viewer' | 'owner') || 'viewer',
        hasRole,
        refetch: load,
      }}
    >
      {children}
    </CurrentUserContext.Provider>
  );
}

// components/admin/grid-map.tsx
'use client';

import { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';
import dynamic from 'next/dynamic';

import { supabase } from '@/admin/lib/supabaseClient';
import GridSidebar from './grid-sidebar';
import { resolveGeo } from '@/lib/resolveGeo';
import type { CityPoint } from '@/types/grid';

// Dynamically load the Leaflet map so it's only rendered on the client
const SafeLeafletMap = dynamic(() => import('./safe-leaflet-map'), {
  ssr: false,
});

const _cache: { points?: CityPoint[]; lastFetched?: number } = {};

export default function GridMap() {
  const [industry, setIndustry] = useState('');
  const [points, setPoints] = useState<CityPoint[]>([]);
  const [zoom, setZoom] = useState(4);
  const [showHotspotsOnly, setShowHotspotsOnly] = useState(false);
  const router = useRouter();

  useEffect(() => {
    const load = async () => {
      const now = Date.now();
      if (_cache.points && _cache.lastFetched && now - _cache.lastFetched < 60_000) {
        setPoints(_cache.points);
        return;
      }
      const { data: campaignLinks } = await supabase
        .from('campaign_leads')
        .select('campaign_id, lead_id');

      const { data: campaigns } = await supabase
        .from('campaigns')
        .select('id, name, city, state');
      const { data: leads } = await supabase
        .from('leads')
        .select('id, business_name, address_city, address_state, industry');

      const { data: domains } = await supabase
        .from('domains')
        .select('city, state, domain');

      const geo: Record<string, CityPoint> = {};

      for (const l of leads || []) {
        const key = `${l.address_city}, ${l.address_state}`;
        geo[key] = geo[key] || {
          city: l.address_city,
          state: l.address_state,
          leadsQty: 0,
          domains: 0,
          leads: [],
          domainNames: [],
          leadIds: [],
          industryCounts: {},
        };
        geo[key].leadsQty += 1;
        const isUnclaimed = !campaignLinks?.some((cl) => cl.lead_id === l.id);
        if (l.business_name) geo[key].leads.push({ id: l.id, name: l.business_name, isClaimed: !isUnclaimed, campaignId: '', industry: l.industry || '' });
        geo[key].leadIds.push(l.id);
        const indKey = (l.industry || '').trim().toLowerCase();
        geo[key].industryCounts![indKey] = (geo[key].industryCounts![indKey] || 0) + 1;
      }

      for (const d of domains || []) {
        const key = `${d.city}, ${d.state}`;
        geo[key] = geo[key] || {
          city: d.city,
          state: d.state,
          leads: 0,
          domains: 0,
          leadNames: [],
          domainNames: [],
          leadIds: [],
          industryCounts: {},
        };
        geo[key].domains += 1;
        if (d.domain) geo[key].domainNames.push(d.domain);
      }

      const enriched = await Promise.all(
        Object.values(geo).map(async (entry) => {
          const { lat, lon } = await resolveGeo(entry.city, entry.state);
          const primaryIndustry = Object.entries(entry.industryCounts || {}).reduce(
            (acc: [string, number], [ind, count]) =>
              typeof count === 'number' && count > acc[1] ? [ind, count] : acc,
            ['', 0]
          )[0];

          const campaignIds = new Set(
            campaignLinks
              ?.filter((cl) => entry.leadIds.includes(cl.lead_id))
              .map((cl) => cl.campaign_id)
          );

          const campaignNames = campaigns
            ?.filter((c) => campaignIds.has(c.id))
            .map((c) => c.name) ?? [];

          const unclaimed = entry.leadIds.filter(
            (id) => !campaignLinks?.some((cl) => cl.lead_id === id)
          ).length;
          const unclaimedByIndustry: Record<string, number> = {};

          for (const lead of entry.leads) {
            const ind = (lead.industry || '').trim().toLowerCase();
            if (!lead.isClaimed) {
              unclaimedByIndustry[ind] = (unclaimedByIndustry[ind] || 0) + 1;
            }
          }

          const has2PlusUnclaimedInSameIndustry = Object.values(unclaimedByIndustry).some(
            (count) => count >= 2
          );

          return {
            ...entry,
            lat,
            lon,
            industry: primaryIndustry,
            campaigns: campaignNames,
            unclaimedLeadCount: unclaimed,
            has2PlusUnclaimedInSameIndustry,
          };
        })
      );

      setPoints(enriched);
      _cache.points = enriched;
      _cache.lastFetched = now;
    };

    load();
  }, []);

  const getColor = (p: CityPoint) => {
    if (p.has2PlusUnclaimedInSameIndustry) return 'red';
    if (p.unclaimedLeadCount && p.unclaimedLeadCount >= 1) return 'yellow';
    if (p.leadsQty >= 2 && p.domains > 0) return 'green';
    if (p.leadsQty >= 2) return 'orange';
    if (p.domains > 0) return 'blue';
    if (p.leadsQty > 0) return 'yellow';
    return 'gray';
  };

  const getUnclaimedLeadCount = (p: CityPoint) => {
    return p.leads.filter((l) => !l.isClaimed).length;
  };

  const filteredPoints = points.filter((p) => {
    if (!industry || industry === '') return true;
    if (!p.industry) return false;
    return p.industry.trim().toLowerCase() === industry.trim().toLowerCase();
  });

  const visiblePoints = showHotspotsOnly
    ? filteredPoints.filter((p) => p.has2PlusUnclaimedInSameIndustry)
    : filteredPoints;

  return (
    <div className="p-6 text-white">
      <div className="flex items-center justify-between mb-6">

        <h1 className="text-3xl font-bold">üåç The Grid</h1>
        <button
          className="text-xs bg-zinc-800 hover:bg-zinc-700 px-3 py-1 rounded border border-zinc-600"
          onClick={() => {
            _cache.points = undefined;
            _cache.lastFetched = 0;
            location.reload();
          }}
        >
          üîÑ Refresh
        </button>
        <div className="md:w-2/5 w-full">
          <GridSidebar
            industry={industry}
            setIndustry={setIndustry}
            filteredPoints={filteredPoints}
            showHotspotsOnly={showHotspotsOnly}
            setShowHotspotsOnly={setShowHotspotsOnly}
            // visiblePoints={visiblePoints}
          />
        </div>
      </div>
      <div className="flex flex-col md:flex-row gap-6">
        <div className="w-full h-[600px] border border-gray-700 rounded overflow-hidden">
          <SafeLeafletMap
            points={visiblePoints}
            zoom={zoom}
            setZoom={setZoom}
            router={router}
            getColor={getColor}
          />
        </div>
      </div>
      <div className="text-xs text-zinc-400 mt-2">
          <div className="flex gap-4 items-center text-sm mt-2">
            <div className="flex items-center gap-1"><span className="inline-block w-3 h-3 bg-yellow-500 rounded-full"></span> 1 Lead</div>
            <div className="flex items-center gap-1"><span className="inline-block w-3 h-3 bg-orange-500 rounded-full"></span> 2+ Leads</div>
            <div className="flex items-center gap-1"><span className="inline-block w-3 h-3 bg-blue-500 rounded-full"></span> 1+ Domain</div>
            <div className="flex items-center gap-1"><span className="inline-block w-3 h-3 bg-green-500 rounded-full"></span> Lead + Domain</div>
            <div className="flex items-center gap-1"><span className="inline-block w-3 h-3 bg-red-600 rounded-full"></span> 2+ Unclaimed in 1 Industry</div>
          </div>
        </div>
    </div>
  );
}

