'use client';

import { useEffect } from 'react';
import {
  MapContainer,
  Marker,
  TileLayer,
  Tooltip,
  useMap,
  Popup,
} from 'react-leaflet';
import MarkerClusterGroup from 'react-leaflet-cluster';
import L from 'leaflet';
import type { CityPoint } from '@/types/grid';
import type { AppRouterInstance } from 'next/dist/shared/lib/app-router-context.shared-runtime';
import type LMap from 'leaflet';

import 'leaflet/dist/leaflet.css';
import 'leaflet.markercluster/dist/MarkerCluster.css';
import 'leaflet.markercluster/dist/MarkerCluster.Default.css';

function getIndustryIcon(industry?: string): string {
  const key = (industry || '').toLowerCase();
  if (key.includes('tow')) return 'üöõ';
  if (key.includes('concrete')) return 'üèóÔ∏è';
  if (key.includes('roof')) return 'üß±';
  if (key.includes('plumb')) return 'üöΩ';
  if (key.includes('lawn') || key.includes('landscap')) return 'üå±';
  return 'üß∞';
}

function getBorderColorClass(p: CityPoint): string {
  if (p.leadsQty >= 2 && p.domains > 0) return 'border-green-500';
  if (p.leadsQty >= 2) return 'border-orange-500';
  if (p.domains > 0) return 'border-blue-500';
  if (p.leadsQty > 0) return 'border-yellow-400';
  return 'border-gray-400';
}

function getPointKey(p: CityPoint): string {
  return `${p.city}${p.state}`;
}

function MapEvents({
  setZoom,
  mapRef,
  points,
  hoveredPointId,
}: {
  setZoom: (z: number) => void;
  mapRef: React.MutableRefObject<LMap.Map | null>;
  points: CityPoint[];
  hoveredPointId: string | null;
}) {
  const map = useMap();

  useEffect(() => {
    mapRef.current = map;
  }, [map]);

  useEffect(() => {
    setZoom(map.getZoom());

    const onZoom = () => setZoom(map.getZoom());
    map.on('zoomend', onZoom);
    // return () => map.off('zoomend', onZoom);
  }, [map, setZoom]);

  useEffect(() => {
    const coords = points
      .filter((p) => typeof p.lat === 'number' && typeof p.lon === 'number')
      .map((p) => [p.lat!, p.lon!] as [number, number]);

    if (coords.length > 0) {
      const bounds = L.latLngBounds(coords);
      map.fitBounds(bounds, { padding: [50, 50] });
    }
  }, [map, points]);

  useEffect(() => {
    if (!hoveredPointId) return;
    const match = points.find((p) => getPointKey(p) === hoveredPointId);
    if (match?.lat && match.lon) {
      map.flyTo([match.lat, match.lon], Math.max(map.getZoom(), 10));
    }
  }, [hoveredPointId, points, map]);

  return null;
}

export default function MapView({
  points,
  zoom,
  setZoom,
  router,
  getColor,
  mapRef,
  hoveredPointId,
}: {
  points: CityPoint[];
  zoom: number;
  setZoom: (z: number) => void;
  router: AppRouterInstance;
  getColor: (p: CityPoint) => string;
  mapRef: React.MutableRefObject<LMap.Map | null>;
  hoveredPointId: string | null;
}) {
  return (
    <MapContainer
      center={[39.5, -98.35]}
      zoom={zoom}
      scrollWheelZoom
      style={{ height: '100%', width: '100%' }}
    >

      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

      <MapEvents
        setZoom={setZoom}
        mapRef={mapRef}
        points={points}
        hoveredPointId={hoveredPointId}
      />

      <MarkerClusterGroup
        iconCreateFunction={(cluster: any) => {
          const markers = cluster.getAllChildMarkers();
          const counts: Record<string, number> = {};

          markers.forEach((marker: any) => {
            const emoji = marker.getElement()?.innerText || 'üß∞';
            counts[emoji] = (counts[emoji] || 0) + 1;
          });

          const summary = Object.entries(counts)
            .map(([emoji, count]) => `${emoji}x${count}`)
            .join(' ');

          return L.divIcon({
            html: `<div class="emoji-cluster">${summary}</div>`,
            className: '',
            iconSize: [40, 40],
          });
        }}
      >
        {points.map((p, i) =>
          typeof p.lat === 'number' && typeof p.lon === 'number' ? (
            <Marker
              key={`${getPointKey(p)}-${i}`}
              position={[p.lat, p.lon]}
              icon={L.divIcon({
                className: '',
                html: `<div class="emoji-marker ${getBorderColorClass(p)} ${
                  getPointKey(p) === hoveredPointId ? 'glow' : ''
                }">${getIndustryIcon(p.industry)}</div>`,
                iconSize: [30, 30],
                iconAnchor: [15, 30],
                popupAnchor: [0, -30],
              })}
            >
                <Popup>
                <div className="space-y-1 text-sm text-black">
                    <strong>
                    {getIndustryIcon(p.industry)} {p.city}, {p.state}
                    </strong>
                    {Array.isArray(p.leads) && p.leads.length > 0 && (
                      <div className="mt-1 text-xs text-zinc-300">
                        Leads:
                        <div className="text-zinc-500 text-[10px] font-medium mb-1">üîµ Assigned</div>
                        <ul className="list-disc list-inside">
                          {p.leads.filter((l) => l.isClaimed).map((l) => (
                            <li key={l.id}>
                              <a
                                href={`/admin/leads/${l.id}`}
                                className="text-blue-500 underline"
                                title="Assigned to campaign"
                              >
                                {l.name}
                              </a>
                            </li>
                          ))}
                        </ul>
                        {p.leads.some((l) => !l.isClaimed) && (
                          <>
                            <div className="text-zinc-500 text-[10px] font-medium mt-2">üü° Unclaimed</div>
                            <ul className="list-disc list-inside">
                              {p.leads.filter((l) => !l.isClaimed).map((l) => (
                                <li key={l.id}>
                                  <a
                                    href={`/admin/leads/${l.id}`}
                                    className="text-yellow-600 font-semibold underline"
                                    title="Not part of any campaign yet"
                                  >
                                    {l.name} üü°
                                  </a>
                                </li>
                              ))}
                            </ul>
                          </>
                        )}
                      </div>
                    )}

                    <div>
                      {p.leadsQty} lead(s), {p.domains} domain(s)
                      {typeof p.unclaimedLeadCount === 'number' && (
                        <div>üü° {p.unclaimedLeadCount} unclaimed</div>
                      )}
                      {Array.isArray(p.campaigns) && p.campaigns.length > 0 && (
                        <div>
                          üìã {p.campaigns.length} campaign(s):
                          <ul className="list-disc list-inside text-xs mt-1">
                      {p.campaigns.map((c, i) => (
                        <li key={i}>
                          <a
                            href={`/admin/campaigns?highlight=${encodeURIComponent(c)}`}
                            className="text-blue-600 underline hover:text-blue-800"
                          >
                            {c}
                          </a>
                        </li>
                      ))}
                    </ul>
                        </div>
                      )}
                    </div>
                    <div>{p.industry || 'Industry N/A'}</div>

                    {p.leadsQty >= 2 && (
                    <button
                        className="mt-1 px-2 py-1 text-xs bg-blue-600 text-white rounded hover:bg-blue-700"
                        onClick={(e) => {
                        e.preventDefault();
                        e.stopPropagation();
                        const query = new URLSearchParams({
                            city: p.city,
                            state: p.state,
                            industry: p.industry || '',
                            leadIds: (p.leadIds || []).join(','),
                        });
                        router.push(`/admin/start-campaign?${query}`);
                        }}
                    >
                        üöÄ Start Campaign
                    </button>
                    )}
                </div>
                </Popup>
            </Marker>
          ) : null
        )}
      </MarkerClusterGroup>
    </MapContainer>
  );
}

'use client';

import { Menu } from '@headlessui/react';
import { useRouter } from 'next/navigation';
import { supabase } from '@/admin/lib/supabaseClient';
import Link from 'next/link';

export default function ProfileDropdown() {
  const router = useRouter();

  const handleLogout = async () => {
    await supabase.auth.signOut();

    // Clear any local auth state if needed
    router.replace('/login?logout=1');
    router.refresh(); // ‚úÖ Triggers SSR rerender of AppHeader
    setTimeout(() => window.location.reload(), 200); // optional hard reload
  };


  return (
    <Menu as="div" className="relative inline-block text-left">
      <Menu.Button className="rounded-full bg-zinc-700 text-white px-3 py-1 text-sm hover:bg-zinc-600">
        üë§ Profile
      </Menu.Button>
      <Menu.Items className="absolute right-0 mt-2 w-36 origin-top-right rounded-md bg-zinc-800 shadow-lg ring-1 ring-zinc-900 focus:outline-none z-50">
        <div className="py-1">
          <Menu.Item>
            {({ active }) => (
              <Link
                href="/admin/profile"
                className={`block px-4 py-2 text-sm ${
                  active ? 'bg-zinc-700 text-white' : 'text-zinc-300'
                }`}
              >
                My Profile
              </Link>
            )}
          </Menu.Item>
          <Menu.Item>
            {({ active }) => (
              <button
                onClick={handleLogout}
                className={`w-full text-left px-4 py-2 text-sm ${
                  active ? 'bg-red-700 text-white' : 'text-red-400'
                }`}
              >
                Log Out
              </button>
            )}
          </Menu.Item>
        </div>
      </Menu.Items>
    </Menu>
  );
}

'use client';

import { Fragment } from 'react';
import { z, ZodObject, ZodFirstPartyTypeKind } from 'zod';
import { Input } from '@/components/ui/input';
import { FieldLabel } from '@/components/ui';
import { Switch } from '@/components/ui/switch';
import { Select, SelectTrigger, SelectContent, SelectItem } from '@/components/ui/select';
import useTypedQueryParam from '@/hooks/useTypedQueryParam';

function FieldRenderer({
  keyPrefix,
  fieldKey,
  validator,
}: {
  keyPrefix: string;
  fieldKey: string;
  validator: any;
}) {
  const fullKey = keyPrefix ? `${keyPrefix}.${fieldKey}` : fieldKey;
  const type = validator._def.typeName;
  const enumValues = validator._def?.values;

  const isArray = type === ZodFirstPartyTypeKind.ZodArray;
  const innerType = isArray ? validator._def.type._def.typeName : null;

  let queryType: any = 'string';
  if (type === ZodFirstPartyTypeKind.ZodNumber || innerType === ZodFirstPartyTypeKind.ZodNumber)
    queryType = isArray ? 'number[]' : 'number';
  if (type === ZodFirstPartyTypeKind.ZodBoolean || innerType === ZodFirstPartyTypeKind.ZodBoolean)
    queryType = isArray ? 'boolean[]' : 'boolean';
  if (isArray) queryType = 'json[]';
  if (type === ZodFirstPartyTypeKind.ZodObject) queryType = 'json';

  const [value, setValue] = useTypedQueryParam(fullKey, isArray ? [] : {}, queryType);

  if (type === ZodFirstPartyTypeKind.ZodObject) {
    const nestedFields = Object.entries(validator.shape);
    return (
      <div className="border rounded p-4 space-y-2 bg-gray-50 dark:bg-gray-900">
        <FieldLabel className="text-xs uppercase tracking-wide text-muted-foreground">{fieldKey}</FieldLabel>
        {nestedFields.map(([nestedKey, nestedValidator]) => (
          <FieldRenderer
            key={nestedKey}
            keyPrefix={fullKey}
            fieldKey={nestedKey}
            validator={nestedValidator}
          />
        ))}
      </div>
    );
  }

  if (type === ZodFirstPartyTypeKind.ZodEnum && enumValues) {
    return (
      <div className="space-y-1">
        <FieldLabel>{fieldKey}</FieldLabel>
        <Select value={value as string} onValueChange={setValue}>
          <SelectTrigger id={fullKey} />
          <SelectContent>
            {enumValues.map((v: string) => (
              <SelectItem key={v} value={v}>
                {v}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
      </div>
    );
  }

  if (type === ZodFirstPartyTypeKind.ZodBoolean) {
    return (
      <div className="space-y-1">
        <FieldLabel htmlFor={fullKey}>{fieldKey}</FieldLabel>
        <Switch
          id={fullKey}
          checked={value === true || value === 'true'}
          onCheckedChange={(v: boolean) => setValue(v)}
        />
      </div>
    );
  }

  if (isArray) {
    const asString = Array.isArray(value) ? value.join(', ') : '';
    return (
      <div className="space-y-1">
        <FieldLabel htmlFor={fullKey}>{fieldKey}</FieldLabel>
        <Input
          id={fullKey}
          value={asString}
          onChange={(e) => {
            const raw = e.target.value;
            const parsed = raw
              .split(',')
              .map((s) => s.trim())
              .filter(Boolean);
            setValue(parsed);
          }}
        />
      </div>
    );
  }

  return (
    <div className="space-y-1">
      <FieldLabel htmlFor={fullKey}>{fieldKey}</FieldLabel>
      <Input
        id={fullKey}
        type={queryType}
        value={value as string}
        onChange={(e) => setValue(e.target.value)}
      />
    </div>
  );
}

export default function QueryParamEditor({ schema, slug }: { schema: z.ZodSchema; slug: string }) {
  const shape = (schema as ZodObject<any>).shape;
  const fields = Object.entries(shape);

  return (
    <div className="space-y-6">
      {fields.map(([key, validator]) => (
        <Fragment key={key}>
          <FieldRenderer keyPrefix="" fieldKey={key} validator={validator} />
        </Fragment>
      ))}
    </div>
  );
}

