'use client';

import { useEffect, useState, useRef } from 'react';
import { motion } from 'framer-motion';
import { ChevronLeft, ChevronRight } from 'lucide-react';
import clsx from 'clsx';
import { AdminNavSections } from '@/components/admin/AppHeader/AdminNavSections';

export default function AdminSidebar({
  onToggle,
}: {
  onToggle?: (collapsed: boolean) => void;
}) {
  const [isCollapsed, setIsCollapsed] = useState(false);
  const [isMobile, setIsMobile] = useState(false);
  const touchStartX = useRef<number | null>(null);

  // Detect mobile
  useEffect(() => {
    const update = () => setIsMobile(window.innerWidth < 768);
    update();
    window.addEventListener('resize', update);
    return () => window.removeEventListener('resize', update);
  }, []);

  // Load from storage
  useEffect(() => {
    const stored = localStorage.getItem('admin-sidebar-collapsed');
    if (stored === 'true' || window.innerWidth < 768) setIsCollapsed(true);
  }, []);

  useEffect(() => {
    localStorage.setItem('admin-sidebar-collapsed', String(isCollapsed));
    onToggle?.(isCollapsed);
  }, [isCollapsed, onToggle]);

  // Swipe close (mobile)
  useEffect(() => {
    const handleTouchStart = (e: TouchEvent) => {
      touchStartX.current = e.touches[0].clientX;
    };
    const handleTouchEnd = (e: TouchEvent) => {
      if (touchStartX.current === null) return;
      const deltaX = e.changedTouches[0].clientX - touchStartX.current;
      if (deltaX < -50 && !isCollapsed) setIsCollapsed(true);
      touchStartX.current = null;
    };
    window.addEventListener('touchstart', handleTouchStart);
    window.addEventListener('touchend', handleTouchEnd);
    return () => {
      window.removeEventListener('touchstart', handleTouchStart);
      window.removeEventListener('touchend', handleTouchEnd);
    };
  }, [isCollapsed]);

  useEffect(() => {
    const handleKey = (e: KeyboardEvent) => {
      if (e.key === 'Escape' && !isCollapsed) setIsCollapsed(true);
    };
    window.addEventListener('keydown', handleKey);
    return () => window.removeEventListener('keydown', handleKey);
  }, [isCollapsed]);

  return (
    <>
      {/* Backdrop for mobile */}
      {isMobile && !isCollapsed && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 0.4 }}
          exit={{ opacity: 0 }}
          transition={{ duration: 0.2 }}
          className="fixed inset-0 bg-black z-30"
          onClick={() => setIsCollapsed(true)}
        />
      )}

      {/* Sidebar container */}
      <motion.aside
        animate={{ width: isCollapsed ? 56 : 288 }} // Tailwind w-14 vs w-72
        transition={{ type: 'spring', stiffness: 250, damping: 30 }}
        className={clsx(
          'fixed top-0 left-0 z-40 h-screen bg-zinc-900 border-r border-zinc-800 text-white overflow-y-auto flex flex-col group'
        )}
      >
        <div className="relative">
          <button
            onClick={() => setIsCollapsed(!isCollapsed)}
            className="p-2 m-2 hover:bg-zinc-800 rounded transition relative"
            title={isCollapsed ? 'Expand Sidebar' : 'Collapse Sidebar'}
          >
            {isCollapsed ? <ChevronRight size={20} /> : <ChevronLeft size={20} />}
            {isCollapsed && (
              <span className="absolute left-14 top-1/2 -translate-y-1/2 whitespace-nowrap rounded bg-zinc-800 px-2 py-1 text-xs text-white opacity-0 group-hover:opacity-100 transition-all z-20 shadow pointer-events-none">
                Expand Sidebar
              </span>
            )}
          </button>

          {isCollapsed && (
            <div className="absolute top-0 right-0 w-2 h-full bg-transparent group-hover:bg-zinc-700 rounded-r cursor-pointer transition-all duration-200" />
          )}
        </div>

        <div className={clsx('flex-1', isCollapsed && 'overflow-hidden')}>
          <AdminNavSections collapsed={isCollapsed} />
        </div>
      </motion.aside>
    </>
  );
}

// components/admin/safe-leaflet-map.tsx
'use client';

import { useEffect, useRef, useState } from 'react';
import ReactDOM from 'react-dom/client';
import dynamic from 'next/dynamic';
import type { CityPoint } from '@/types/grid';
import type { AppRouterInstance } from 'next/dist/shared/lib/app-router-context.shared-runtime';
import type L from 'leaflet';

// Lazy load the map view to avoid SSR + hydration conflicts
const MapView = dynamic(() => import('./map-view'), { ssr: false });

type Props = {
  points: CityPoint[];
  zoom: number;
  setZoom: React.Dispatch<React.SetStateAction<number>>;
  router: AppRouterInstance;
  getColor: (p: CityPoint) => string;
};

export default function SafeLeafletMap({ points, zoom, setZoom, router, getColor }: Props) {
  const [mapVisible, setMapVisible] = useState(false);
  const [hydrated, setHydrated] = useState(false);
  const mapRef = useRef<L.Map | null>(null);
  const mountRef = useRef<HTMLDivElement | null>(null);
  const rootRef = useRef<ReactDOM.Root | null>(null);

  useEffect(() => {
    if (!mapVisible || !mountRef.current || rootRef.current) return;

    rootRef.current = ReactDOM.createRoot(mountRef.current);
    rootRef.current.render(
      <MapView
        points={points}
        zoom={zoom}
        setZoom={setZoom}
        router={router}
        getColor={getColor}
        mapRef={mapRef}
        hoveredPointId={null}
      />
    );
  }, [mapVisible]);


  useEffect(() => {
    setHydrated(true);
  }, []);

  const stats = points.reduce(
    (acc, p) => {
      if (p.has2PlusUnclaimedInSameIndustry) acc.red++;
      else if (p.leadsQty >= 2 && p.domains > 0) acc.green++;
      else if (p.leadsQty >= 2) acc.orange++;
      else if (p.domains > 0) acc.blue++;
      else if (p.leadsQty > 0) acc.yellow++;
      return acc;
    },
    { yellow: 0, orange: 0, blue: 0, green: 0, red: 0 }
  );

  const monthlyValue = points.reduce((sum, p) => {
    if (p.has2PlusUnclaimedInSameIndustry) sum += 49;
    return sum;
  }, 0);

  return (
    <div className="relative h-full w-full">
      {!mapVisible && (
        <button
          className="absolute z-10 left-4 top-4 px-4 py-2 bg-blue-600 text-white rounded shadow"
          onClick={() => setMapVisible(true)}
        >
          Load Map
        </button>
      )}
      <div ref={mountRef} className="h-full w-full" />

      {/* Always show legend when map is visible and hydrated */}
      {mapVisible && hydrated && (
        <div className="absolute bottom-4 left-4 text-sm text-white bg-black/60 p-2 rounded shadow z-[1000]">
          <div className="flex gap-2 items-center">
            <div className="w-3 h-3 bg-yellow-500 rounded-full" /> {stats.yellow}
            <div className="w-3 h-3 bg-orange-500 rounded-full" /> {stats.orange}
            <div className="w-3 h-3 bg-blue-500 rounded-full" /> {stats.blue}
            <div className="w-3 h-3 bg-green-500 rounded-full" /> {stats.green}
            <div className="w-3 h-3 bg-red-600 rounded-full" /> {stats.red}
          </div>
          <div className="mt-1 text-lime-300">üí∞ ${monthlyValue.toFixed(2)} / month</div>
        </div>
      )}
    </div>
  );
}

'use client';

import { useEffect, useState } from 'react';
import { createClient } from '@supabase/supabase-js';
import { useRouter } from 'next/navigation';
import { Tooltip, TooltipTrigger, TooltipContent } from '@/components/ui';
import { ClipboardCopy } from 'lucide-react';

const supabase = createClient(
  process.env.NEXT_PUBLIC_SUPABASE_URL!,
  process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!
);

export default function SiteSettingsPanel({ siteId }: { siteId: string }) {
  const router = useRouter();
  const [bizName, setBizName] = useState('');
  const [location, setLocation] = useState('');
  const [slug, setSlug] = useState('');
  const [slugAvailable, setSlugAvailable] = useState<boolean | null>(null);
  const [colorScheme, setColorScheme] = useState('');
  const [isPublished, setIsPublished] = useState(false);
  const [customDomain, setCustomDomain] = useState('');
  const [analytics, setAnalytics] = useState<{ visits: number } | null>(null);
  const [loading, setLoading] = useState(false);
  const [seoTitle, setSeoTitle] = useState('');
  const [seoDescription, setSeoDescription] = useState('');
  const [twitterHandle, setTwitterHandle] = useState('');
  const [siteName, setSiteName] = useState('');

  useEffect(() => {
    if (!siteId) return;
    (async () => {
      const { data, error } = await supabase
        .from('sites')
        .select('*')
        .eq('id', siteId)
        .maybeSingle();

      if (!error && data) {
        setBizName(data.business_name || '');
        setLocation(data.location || '');
        setSlug(data.slug || '');
        setColorScheme(data.color_scheme || '');
        setIsPublished(data.published || false);
        setCustomDomain(data.custom_domain || '');
        setSeoTitle(data.seo_title || '');
        setSeoDescription(data.seo_description || '');
        setTwitterHandle(data.twitter_handle || '');
        setSiteName(data.site_name || '');
      }
    })();
  }, [siteId]);

  useEffect(() => {
    if (!slug) return setSlugAvailable(null);
    const delay = setTimeout(async () => {
      const { data } = await supabase
        .from('sites')
        .select('id')
        .eq('slug', slug)
        .not('id', 'eq', siteId)
        .maybeSingle();
      setSlugAvailable(!data);
    }, 300);
    return () => clearTimeout(delay);
  }, [slug]);

  useEffect(() => {
    if (!siteId) return;
    (async () => {
      const { count, error } = await supabase
        .from('site_visits')
        .select('*', { count: 'exact', head: true })
        .eq('site_id', siteId);
      if (!error) setAnalytics({ visits: count || 0 });
    })();
  }, [siteId]);

  const saveSettings = async () => {
    if (!slugAvailable) return alert('Slug not available.');
    const { error } = await supabase
      .from('sites')
      .update({
        business_name: bizName,
        location,
        slug,
        color_scheme: colorScheme,
        published: isPublished,
        custom_domain: customDomain,
        seo_title: seoTitle,
        seo_description: seoDescription,
        twitter_handle: twitterHandle,
        site_name: siteName,
      })
      .eq('id', siteId);
    if (error) alert('‚ùå Save failed.');
    else alert('‚úÖ Settings saved!');
  };

  const duplicateSite = async () => {
    const newSlug = prompt('New slug for duplicate site:');
    if (!newSlug) return;
    const { data: original, error } = await supabase
      .from('sites')
      .select('*')
      .eq('id', siteId)
      .maybeSingle();
    if (error || !original) return alert('‚ùå Fetch failed');
    const { error: createError } = await supabase.from('sites').insert({
      ...original,
      slug: newSlug,
      published: false,
      created_at: new Date().toISOString(),
    });
    if (createError) alert('‚ùå Duplicate failed');
    else {
      alert('‚úÖ Duplicated!');
      router.push(`/edit/${newSlug}`);
    }
  };

  const deleteSite = async () => {
    if (!confirm('Are you sure? This cannot be undone.')) return;
    setLoading(true);
    const { error } = await supabase.from('sites').delete().eq('id', siteId);
    if (error) alert('‚ùå Delete failed');
    else router.push('/dashboard');
  };

  const handleUpload = async (file: File, key: string) => {
    const { error } = await supabase.storage.from('site-assets').upload(`${siteId}/${key}`, file, {
      cacheControl: '3600',
      upsert: true,
    });
    if (error) alert('‚ùå Upload failed');
    else alert(`${key} uploaded!`);
  };

  console.log('‚úÖ SiteSettingsPanel mounted with siteId:', siteId);
  //   return (
  //     <div className="bg-red-600 text-white p-6 text-center rounded-lg">
  //       <p>üî• Settings panel loaded for site: {siteId}</p>
  //     </div>
  //   );

  return (
    <div className="flex flex-col h-full space-y-6 bg-zinc-900 overflow-y-auto rounded border border-zinc-700 p-6">
      <h2 className="text-xl font-semibold text-white">Site Settings</h2>

      <fieldset className="space-y-4">
        <legend className="text-sm font-medium text-zinc-400 mb-2">Basic Info</legend>

        <div>
          <label className="block text-sm font-medium text-zinc-300 mb-1">Business Name</label>
          <input
            value={bizName}
            onChange={(e) => setBizName(e.target.value)}
            className="w-full p-2 bg-zinc-900 border border-zinc-700 rounded"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-zinc-300 mb-1">Location</label>
          <input
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            className="w-full p-2 bg-zinc-900 border border-zinc-700 rounded"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-zinc-300 mb-1">Slug</label>
          <input
            value={slug}
            onChange={(e) => setSlug(e.target.value.toLowerCase().replace(/[^a-z0-9-]+/g, '-'))}
            className="w-full p-2 bg-zinc-900 border border-zinc-700 rounded"
          />
          {slug && slugAvailable !== null && (
            <p className={`text-sm mt-1 ${slugAvailable ? 'text-green-400' : 'text-red-400'}`}>
              {slugAvailable ? '‚úÖ Available' : 'üö´ Taken'}
            </p>
          )}
          {isPublished && (customDomain || slug) && (
            <div>
              <label className="block text-sm font-medium text-zinc-300 mb-1">Live URL</label>
              <div className="flex items-center gap-3">
                <Tooltip>
                  <TooltipTrigger asChild>
                    <a
                      href={`https://${customDomain || `${slug}.quicksites.ai`}`}
                      target="_blank"
                      rel="noopener noreferrer"
                      onClick={() => {
                        console.log('üîç Tracking: live site clicked');
                        // TODO: Supabase.track("click_live_link", { siteId })
                      }}
                      className="inline-flex items-center text-sm text-white bg-blue-600 hover:bg-blue-700 px-3 py-1.5 rounded transition"
                    >
                      üîó View Site
                    </a>
                  </TooltipTrigger>
                  <TooltipContent>
                    {customDomain
                      ? `Your domain should point to a CNAME record: ${slug}.quicksites.ai`
                      : `Visit your published site`}
                  </TooltipContent>
                </Tooltip>

                {/* Copy button */}
                <button
                  onClick={async () => {
                    const url = `https://${customDomain || `${slug}.quicksites.ai`}`;
                    await navigator.clipboard.writeText(url);
                    console.log('‚úÖ Copied to clipboard:', url);
                  }}
                  className="p-1 rounded bg-zinc-800 hover:bg-zinc-700 text-white"
                  title="Copy to clipboard"
                >
                  <ClipboardCopy className="w-4 h-4" />
                </button>

                {customDomain && <span className="text-xs text-zinc-400">(custom domain)</span>}
              </div>
            </div>
          )}
        </div>

        <div>
          <label className="block text-sm font-medium text-zinc-300 mb-1">Custom Domain</label>
          <input
            value={customDomain}
            onChange={(e) => setCustomDomain(e.target.value)}
            className="w-full p-2 bg-zinc-900 border border-zinc-700 rounded"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-zinc-300 mb-1">Color Scheme</label>
          <select
            value={colorScheme}
            onChange={(e) => setColorScheme(e.target.value)}
            className="w-full p-2 bg-zinc-900 border border-zinc-700 rounded"
          >
            <option value="">Choose Color Scheme</option>
            <option value="blue">Blue</option>
            <option value="red">Red</option>
            <option value="green">Green</option>
            <option value="dark">Dark</option>
          </select>
        </div>

        <div className="flex items-center gap-2">
          <input
            type="checkbox"
            checked={isPublished}
            onChange={() => setIsPublished(!isPublished)}
          />
          <label className="text-sm font-medium text-zinc-300">Published</label>
        </div>
      </fieldset>
      <fieldset className="space-y-4">
        <legend className="text-sm font-medium text-zinc-400 mb-2">üîç SEO Settings</legend>

        <div>
          <label className="block text-sm font-medium text-zinc-300 mb-1">SEO Title</label>
          <input
            value={seoTitle}
            onChange={(e) => setSeoTitle(e.target.value)}
            className="w-full p-2 bg-zinc-900 border border-zinc-700 rounded"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-zinc-300 mb-1">SEO Description</label>
          <textarea
            value={seoDescription}
            onChange={(e) => setSeoDescription(e.target.value)}
            className="w-full p-2 bg-zinc-900 border border-zinc-700 rounded"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-zinc-300 mb-1">Twitter Handle</label>
          <input
            value={twitterHandle}
            onChange={(e) => setTwitterHandle(e.target.value)}
            placeholder="@yourhandle"
            className="w-full p-2 bg-zinc-900 border border-zinc-700 rounded"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-zinc-300 mb-1">
            Site Name (OpenGraph)
          </label>
          <input
            value={siteName}
            onChange={(e) => setSiteName(e.target.value)}
            className="w-full p-2 bg-zinc-900 border border-zinc-700 rounded"
          />
        </div>
      </fieldset>

      <fieldset className="space-y-4">
        <legend className="text-sm font-medium text-zinc-400 mb-2">Assets</legend>

        <div>
          <label className="block text-sm font-medium text-zinc-300 mb-1">Favicon Upload</label>
          <input
            type="file"
            onChange={(e) => e.target.files?.[0] && handleUpload(e.target.files[0], 'favicon.png')}
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-zinc-300 mb-1">Logo Upload</label>
          <input
            type="file"
            onChange={(e) => e.target.files?.[0] && handleUpload(e.target.files[0], 'logo.png')}
          />
        </div>
      </fieldset>

      {analytics && (
        <div className="text-sm text-zinc-400">üìä {analytics.visits} visits recorded</div>
      )}

      <div className="flex justify-between pt-4 border-t border-zinc-700 mt-4">
        <button
          onClick={duplicateSite}
          disabled={loading}
          className="bg-yellow-600 px-3 py-2 rounded text-white"
        >
          üì¶ Duplicate
        </button>
        <button
          onClick={deleteSite}
          disabled={loading}
          className="bg-red-600 px-3 py-2 rounded text-white"
        >
          üóë Delete
        </button>
        <button
          onClick={saveSettings}
          disabled={loading}
          className="bg-blue-600 px-3 py-2 rounded text-white"
        >
          üíæ Save
        </button>
      </div>
    </div>
  );
}

// components/TagFilterControls.tsx
import { useEffect, useState } from 'react';

export default function TagFilterControls({
  tagFilter,
  setTagFilter,
  tagMatchMode,
  setTagMatchMode,
  allTags,
}: {
  tagFilter: string[];
  setTagFilter: (tags: string[]) => void;
  tagMatchMode: 'any' | 'all';
  setTagMatchMode: (mode: 'any' | 'all') => void;
  allTags: [string, number][];
}) {
  const [manualTag, setManualTag] = useState('');

  const addManualTag = () => {
    const newTag = manualTag.trim().toLowerCase();
    if (newTag && !tagFilter.includes(newTag)) {
      const next = [...tagFilter, newTag];
      setTagFilter(next);
      updateTagURL(next);
    }
    setManualTag('');
  };

  const updateTagURL = (tags: string[]) => {
    const url = new URL(window.location.href);
    if (tags.length > 0) url.searchParams.set('tags', tags.join(','));
    else url.searchParams.delete('tags');
    window.history.replaceState({}, '', url.toString());
  };

  const toggleTag = (tag: string) => {
    const lower = tag.toLowerCase();
    if (!tagFilter.includes(lower)) {
      const next = [...tagFilter, lower];
      setTagFilter(next);
      updateTagURL(next);
    }
  };

  const removeTag = (tag: string) => {
    const next = tagFilter.filter((t) => t !== tag);
    setTagFilter(next);
    updateTagURL(next);
  };

  const toggleMode = () => {
    const next = tagMatchMode === 'all' ? 'any' : 'all';
    setTagMatchMode(next);
    const url = new URL(window.location.href);
    url.searchParams.set('tagMode', next);
    window.history.replaceState({}, '', url.toString());
  };

  return (
    <div className="space-y-2">
      <label className="text-sm font-medium">Filter by Tag:</label>
      <input
        type="text"
        value={tagFilter.join(', ')}
        onChange={(e) => {
          const next = e.target.value
            .split(',')
            .map((t) => t.trim().toLowerCase())
            .filter(Boolean);
          setTagFilter(next);
          updateTagURL(next);
        }}
        placeholder="e.g. cold, social"
        className="border px-2 py-1 rounded w-full"
      />

      {tagFilter.length > 0 && (
        <div className="flex flex-wrap gap-1 mt-1">
          {tagFilter.map((tag, i) => (
            <button
              key={i}
              onClick={() => removeTag(tag)}
              className="bg-blue-100 text-blue-800 text-xs px-2 py-0.5 rounded hover:bg-blue-200"
            >
              #{tag} ‚úï
            </button>
          ))}
        </div>
      )}

      <div className="flex gap-2 items-center mt-2">
        <input
          value={manualTag}
          onChange={(e) => setManualTag(e.target.value)}
          placeholder="Add tag..."
          className="border px-2 py-1 text-sm rounded"
        />
        <button onClick={addManualTag} className="text-sm bg-blue-700 text-white px-3 py-1 rounded">
          + Add
        </button>
        <label
          title="Click to toggle mode (or press 'm')"
          onClick={toggleMode}
          className="text-xs font-semibold text-gray-700 cursor-pointer"
        >
          Mode:{' '}
          <span
            className={`ml-1 font-bold ${tagMatchMode === 'any' ? 'text-indigo-500' : 'text-green-600'}`}
          >
            {tagMatchMode}
          </span>
        </label>
        <select
          value={tagMatchMode}
          onChange={(e) => {
            const mode = e.target.value as 'any' | 'all';
            setTagMatchMode(mode);
            const url = new URL(window.location.href);
            url.searchParams.set('tagMode', mode);
            window.history.replaceState({}, '', url.toString());
          }}
          className={`text-xs border px-1 py-0.5 rounded text-white ${tagMatchMode === 'any' ? 'bg-indigo-500' : 'bg-green-600'}`}
        >
          <option value="all">Match All</option>
          <option value="any">Match Any</option>
        </select>
      </div>

      {allTags.length > 0 && (
        <div className="space-y-1 mt-4">
          <span className="text-xs text-gray-500">Suggestions (by frequency):</span>
          <div className="flex flex-wrap gap-2">
            {allTags.map(([tag, count], i) => (
              <button
                key={i}
                onClick={() => toggleTag(tag)}
                className="bg-gray-100 text-gray-700 text-xs px-2 py-0.5 rounded hover:bg-gray-200"
              >
                #{tag} ({count})
              </button>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

'use client';

import { useEffect, useState } from 'react';
import { supabase } from '@/admin/lib/supabaseClient';
import { Button } from '@/components/ui';

type Template = { id: string; name: string };

export default function TemplateAssigner() {
  const [templates, setTemplates] = useState<Template[]>([]);
  const [email, setEmail] = useState('');
  const [selected, setSelected] = useState('');
  const [userId, setUserId] = useState<string | null>(null);

  useEffect(() => {
    (async () => {
      const { data, error } = await supabase
        .from('dashboard_layout_templates')
        .select('id, name')
        .order('created_at', { ascending: false });

      if (error) console.error('Error fetching templates:', error);
      setTemplates(data || []);
    })();
  }, []);

  const fetchUserId = async () => {
    const { data: user, error } = await supabase
      .from('users')
      .select('id')
      .eq('email', email)
      .single();

    if (error || !user) {
      alert('User not found');
      return null;
    }

    return user.id as string;
  };

  const assignTemplate = async () => {
    if (!selected || !email) {
      alert('Please provide both an email and a template');
      return;
    }

    const id = await fetchUserId();
    if (!id) return;

    await supabase.from('dashboard_user_layouts').upsert({ user_id: id, template_id: selected });

    alert('‚úÖ Template assigned!');
  };

  return (
    <div className="p-4 border rounded bg-white shadow max-w-xl mt-8">
      <h2 className="text-lg font-semibold mb-3">Assign Template to User</h2>
      <div className="flex flex-col gap-3">
        <input
          placeholder="User email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="border p-2 text-sm"
        />
        <select
          className="border p-2 text-sm"
          value={selected}
          onChange={(e) => setSelected(e.target.value)}
        >
          <option value="">Select template</option>
          {templates.map((tpl) => (
            <option key={tpl.id} value={tpl.id}>
              {tpl.name}
            </option>
          ))}
        </select>
        <Button onClick={assignTemplate}>Assign</Button>
      </div>
    </div>
  );
}

