'use client';

import { useEffect, useState } from 'react';
import { supabase } from '@/admin/lib/supabaseClient';
import { Button } from '@/components/ui';
// import { Template } from '@/types/template';

export default function TemplateUserViewer() {
  type Assignment = {
    user_id: string;
    email: string;
    template_id: string;
    template: string;
  };
  type TemplateOption = { id: string; name: string };
  const [templates, setTemplates] = useState<TemplateOption[]>([]);
  const [selectedTemplate, setSelectedTemplate] = useState<string>('');
  const [assignments, setAssignments] = useState<Assignment[]>([]);
  const [filtered, setFiltered] = useState<Assignment[]>([]);
  const [search, setSearch] = useState('');
  const [filterTemplate, setFilterTemplate] = useState('');
  const [selected, setSelected] = useState('');

  useEffect(() => {
    loadData();
  }, []);

  useEffect(() => {
    setFiltered(
      assignments.filter(
        (a) =>
          (!search || a.email.toLowerCase().includes(search.toLowerCase())) &&
          (!filterTemplate || a.template === filterTemplate)
      )
    );
  }, [search, filterTemplate, assignments]);

  const loadData = async () => {
    const { data: map } = await supabase
      .from('dashboard_user_layouts')
      .select('user_id, template_id');

    const { data: users } = await supabase.from('auth.users').select('id, email');

    const { data: templates } = await supabase
      .from('dashboard_layout_templates')
      .select('id, name');

    const joined =
      map?.map((row) => ({
        user_id: row.user_id,
        email: users?.find((u) => u.id === row.user_id)?.email || '‚Äî',
        template_id: row.template_id,
        template: templates?.find((t) => t.id === row.template_id)?.name || '(deleted)',
      })) || [];

    setAssignments(joined);
    setTemplates(templates || []);
    setFiltered(joined);
  };

  const bulkAssign = async () => {
    const { data: users } = await supabase.from('auth.users').select('id');
    const inserts = users?.map((u) => ({
      user_id: u.id,
      template_id: selected,
    }));
    await supabase.from('dashboard_user_layouts').upsert(inserts || []);
    loadData();
    alert('All users assigned!');
  };

  const revertUser = async (user_id: string) => {
    await supabase.from('dashboard_user_layouts').delete().eq('user_id', user_id);
    loadData();
  };

  return (
    <div className="p-4 border rounded bg-white shadow max-w-3xl mt-8">
      <h2 className="text-lg font-semibold mb-3">User ‚Üí Template Assignments</h2>

      <div className="flex flex-wrap gap-3 items-center mb-4">
        <input
          type="text"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          placeholder="Search user email..."
          className="border p-2 text-sm"
        />
        <select
          value={filterTemplate}
          onChange={(e) => setFilterTemplate(e.target.value)}
          className="border p-2 text-sm"
        >
          <option value="">All templates</option>
          {templates.map((tpl) => (
            <option key={tpl.id} value={tpl.name}>
              {tpl.name}
            </option>
          ))}
        </select>
        <select
          value={selected}
          onChange={(e) => setSelected(e.target.value)}
          className="border p-2 text-sm"
        >
          <option value="">Select template to assign all</option>
          {templates.map((tpl) => (
            <option key={tpl.id} value={tpl.id}>
              {tpl.name}
            </option>
          ))}
        </select>
        <Button onClick={bulkAssign}>Assign to All</Button>
      </div>

      <table className="w-full text-sm border">
        <thead className="bg-gray-100">
          <tr>
            <th className="text-left p-2">User Email</th>
            <th className="text-left p-2">Template</th>
            <th className="text-left p-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {filtered.map((a) => (
            <tr key={a.user_id} className="border-b">
              <td className="p-2">{a.email}</td>
              <td className="p-2">{a.template}</td>
              <td className="p-2">
                <Button variant="ghost" size="sm" onClick={() => revertUser(a.user_id)}>
                  Revert
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

// BlockEditor.tsx
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import { Button } from '@/components/ui/button';
import { Plus, Trash2 } from 'lucide-react';

type Block = {
  id: string;
  type: string;
  [key: string]: any;
};

type Props = {
  block: Block;
  onChange: (block: Block) => void;
};

export default function BlockEditor({ block, onChange }: Props) {
  const updateField = (key: string, value: any) => {
    onChange({ ...block, [key]: value });
  };

  const updateListItem = (index: number, value: string) => {
    const items = [...(block.items || [])];
    items[index] = value;
    updateField('items', items);
  };

  const addListItem = () => {
    const items = [...(block.items || []), ''];
    updateField('items', items);
  };

  const deleteListItem = (index: number) => {
    const items = [...(block.items || [])];
    items.splice(index, 1);
    updateField('items', items);
  };

  switch (block.type) {
    case 'text':
      return (
        <div>
          <Label>Text</Label>
          <Textarea
            value={block.value || ''}
            onChange={(e) => updateField('value', e.target.value)}
          />
        </div>
      );

    case 'image':
      return (
        <div className="space-y-2">
          <Label>Image URL</Label>
          <Input value={block.url || ''} onChange={(e) => updateField('url', e.target.value)} />
          <Label>Alt Text</Label>
          <Input value={block.alt || ''} onChange={(e) => updateField('alt', e.target.value)} />
          {block.url && <img src={block.url} alt={block.alt} className="w-full rounded" />}
        </div>
      );

    case 'cta':
      return (
        <div className="space-y-2">
          <Label>CTA Label</Label>
          <Input value={block.label || ''} onChange={(e) => updateField('label', e.target.value)} />
          <Label>Link</Label>
          <Input value={block.link || ''} onChange={(e) => updateField('link', e.target.value)} />
        </div>
      );

    case 'quote':
      return (
        <div className="space-y-2">
          <Label>Quote</Label>
          <Textarea
            value={block.text || ''}
            onChange={(e) => updateField('text', e.target.value)}
          />
          <Label>Attribution</Label>
          <Input
            value={block.attribution || ''}
            onChange={(e) => updateField('attribution', e.target.value)}
          />
        </div>
      );

    case 'video':
      return (
        <div className="space-y-2">
          <Label>Embed URL (YouTube, Vimeo...)</Label>
          <Input
            value={block.embed_url || ''}
            onChange={(e) => updateField('embed_url', e.target.value)}
          />
          <Label>Caption</Label>
          <Input
            value={block.caption || ''}
            onChange={(e) => updateField('caption', e.target.value)}
          />
          {block.embed_url && (
            <iframe src={block.embed_url} className="w-full aspect-video rounded" allowFullScreen />
          )}
        </div>
      );

    case 'divider':
      return <div className="my-4 border-t border-muted" />;

    case 'list':
      return (
        <div className="space-y-2">
          <Label>List Items</Label>
          {(block.items || []).map((item: string, i: number) => (
            <div key={i} className="flex gap-2 items-center">
              <Input value={item} onChange={(e) => updateListItem(i, e.target.value)} />
              <Button variant="ghost" size="sm" onClick={() => deleteListItem(i)}>
                <Trash2 className="w-4 h-4" />
              </Button>
            </div>
          ))}
          <Button onClick={addListItem} variant="outline" size="sm">
            <Plus className="w-4 h-4 mr-2" /> Add Item
          </Button>
        </div>
      );

    default:
      return (
        <div className="text-sm text-muted-foreground">Unsupported block type: {block.type}</div>
      );
  }
}

