type Props = {
  preset: GridPreset;
  onSelect?: (preset: GridPreset) => void;
};

export default function GridThumbnailRenderer({ preset, onSelect }: Props) {
  return (
    <div
      className={`grid gap-2 mb-1 grid-cols-${preset.columns}`}
