'use client';

import type { Block } from '@/types/blocks';
import SortableGridBlock from './sortable-grid-block';
import { normalizeBlock } from '@/types/blocks';
import { createFallbackBlock } from '@/lib/blocks';

export default function RenderBlock({
  block,
  handleNestedBlockUpdate,
}: {
  block: Block;
  handleNestedBlockUpdate?: (updated: Block) => void;
}) {
  switch (block.type) {
    case 'text':
      return <p className="mb-4 text-base text-gray-800">{block.content?.value}</p>;

    case 'image':
      return (
        <img
          src={block.content?.url}
          alt={block.content?.alt}
          className="mb-4 rounded shadow-md max-w-full"
        />
      );

    case 'video':
      return (
        <div className="mb-4">
          <video controls className="max-w-full rounded">
            <source src={block.content?.url} />
          </video>
          {block.content?.caption && (
            <p className="text-sm text-gray-500 mt-1">{block.content.caption}</p>
          )}
        </div>
      );

    case 'audio':
      return (
        <div className="mb-4">
          <iframe
            className="w-full"
            style={{ height: 80 }}
            src={block.content?.url}
            title={block.content?.title || block.content?.provider}
            allow="autoplay; encrypted-media"
            loading="lazy"
          />
        </div>
      );

    case 'quote':
      return (
        <blockquote className="border-l-4 pl-4 italic text-gray-600 mb-4">
          ‚Äú{block.content?.text}‚Äù
          {block.content?.attribution && (
            <footer className="mt-1 text-sm text-right">‚Äî {block.content.attribution}</footer>
          )}
        </blockquote>
      );

    case 'button':
      return (
        <a
          href={block.content?.href}
          className={`inline-block px-4 py-2 rounded text-white text-sm mb-4
            ${
              block.content?.style === 'ghost'
                ? 'bg-transparent border'
                : block.content?.style === 'secondary'
                ? 'bg-gray-600'
                : 'bg-blue-600'
            }`}
        >
          {block.content?.label}
        </a>
      );

    case 'grid': {
      const normalizedItems = block.content?.items?.map(normalizeBlock) || [];
      const gridLabel = handleNestedBlockUpdate ? 'Grid (drag enabled)' : 'Grid (static)';
      const columns = block.content?.columns || 1;

      return (
        <div className="mb-4">
          <div className="flex justify-between items-center mb-2">
            <span className="text-xs text-muted-foreground italic">{gridLabel}</span>
            <span className="text-xs text-muted-foreground">
              {normalizedItems.length} item{normalizedItems.length !== 1 ? 's' : ''}
            </span>
          </div>

          {handleNestedBlockUpdate ? (
            <SortableGridBlock
              columns={columns}
              items={normalizedItems}
              onChange={(updated) => {
                const updatedBlock = {
                  ...block,
                  content: {
                    ...block.content,
                    items: updated,
                  },
                };
                handleNestedBlockUpdate(updatedBlock);
              }}
              onInsert={(index) => {
                const fallback = createFallbackBlock(block.type);
                const items = [...normalizedItems];
                items.splice(index, 0, fallback);
                const updatedBlock = {
                  ...block,
                  content: {
                    ...block.content,
                    items,
                  },
                };
                handleNestedBlockUpdate(updatedBlock);
              }}
            />
          ) : (
            <div className={`grid grid-cols-${columns} gap-4`}>
              {normalizedItems.map((b: Block, i: number) => (
                <RenderBlock key={i} block={b} />
              ))}
            </div>
          )}
        </div>
      );
    }

    case 'hero':
  return (
    <section className="mb-8 text-center">
      <h1 className="text-3xl font-bold text-white mb-2">{block.content?.title}</h1>
      {block.content?.description && (
        <p className="text-gray-400 mb-4">{block.content.description}</p>
      )}
      {block.content?.cta_label && block.content?.cta_link && (
        <a
          href={block.content.cta_link}
          className="inline-block px-5 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition"
        >
          {block.content.cta_label}
        </a>
      )}
    </section>
  );

  case 'services':
  return (
    <div className="mb-6">
      <h3 className="text-xl font-semibold text-white mb-2">Our Services</h3>
      <ul className="grid gap-2 list-disc list-inside text-gray-300">
        {block.content?.items?.map((item, i) => (
          <li key={i}>{item}</li>
        ))}
      </ul>
    </div>
  );

  case 'cta':
  return (
    <div className="mb-6 text-center">
      <a
        href={block.content?.link}
        className="inline-block px-6 py-2 bg-green-600 text-white rounded font-medium hover:bg-green-700 transition"
      >
        {block.content?.label}
      </a>
    </div>
  );

  case 'testimonial':
  return (
    <div className="mb-6 border-l-4 pl-4 border-blue-500 text-gray-200 italic">
      ‚Äú{block.content?.quote}‚Äù
      {block.content?.attribution && (
        <footer className="mt-2 text-sm text-blue-300">
          ‚Äî {block.content.attribution}
        </footer>
      )}
    </div>
  );

  default: {
    const _exhaustiveCheck: never = block;
    return (
      <div className="mb-4 text-yellow-300 italic">
        Block type <code>{(block as any).type}</code> not implemented.
      </div>
    );
  }



  }
}

import {
  DndContext,
  closestCenter,
  KeyboardSensor,
  PointerSensor,
  useSensor,
  useSensors,
} from '@dnd-kit/core';
import {
  arrayMove,
  SortableContext,
  useSortable,
  verticalListSortingStrategy,
} from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import { useState } from 'react';
import TemplatePreview from './template-preview';
import { ReorderableBlockListProps } from '@/types/blocks';

// type ReorderableBlockListProps = {
//   data: any;
//   colorScheme: string;
//   onBlockClick?: (block: any) => void;
//   onReorder: (updatedData: any) => void;
// };

export default function ReorderableBlockList({
  data,
  colorScheme,
  onBlockClick,
  onReorder,
}: {
  data: any;
  colorScheme: string;
  onBlockClick?: (block: any) => void;
  onReorder: (updatedData: any) => void;
}) {
  const sensors = useSensors(useSensor(PointerSensor), useSensor(KeyboardSensor));

  const [activePageIdx] = useState(0); // assuming only 1 page for now
  const blocks = data?.pages?.[activePageIdx]?.content_blocks || [];

  return (
    <DndContext
      sensors={sensors}
      collisionDetection={closestCenter}
      onDragEnd={({ active, over }) => {
        if (!over || active.id === over.id) return;
        const oldIndex = blocks.findIndex((b: any) => b._id === active.id);
        const newIndex = blocks.findIndex((b: any) => b._id === over.id);
        const reordered = arrayMove(blocks, oldIndex, newIndex);
        const updatedData = {
          ...data,
          pages: data.pages.map((p: any, i: number) =>
            i === activePageIdx ? { ...p, content_blocks: reordered } : p
          ),
        };
        onReorder(updatedData);
      }}
    >
      <SortableContext items={blocks.map((b: any) => b._id)} strategy={verticalListSortingStrategy}>
        <TemplatePreview
          data={data}
          colorScheme={colorScheme}
          // onBlockClick={onBlockClick}
        />
      </SortableContext>
    </DndContext>
  );
}

// components/admin/templates/sortable-block-board.tsx
'use client';

import {
  DndContext,
  closestCenter,
  useSensor,
  useSensors,
  PointerSensor,
} from '@dnd-kit/core';
import {
  SortableContext,
  arrayMove,
  verticalListSortingStrategy,
} from '@dnd-kit/sortable';

import { motion } from 'framer-motion';
import { useState } from 'react';
import type { Template, Page } from '@/types/template';
import { BlockSchema } from '@/admin/lib/zod/blockSchema';
import { GripVertical } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { useSortable } from '@dnd-kit/sortable';

type Props = {
  template: Template;
  onChange: (t: Template) => void;
  onLivePreviewUpdate: (d: Template['data']) => void;
  maxBlocksPerPage?: number;
};

export function SortableBlockBoard({ template, onChange, onLivePreviewUpdate, maxBlocksPerPage = 6 }: Props) {
  const sensors = useSensors(useSensor(PointerSensor));
  const [draggingBlockId, setDraggingBlockId] = useState<string | null>(null);

  const handleDragStart = (event: any) => {
    setDraggingBlockId(event.active.id);
  };

  const handleDragEnd = (event: any) => {
    const { active, over } = event;
    setDraggingBlockId(null);

    if (!over || active.id === over.id) return;

    const fromPage = template.data.pages.find((p) =>
      p.content_blocks.some((b) => b._id === active.id)
    );
    const toPage = template.data.pages.find((p) =>
      p.content_blocks.some((b) => b._id === over.id)
    );

    if (!fromPage || !toPage) return;

    const fromIndex = fromPage.content_blocks.findIndex((b) => b._id === active.id);
    const toIndex = toPage.content_blocks.findIndex((b) => b._id === over.id);

    // Prevent moving to a page over max limit
    if (
      fromPage.id !== toPage.id &&
      toPage.content_blocks.length >= maxBlocksPerPage
    ) {
      return alert('This page already has the max number of blocks');
    }

    const fromBlocks = [...fromPage.content_blocks];
    const [moved] = fromBlocks.splice(fromIndex, 1);

    const toBlocks = [...toPage.content_blocks];
    toBlocks.splice(toIndex, 0, moved);

    const updatedPages = template.data.pages.map((p) => {
      if (p.id === fromPage.id) return { ...p, content_blocks: fromBlocks };
      if (p.id === toPage.id) return { ...p, content_blocks: toBlocks };
      return p;
    });

    const updated = { ...template, data: { ...template.data, pages: updatedPages } };
    onChange(updated);
    onLivePreviewUpdate(updated.data);
  };

  return (
    <DndContext
      sensors={sensors}
      collisionDetection={closestCenter}
      onDragStart={handleDragStart}
      onDragEnd={handleDragEnd}
    >
      <div className="space-y-6">
        {template.data.pages.map((page, index) => (
          <div key={page.id} className="border rounded p-4 bg-muted/50 space-y-2 group">
            <div className="flex justify-between items-center">
              <div className="font-semibold text-sm">{page.title || `Page ${index + 1}`}</div>
              <span className="text-xs text-muted-foreground">
                {page.content_blocks.length} / {maxBlocksPerPage}
              </span>
            </div>

            <SortableContext
              items={page.content_blocks.map((b) => b._id).filter((id): id is string => id !== undefined)}
              strategy={verticalListSortingStrategy}
            >
              <div className="space-y-2 min-h-[40px]">
                {page.content_blocks.map((block) => (
                  <SortableBlock key={block._id} block={block} />
                ))}
                {page.content_blocks.length === 0 && (
                  <div className="text-xs text-muted-foreground italic">Drop a block here</div>
                )}
              </div>
            </SortableContext>
          </div>
        ))}
      </div>
    </DndContext>
  );
}

function SortableBlock({ block }: { block: any }) {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
    isDragging,
  } = useSortable({ id: block._id });

  const style = {
    transform: transform ? `translate(${transform.x}px, ${transform.y}px)` : undefined,
    transition,
  };

  const isValid = BlockSchema.safeParse(block).success;

  return (
    <motion.div
      ref={setNodeRef}
      layout
      style={style}
      className={`flex items-center justify-between p-3 rounded bg-white/5 border ${
        isDragging ? 'border-blue-500' : isValid ? 'border-gray-700' : 'border-red-500'
      } group relative`}
    >
      <div
        {...listeners}
        {...attributes}
        className="absolute -left-6 top-2 cursor-grab text-gray-500 group-hover:text-white opacity-0 group-hover:opacity-100"
      >
        <GripVertical size={16} />
      </div>
      <div className="text-sm">{block?.type || '(unknown)'}</div>
      {!isValid && (
        <Button size="icon" variant="outline" className="text-xs">
          Fix
        </Button>
      )}
    </motion.div>
  );
}

'use client';

import {
  DndContext,
  closestCenter,
  PointerSensor,
  useSensor,
  useSensors,
} from '@dnd-kit/core';
import {
  SortableContext,
  verticalListSortingStrategy,
  arrayMove,
} from '@dnd-kit/sortable';

import SortableBlock from './sortable-block';
import { BlockSchema } from '@/admin/lib/zod/blockSchema';
import { motion, AnimatePresence } from 'framer-motion';
import { Button } from '@/components/ui/button';
import { Pencil, Trash2, Plus } from 'lucide-react';
import { BlockWithId } from '@/types/blocks';

type Props = {
  blocks: BlockWithId[];
  onChange: (next: BlockWithId[]) => void;
  onBlockEdit?: (block: BlockWithId, index: number) => void;
  onInsertBlock?: (atIndex: number) => void;
};

export function SortableBlockList({
  blocks,
  onChange,
  onBlockEdit,
  onInsertBlock,
}: Props) {
  const sensors = useSensors(useSensor(PointerSensor));

  const handleDragEnd = (event: any) => {
    const { active, over } = event;
    if (!over || active.id === over.id) return;

    const oldIndex = blocks.findIndex((b) => b._id === active.id);
    const newIndex = blocks.findIndex((b) => b._id === over.id);
    if (oldIndex < 0 || newIndex < 0) return;

    const reordered = arrayMove(blocks, oldIndex, newIndex);
    onChange(reordered);
  };

  const handleDelete = (index: number) => {
    if (!confirm('Remove this block?')) return;
    const next = blocks.filter((_, i) => i !== index);
    onChange(next);
  };

  const handleInsert = (atIndex: number) => {
    if (onInsertBlock) onInsertBlock(atIndex);
  };

  return (
    <DndContext
      collisionDetection={closestCenter}
      sensors={sensors}
      onDragEnd={handleDragEnd}
    >      
      <SortableContext items={blocks.map((b) => b._id)} strategy={verticalListSortingStrategy}>
        <div className="space-y-2">
          <AnimatePresence>
            {blocks.map((block, i) => {
              const isValid = BlockSchema.safeParse(block).success;

              return (
                <SortableBlock id={block._id} key={block._id}>
                  <motion.div
                    layout
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    exit={{ opacity: 0 }}
                    className={`rounded border px-3 py-2 text-sm flex justify-between items-center ${
                      isValid
                        ? 'border-gray-700 bg-white/5'
                        : 'border-red-500 bg-red-500/10'
                    }`}
                  >
                    <div>{block?.type || '(unknown)'}</div>
                    <div className="flex gap-1">
                      {onBlockEdit && (
                        <Button
                          size="icon"
                          variant="ghost"
                          onClick={() => onBlockEdit(block, i)}
                        >
                          <Pencil className="w-3 h-3" />
                        </Button>
                      )}
                      <Button
                        size="icon"
                        variant="ghost"
                        onClick={() => handleDelete(i)}
                      >
                        <Trash2 className="w-3 h-3" />
                      </Button>
                    </div>
                  </motion.div>
                </SortableBlock>
              );
            })}

            {blocks.length === 0 && (
              <motion.div
                key="empty-state"
                layout
                initial={{ opacity: 0 }}
                animate={{ opacity: 0.5 }}
                exit={{ opacity: 0 }}
                className="text-xs text-muted-foreground italic px-2 py-4 border border-dashed border-gray-600 rounded text-center"
              >
                No blocks yet. Drag or insert to begin.
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      </SortableContext>
    </DndContext>
  );
}

