'use client';

import { useState } from 'react';
import type { Template, Page } from '@/types/template';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Plus } from 'lucide-react';
import { industryPresets } from '@/lib/presets';
import { PageCard } from './page-card';
import { SortablePage } from './sortable-page';

import {
  DndContext,
  closestCenter,
  PointerSensor,
  useSensor,
  useSensors,
} from '@dnd-kit/core';
import {
  SortableContext,
  verticalListSortingStrategy,
  arrayMove,
} from '@dnd-kit/sortable';

import {
  Select,
  SelectTrigger,
  SelectContent,
  SelectItem,
  SelectValue,
} from '@/components/ui/select';

type Props = {
  template: Template;
  onChange: (updated: Template) => void;
  onLivePreviewUpdate: (data: Template['data']) => void;
};

export default function TemplatePageEditor({ template, onChange, onLivePreviewUpdate }: Props) {
  const [newTitle, setNewTitle] = useState('');
  const [newSlug, setNewSlug] = useState('');
  const [newContentBlocks, setNewContentBlocks] = useState<any[]>([]);
  const [expandedPages, setExpandedPages] = useState<Record<string, boolean>>(() =>
    Object.fromEntries(template.data.pages.map((p) => [p.id, false]))
  );

  const sensors = useSensors(useSensor(PointerSensor));

  const handleDragEnd = (event: any) => {
    const { active, over } = event;
    if (!over || active.id === over.id) return;

    const oldIndex = template.data.pages.findIndex((p) => p.id === active.id);
    const newIndex = template.data.pages.findIndex((p) => p.id === over.id);

    if (oldIndex < 0 || newIndex < 0) return;

    const reordered = arrayMove(template.data.pages, oldIndex, newIndex);
    const updatedTemplate = {
      ...template,
      data: { ...template.data, pages: reordered },
    };

    onChange(updatedTemplate);
    onLivePreviewUpdate(updatedTemplate.data);
  };

  const getStarterPagePresets = (industry: string) => {
    const presetBlocks = industryPresets[industry.toLowerCase()] || industryPresets.default;
    return Object.entries(presetBlocks).map(([type, block]) => ({
      label: `${type.charAt(0).toUpperCase() + type.slice(1)} Page`,
      title: type.charAt(0).toUpperCase() + type.slice(1),
      slug: type.toLowerCase(),
      content_blocks: [block],
    }));
  };
  const pagePresets = getStarterPagePresets(template.industry || 'default');

  const handleAddPage = () => {
    if (!newTitle.trim() || !newSlug.trim()) return;
    const newPage: Page = {
      id: crypto.randomUUID(),
      title: newTitle.trim(),
      slug: newSlug.trim(),
      content_blocks: newContentBlocks,
    };
    const updatedTemplate = {
      ...template,
      data: {
        ...template.data,
        pages: [...template.data.pages, newPage],
      },
    };
    onChange(updatedTemplate);
    onLivePreviewUpdate(updatedTemplate.data);
    setNewTitle('');
    setNewSlug('');
    setNewContentBlocks([]);
  };

  const handleRemovePage = (id: string) => {
    const updatedTemplate = {
      ...template,
      data: {
        ...template.data,
        pages: template.data.pages.filter((p) => p.id !== id),
      },
    };
    onChange(updatedTemplate);
    onLivePreviewUpdate(updatedTemplate.data);
  };

  const handleUpdatePage = (id: string, field: keyof Page, value: string) => {
    const updatedPages = template.data.pages.map((p) =>
      p.id === id ? { ...p, [field]: value } : p
    );
    const updatedTemplate = {
      ...template,
      data: { ...template.data, pages: updatedPages },
    };
    onChange(updatedTemplate);
    onLivePreviewUpdate(updatedTemplate.data);
  };

  const movePage = (index: number, direction: 'up' | 'down') => {
    const pages = [...template.data.pages];
    const targetIndex = direction === 'up' ? index - 1 : index + 1;
    if (targetIndex < 0 || targetIndex >= pages.length) return;
    [pages[index], pages[targetIndex]] = [pages[targetIndex], pages[index]];
    const updatedTemplate = {
      ...template,
      data: { ...template.data, pages },
    };
    onChange(updatedTemplate);
    onLivePreviewUpdate(updatedTemplate.data);
  };

  const togglePage = (id: string) => {
    setExpandedPages((prev) => ({ ...prev, [id]: !prev[id] }));
  };

  const setAllPagesExpanded = (expanded: boolean) => {
    const next = Object.fromEntries(template.data.pages.map((p) => [p.id, expanded]));
    setExpandedPages(next);
  };

  return (
    <div className="space-y-4">
      <div className="flex justify-between items-center">
        <h3 className="text-lg font-semibold">Template Pages</h3>
        <div className="flex gap-2">
          <Button variant="ghost" size="sm" onClick={() => setAllPagesExpanded(true)}>
            Expand All
          </Button>
          <Button variant="ghost" size="sm" onClick={() => setAllPagesExpanded(false)}>
            Collapse All
          </Button>
        </div>
      </div>

      <DndContext sensors={sensors} collisionDetection={closestCenter} onDragEnd={handleDragEnd}>
        <SortableContext items={template.data.pages.map((p) => p.id)} strategy={verticalListSortingStrategy}>
          {template.data.pages.map((page, i) => (
            <SortablePage key={page.id} id={page.id}>
              <PageCard
                page={page}
                index={i}
                total={template.data.pages.length}
                template={template}
                expanded={expandedPages[page.id] ?? true}
                onToggle={() => togglePage(page.id)}
                onChange={onChange}
                onLivePreviewUpdate={onLivePreviewUpdate}
                onUpdatePageMeta={handleUpdatePage}
                onMovePage={movePage}
                onRemovePage={handleRemovePage}
              />
            </SortablePage>
          ))}
        </SortableContext>
      </DndContext>

      <div className="border-t pt-4 space-y-2">
        <h4 className="font-medium">Add New Page</h4>

        <Label>Start from Preset</Label>
        <Select
          onValueChange={(label) => {
            const preset = pagePresets.find((p) => p.label === label);
            if (preset) {
              setNewTitle(preset.title);
              setNewSlug(preset.slug);
              setNewContentBlocks(preset.content_blocks || []);
            }
          }}
        >
          <SelectTrigger>
            <SelectValue placeholder="Choose a preset page" />
          </SelectTrigger>
          <SelectContent className="radix-select-content">
            {pagePresets.map((preset) => (
              <SelectItem key={preset.label} value={preset.label}>
                {preset.label}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>

        <Label>Title</Label>
        <Input value={newTitle} onChange={(e) => setNewTitle(e.target.value)} />

        <Label>Slug</Label>
        <Input value={newSlug} onChange={(e) => setNewSlug(e.target.value)} />

        <Button onClick={handleAddPage} className="mt-2">
          <Plus className="w-4 h-4 mr-2" /> Add Page
        </Button>
      </div>
    </div>
  );
}

// components/admin/templates/template-save-bar.tsx
// Use TemplateSaveBar() when you need to save a template
// Use getUserFromRequest() when you need the user context

'use client';

import { Button } from '@/components/ui';
import { Dispatch, SetStateAction, useState } from 'react';
import { supabase } from '@/admin/lib/supabaseClient';
import toast from 'react-hot-toast';

type TemplateSaveBarProps = {
  template: any;
  rawJson: string;
  commitMessage: string;
  setSaveStatus: Dispatch<SetStateAction<'idle' | 'saved' | 'error'>>;
  setHighlightErrors: Dispatch<SetStateAction<boolean>>;
};

export default function TemplateSaveBar({
  template,
  rawJson,
  commitMessage,
  setSaveStatus,
  setHighlightErrors,
}: TemplateSaveBarProps) {
  const [saveStatus, setLocalSaveStatus] = useState<'idle' | 'saved' | 'error'>('idle');

  const handleSave = async () => {
    if (
      !template?.template_name ||
      !template?.industry ||
      !template?.layout ||
      !template?.data?.pages?.length
    ) {
      setHighlightErrors(true);
      return;
    }

    try {
      const {
        data: { user },
        error,
      } = await supabase.auth.getUser();

      if (error || !user) {
        throw new Error('No authenticated user');
      }

      const editor_id = user.email ?? 'unknown';
      const payload = { ...template, data: JSON.parse(rawJson) };

      const res = await fetch('/api/templates/update', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          ...payload,
          commit_message: commitMessage,
          editor_id,
        }),
      });

      if (res.ok) {
        setLocalSaveStatus('saved');
        setSaveStatus('saved');
        toast.success('Template saved successfully.');
        setTimeout(() => {
          setLocalSaveStatus('idle');
          setSaveStatus('idle');
        }, 3000);
      } else {
        throw new Error('Non-200 response');
      }
    } catch (err) {
      console.error('[‚ùå Template Save Error]', err);
      setLocalSaveStatus('error');
      setSaveStatus('error');
      toast.error('Failed to save template.');
    }
  };

  return (
    <div className="flex justify-between items-center fixed bottom-0 left-0 right-0 bg-background border-t p-4 z-50">
      <div className="flex items-center gap-2">
        <Button onClick={handleSave}>Save Changes</Button>
        {saveStatus === 'saved' && (
          <span className="text-green-500 text-xs animate-pulse">‚úì Saved</span>
        )}
        {saveStatus === 'error' && (
          <span className="text-red-500 text-xs">‚ö†Ô∏è Error</span>
        )}
      </div>
    </div>
  );
}

'use client';

import { useState, useMemo } from 'react';
import { useRouter } from 'next/navigation';
import { formatDistanceToNow } from 'date-fns';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu';
import { toast } from 'react-hot-toast';
import debounce from 'lodash.debounce';

type Template = {
  id: string;
  template_name: string;
  slug: string;
  updated_at: string;
  banner_url?: string | null;
  status?: 'draft' | 'published' | null;
};

const dateOptions = ['Last 7 days', 'This month', 'This year', 'All time'];

export default function TemplatesIndexTable({
  templates,
  selectedFilter = '',
}: {
  templates: Template[];
  selectedFilter?: string;
}) {
  const router = useRouter();
  const [currentFilter, setCurrentFilter] = useState(selectedFilter);
  const [search, setSearch] = useState('');
  const [editingId, setEditingId] = useState<string | null>(null);
  const [renames, setRenames] = useState<Record<string, string>>({});

  const filtered = useMemo(() => {
    return templates.filter((t) => {
      const term = search.toLowerCase();
      return (
        t.template_name.toLowerCase().includes(term) ||
        t.slug.toLowerCase().includes(term)
      );
    });
  }, [templates, search]);

  const handleFilterChange = (option: string) => {
    const url = new URL(window.location.href);
    if (option === 'All time') {
      url.searchParams.delete('date');
    } else {
      url.searchParams.set('date', option);
    }
    setCurrentFilter(option);
    router.push(url.toString());
  };

  const handleRename = useMemo(
    () =>
      debounce(async (id: string, newName: string) => {
        const res = await fetch('/api/templates/rename', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ template_id: id, newName }),
        });
        if (res.ok) {
          toast.success('Template renamed!');
        } else {
          toast.error('Failed to rename template');
        }
      }, 400),
    []
  );

  const handleDuplicate = async (slug: string) => {
    const res = await fetch(`/api/templates/duplicate?slug=${slug}`, {
      method: 'POST',
    });
    if (res.ok) {
      toast.success('Template duplicated!');
      router.refresh();
    } else {
      toast.error('Duplicate failed');
    }
  };

  const handleExport = async (id: string) => {
    const res = await fetch(`/api/templates/export?id=${id}`);
    if (!res.ok) return toast.error('Export failed');

    const blob = await res.blob();
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `template-${id}.json`;
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div className="space-y-6">
