// components/admin/templates/use-template-editor-state.ts
import { useState, useEffect } from 'react';
import { createEmptyTemplate } from '@/lib/createEmptyTemplate';
import { useAutosaveTemplate } from '@/hooks/useAutosaveTemplate';
import { useTemplateMeta } from '@/hooks/useTemplateMeta';
import { useTemplateJsonSync } from '@/hooks/useTemplateJsonSync';
import { normalizeTemplate } from '@/admin/utils/normalizeTemplate';
import { useTemplateSave } from '@/hooks/useTemplateSave';
import type { Snapshot, Template } from '@/types/template';
import toast from 'react-hot-toast';
import { validateBlock } from '@/lib/validateBlock';
import { Block } from '@/types/blocks';

export function useTemplateEditorState({
  templateName,
  initialData,
  onRename,
}: {
  templateName: string;
  initialData?: Snapshot | Template;
  onRename?: (newName: string) => void;
}) {
  const fallback = createEmptyTemplate(templateName);
  const [blockErrors, setBlockErrors] = useState<Record<string, string[]>>({});

  const applyDefaults = (tpl: Partial<Template>): Snapshot => {
    const autoFilled: string[] = [];
    const errors: Record<string, string[]> = {};

    const snapshot: Snapshot = {
      template_name: 'template_name' in tpl ? tpl.template_name || templateName : templateName,
      layout: tpl.layout || 'standard',
      color_scheme: tpl.color_scheme || 'neutral',
      theme: tpl.theme || 'default',
      brand: tpl.brand || 'default',
      commit: 'commit' in tpl ? tpl.commit || '' : '',
      id: tpl.id || crypto.randomUUID(),
      slug: tpl.slug || '',
      industry: tpl.industry || 'default',
      data: {
        pages: tpl.data?.pages?.map((page) => ({
          id: page.id || crypto.randomUUID(),
          slug: page.slug || '',
          title: page.title || '',
          content_blocks: (page.content_blocks?.map((block) => {
            const type = block.type || 'text';

            const defaultContent: Record<string, any> = {
              text: { value: '' },
              image: { url: '', alt: '' },
              video: { url: '', caption: '' },
              audio: { url: '', title: '', provider: 'spotify' },
              quote: { text: '', attribution: '' },
              cta: { label: '', link: '' },
              testimonial: { quote: '', attribution: '' },
              services: { items: [] },
              hero: { headline: '', subheadline: '', cta_text: '', cta_link: '' },
              button: { label: '', href: '', style: 'primary' },
              grid: { columns: 2, items: [] },
            };

            let content = block.content || defaultContent[type] || {};
            if (!block.content && defaultContent[type]) {
              autoFilled.push(type);
            }

            const fullBlock = {
              ...block,
              type,
              content,
              _id: block._id || crypto.randomUUID(),
            };

            const errorList = validateBlock(fullBlock as Block);
            if (errorList.length) {
              errors[fullBlock._id] = errorList;

              if (process.env.NODE_ENV === 'development') {
                console.warn('[TemplateEditor] Block schema validation failed for:', fullBlock.type, errorList);
              }
            }

            return fullBlock;
          }) ?? []) as Block[],
        })) ?? [],
      },
    };

    if (autoFilled.length && process.env.NODE_ENV === 'development') {
      console.log('[TemplateEditor] Auto-filled block content types:', Array.from(new Set(autoFilled)));
    }

    setBlockErrors(errors);
    return snapshot;
  };

  const [template, setTemplate] = useState(() =>
    applyDefaults(initialData ? normalizeTemplate(initialData as Template) : fallback)
  );

  const [isCreating, setIsCreating] = useState(!initialData);
  const [isRenaming, setIsRenaming] = useState(false);

  const { rawJson, setRawJson, livePreviewData } = useTemplateJsonSync(template.data);
  const { inputValue, setInputValue, slugPreview, nameExists } = useTemplateMeta(template.template_name, template.id);

  const autosave = useAutosaveTemplate(template, rawJson);

  useEffect(() => {
    if (!initialData && isCreating && template?.slug) {
      console.log('template.slug', template.slug);
      console.log('template.id', template.id);
      const timeout = setTimeout(() => {
        console.log('would have redirected to', window.location.href + '/admin/templates/' + template.id);
        alert('would have redirected to ' + window.location.href + '/admin/templates/' + template.id);
      }, 1200);
      return () => clearTimeout(timeout);
    }
  }, [isCreating, initialData, template]);

  const handleSaveDraft = () => {
    localStorage.setItem(`draft-${template.id}`, rawJson);
    toast.success('Draft saved');
  };

  const handleRename = async () => {
    const newName = inputValue.trim();
    if (newName.length < 3) return toast.error('Name must be at least 3 chars.');
    if (newName === template.template_name) return setIsRenaming(false);

    const res = await fetch('/api/templates/rename', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ template_id: template.id, newName }),
    });
    const json = await res.json();
    if (!res.ok) return toast.error(json?.error || 'Failed to rename');

    setTemplate((prev) => ({ ...prev, template_name: newName }));
    onRename?.(newName);
    setIsRenaming(false);
  };

  return {
    template,
    setTemplate,
    rawJson,
    setRawJson,
    livePreviewData,
    autosave,
    isCreating,
    isRenaming,
    setIsRenaming,
    handleRename,
    inputValue,
    setInputValue,
    slugPreview,
    handleSaveDraft,
    nameExists,
    blockErrors,
  };
}

import { useEffect, useState } from 'react';
import { Button } from '@/components/ui';
import { supabase } from '@/admin/lib/supabaseClient';
import VersionCompareDropdowns from '@/components/admin/templates/version-compare-dropdowns';
import { renderVersionDiff } from '@/admin/utils/renderVersionDiff';

export default function VersionHistoryPanel({
  slug,
  onRestore,
}: {
  slug: string;
  onRestore: (snapshotId: string, brandingProfileId: string) => void;
}) {
  const [versions, setVersions] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [currentVersion, setCurrentVersion] = useState<string | null>(null);
  const [confirmingDelete, setConfirmingDelete] = useState<string | null>(null);

  useEffect(() => {
    const fetchVersions = async () => {
      const [versionRes, currentRes] = await Promise.all([
        supabase
          .from('published_versions')
          .select(
            `id, created_at, snapshot_id, label, slug, branding_profile_id, branding_profiles ( id, name, primary_color, secondary_color, font_family )`
          )
          .eq('slug', slug)
          .order('created_at', { ascending: false }),
        supabase.from('published_sites').select('snapshot_id').eq('slug', slug).maybeSingle(),
      ]);

      if (!versionRes.error) setVersions(versionRes.data || []);
      if (!currentRes.error && currentRes.data) setCurrentVersion(currentRes.data.snapshot_id);
      setLoading(false);
    };

    fetchVersions();
  }, [slug]);

  if (loading) return <p className="text-sm text-gray-500">Loading versions...</p>;
  if (versions.length === 0)
    return <p className="text-sm text-gray-500">No version history available.</p>;

  return (
    <div className="mt-4 space-y-2">
      <h3 className="font-semibold text-base">Version History</h3>
      <VersionCompareDropdowns slug={slug} onRestore={onRestore} />
      <ul className="space-y-2">
        {versions.map((v) => {
          const isCurrent = v.snapshot_id === currentVersion;
          return (
            <li
              key={v.id}
              className={`border rounded p-2 flex justify-between items-center ${isCurrent ? 'border-green-500' : 'border-gray-700'}`}
            >
              <div>
                <div className="text-sm text-gray-100">
                  Snapshot: <code>{v.snapshot_id}</code>
                </div>
                <div className="text-xs text-gray-400">Label: {v.label || 'No label'}</div>
                <div className="text-xs text-gray-500">
                  Published: {new Date(v.created_at).toLocaleString()}
                </div>
                <div
                  className="text-xs mt-1"
                  style={{
                    fontFamily: v.branding_profiles?.font_family || 'sans-serif',
                  }}
                >
                  Heading Preview in {v.branding_profiles?.font_family || 'Default'}
                </div>
              </div>
              <div className="flex gap-2">
                {!isCurrent && (
                  <Button size="sm" onClick={() => onRestore(v.snapshot_id, v.branding_profile_id)}>
                    Restore
                  </Button>
                )}
                {confirmingDelete === v.id ? (
                  <Button
                    size="sm"
                    variant="destructive"
                    onClick={async () => {
                      const { error } = await supabase
                        .from('published_versions')
                        .delete()
                        .eq('id', v.id);
                      if (!error) setVersions(versions.filter((ver) => ver.id !== v.id));
                      setConfirmingDelete(null);
                    }}
                  >
                    Confirm Delete
                  </Button>
                ) : (
                  <Button size="sm" variant="outline" onClick={() => setConfirmingDelete(v.id)}>
                    Delete
                  </Button>
                )}
              </div>
            </li>
          );
        })}
      </ul>
    </div>
  );
}

'use client';
import { ReactNode, useEffect, useRef, useState } from 'react';

interface TooltipProps {
  placement?: 'top' | 'bottom' | 'left' | 'right';
  content: ReactNode;
  children: ReactNode;
  labelledById?: string;
  clickable?: boolean;
}

export default function Tooltip({
  content,
  children,
  clickable = false,
  placement = 'top',
  labelledById = `tooltip-${Math.random().toString(36).slice(2, 10)}`,
}: TooltipProps) {
  const ref = useRef<HTMLDivElement>(null);
  const [autoPlacement, setAutoPlacement] = useState<'top' | 'bottom' | 'left' | 'right'>(
    placement
  );
  const [prevPlacement, setPrevPlacement] = useState<'top' | 'bottom' | 'left' | 'right'>(
    placement
  );

  useEffect(() => {
    if (!ref.current) return;
    setPrevPlacement(autoPlacement);
    const rect = ref.current.getBoundingClientRect();
    const buffer = 20;
    if (placement === 'top' && rect.top < buffer) setAutoPlacement('bottom');
    else if (placement === 'bottom' && rect.bottom > window.innerHeight - buffer)
      setAutoPlacement('top');
    else if (placement === 'left' && rect.left < buffer) setAutoPlacement('right');
    else if (placement === 'right' && rect.right > window.innerWidth - buffer)
      setAutoPlacement('left');
  }, [placement]);

  const tooltipPosition =
    autoPlacement === 'top'
      ? 'bottom-full mb-1 -top-6'
      : autoPlacement === 'bottom'
        ? 'top-full mt-1 translate-y-1'
        : autoPlacement === 'left'
          ? 'right-full mr-1 -left-6 top-1/2 -translate-y-1/2'
          : 'left-full ml-1 -right-6 top-1/2 -translate-y-1/2';

  const arrowStyles =
    autoPlacement === 'top'
      ? 'before:-bottom-1 before:border-t-zinc-700'
      : autoPlacement === 'bottom'
        ? 'before:-top-1 before:border-b-zinc-700'
        : autoPlacement === 'left'
          ? 'before:-right-1 before:border-l-zinc-700'
          : 'before:-left-1 before:border-r-zinc-700';

  return (
    <div
      ref={ref}
      className="group relative inline-block touch-none"
      aria-describedby={labelledById}
      onTouchStart={() =>
        ref.current?.querySelector('[role=tooltip]')?.classList.add('opacity-100')
      }
    >
      <span
        role="tooltip"
        id={labelledById}
        aria-live="polite"
        aria-hidden="false"
        tabIndex={0}
        onKeyDown={(e) => {
          if (e.key === 'Escape') {
            (e.target as HTMLElement).blur();
          }
        }}
        className={`opacity-0 group-hover:opacity-100 group-hover:translate-y-0 absolute left-1/2 -translate-x-1/2 transition-[top,left] duration-300 ease-in-out bg-zinc-700 text-white text-xs rounded px-2 py-1 whitespace-nowrap z-10 transition-opacity transition-transform duration-300 ${clickable ? 'pointer-events-auto cursor-pointer' : 'pointer-events-none'} ${tooltipPosition} before:content-[''] before:absolute before:left-1/2 before:-translate-x-1/2 before:border-4 before:border-transparent ${arrowStyles}`}
      >
        {content}
      </span>
      {children}
    </div>
  );
}

// components/admin/traces/filter-bar.tsx
'use client';

import { useState, useEffect } from 'react';

type TraceEntry = {
  id: string;
  message: string;
  created_at: string;
};

type FilterBarProps = {
  entries: TraceEntry[];
  onFilter: (filtered: TraceEntry[]) => void;
};

export function FilterBar({ entries, onFilter }: FilterBarProps) {
  const [query, setQuery] = useState('');
  const [range, setRange] = useState('all');

  useEffect(() => {
    const now = Date.now();
    const filtered = entries.filter((entry) => {
      const matchesQuery = entry.message.toLowerCase().includes(query.toLowerCase());

      const createdAt = new Date(entry.created_at).getTime();
      const matchesTime =
        range === 'all' ||
        (range === '24h' && now - createdAt < 1000 * 60 * 60 * 24) ||
        (range === '7d' && now - createdAt < 1000 * 60 * 60 * 24 * 7);

      return matchesQuery && matchesTime;
    });

    onFilter(filtered);
  }, [query, range, entries]);

  return (
    <div className="mb-4 flex flex-wrap gap-4 items-center text-sm text-zinc-200">
      <input
        type="text"
        placeholder="Search messages..."
        className="bg-zinc-800 border border-zinc-600 rounded px-3 py-1 w-64"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
      />

      <select
        value={range}
        onChange={(e) => setRange(e.target.value)}
        className="bg-zinc-800 border border-zinc-600 rounded px-2 py-1"
      >
        <option value="all">All Time</option>
        <option value="24h">Last 24h</option>
        <option value="7d">Last 7d</option>
      </select>
    </div>
  );
}

// components/admin/traces/group-by-route.tsx
'use client';

import { useState } from 'react';

type TraceEntry = {
  id: string;
  route: string;
  message: string;
  context?: Record<string, any>;
  created_at: string;
};

export function GroupByRoute({
  entries,
}: {
  entries: TraceEntry[];
}) {
  const [grouped, setGrouped] = useState(true);

  const groupedData = entries.reduce((acc, entry) => {
    acc[entry.route] = acc[entry.route] || [];
    acc[entry.route].push(entry);
    return acc;
  }, {} as Record<string, TraceEntry[]>);

  return (
    <div className="space-y-4">
      <div className="mb-2 text-sm text-zinc-400">
        <label>
          <input
            type="checkbox"
            checked={grouped}
            onChange={() => setGrouped((v) => !v)}
            className="mr-2"
          />
          Group by route
        </label>
      </div>

      {grouped
        ? Object.entries(groupedData).map(([route, group]) => (
            <div key={route} className="border border-zinc-700 rounded">
              <div className="bg-zinc-800 px-4 py-2 font-mono text-xs text-indigo-300 border-b border-zinc-700">
                {route}
              </div>
              <div className="divide-y divide-zinc-800">
                {group.map((entry) => (
                  <div key={entry.id} className="p-4">
                    <div className="text-white font-medium mb-1">{entry.message}</div>
                    {entry.context && (
                      <pre className="bg-zinc-950 text-zinc-400 p-3 text-xs rounded overflow-x-auto">
                        {JSON.stringify(entry.context, null, 2)}
                      </pre>
                    )}
                    <div className="text-right text-zinc-500 text-xs mt-2">
                      {new Date(entry.created_at).toLocaleString()}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ))
        : entries.map((entry) => (
            <div key={entry.id} className="bg-zinc-900 border border-zinc-700 rounded-lg p-4">
              <div className="text-white font-medium mb-1">{entry.message}</div>
              <div className="text-zinc-400 text-xs mb-2">{entry.route}</div>
              {entry.context && (
                <pre className="bg-zinc-950 text-zinc-400 p-3 text-xs rounded overflow-x-auto">
                  {JSON.stringify(entry.context, null, 2)}
                </pre>
              )}
              <div className="text-right text-zinc-500 text-xs mt-2">
                {new Date(entry.created_at).toLocaleString()}
              </div>
            </div>
          ))}
    </div>
  );
}

// components/admin/traces/trace-filter-controls.tsx
'use client';

import { useState } from 'react';

type TraceFilterControlsProps = {
  initialRole?: string;
  initialVariant?: string;
  initialSession?: string;
  entries: any[];
  onChange: (filtered: any[]) => void;
};

export function TraceFilterControls({
  entries,
  onChange,
}: TraceFilterControlsProps) {
  const [role, setRole] = useState<string>('all');
  const [variant, setVariant] = useState<string>('all');
  const [session, setSession] = useState<string>('all');

  const allRoles = Array.from(new Set(entries.map((e) => e.role).filter(Boolean)));
  const allVariants = Array.from(new Set(entries.map((e) => e.ab_variant).filter(Boolean)));
  const allSessions = Array.from(new Set(entries.map((e) => e.session_id).filter(Boolean)));

  const applyFilter = () => {
    const filtered = entries.filter((entry) => {
      return (
        (role === 'all' || entry.role === role) &&
        (variant === 'all' || entry.ab_variant === variant) &&
        (session === 'all' || entry.session_id === session)
      );
    });
    onChange(filtered);
  };

  return (
    <div className="mb-6 flex gap-4 items-center text-sm text-zinc-200">
      <select
        value={role}
        onChange={(e) => { setRole(e.target.value); applyFilter(); }}
        className="bg-zinc-800 border border-zinc-600 rounded px-2 py-1"
      >
        <option value="all">All Roles</option>
        {allRoles.map((r) => <option key={r} value={r}>{r}</option>)}
      </select>

      <select
        value={variant}
        onChange={(e) => { setVariant(e.target.value); applyFilter(); }}
        className="bg-zinc-800 border border-zinc-600 rounded px-2 py-1"
      >
        <option value="all">All A/B Variants</option>
        {allVariants.map((v) => <option key={v} value={v}>{v}</option>)}
      </select>

      <select
        value={session}
        onChange={(e) => { setSession(e.target.value); applyFilter(); }}
        className="bg-zinc-800 border border-zinc-600 rounded px-2 py-1"
      >
        <option value="all">All Sessions</option>
        {allSessions.map((s) => <option key={s} value={s}>{s.slice(0, 8)}...</option>)}
      </select>
    </div>
  );
}

// components/admin/traces/trace-inspector.tsx
'use client';

import { useState } from 'react';
import { TraceFilterControls } from './trace-filter-controls';
import { GroupByRoute } from './group-by-route';
import { FilterBar } from './filter-bar';

export function TraceInspector({
  traceId,
  entries,
}: {
  traceId: string;
  entries: any[];
}) {
  const [filtered, setFiltered] = useState(entries);

  return (
    <div className="p-6 max-w-5xl mx-auto text-sm">
      <h1 className="text-lg font-semibold text-indigo-400 mb-2">
        Trace: {traceId.slice(0, 8)} ({entries.length} event{entries.length > 1 ? 's' : ''})
      </h1>

