export function MockGeolocationEditor({
  cookieValue,
  onChange,
  onDelete,
  locationQuery,
  setLocationQuery,
  loading,
  setLoading,
  activeGeo,
  collapsedByDefault = false,
}: {
  cookieValue: string;
  onChange: (key: string, value: string) => void;
  onDelete: () => void;
  locationQuery: string;
  setLocationQuery: (v: string) => void;
  loading: boolean;
  setLoading: (v: boolean) => void;
  activeGeo: { lat?: string; lon?: string };
  collapsedByDefault?: boolean;
}) {
  useEffect(() => {
    if (!locationQuery) return;
    const delay = setTimeout(async () => {
      setLoading(true);
      const result = await getLatLonFromQuery(locationQuery);
      if (result) {
        onChange('mock-geo', `${result.lat},${result.lon}`);
        setLocationQuery('');
      }
      setLoading(false);
    }, 1000);
    return () => clearTimeout(delay);
  }, [locationQuery]);

  return (
    <SectionToggle title="üìç Mock Geolocation" collapsedByDefault={collapsedByDefault}>
      <div className="space-y-2">
        <input
          className="w-full px-2 py-1 text-xs rounded bg-zinc-800 border border-zinc-600"
