'use client';

import { useEffect } from 'react';
import { useRouter } from 'next/navigation';
import { supabase } from '@/admin/lib/supabaseClient';

type Role = 'admin' | 'owner' | 'reseller' | 'viewer' | string;

type RoleMap = Partial<Record<Role, string>>;

type Options = {
  roleRoutes?: RoleMap;
  fallbackRoute?: string;
  enableTestBypass?: boolean;
};

/**
 * Redirects the user based on their Supabase `user_profiles.role`.
 * Customize the `roleRoutes` map to control where each role should go.
 */
export function useAutoRedirectByRole({
  roleRoutes = {
    admin: '/admin/dashboard',
    owner: '/admin/dashboard',
    reseller: '/admin/dashboard',
    viewer: '/viewer',
  },
  fallbackRoute = '/unauthorized',
  enableTestBypass = false,
}: Options = {}) {
  const router = useRouter();

  useEffect(() => {
    if (typeof window === 'undefined') return;

    const isTestMode =
      enableTestBypass && process.env.NEXT_PUBLIC_IS_PLAYWRIGHT_TEST === 'true';

    if (isTestMode) {
      router.replace(roleRoutes.admin || '/admin/dashboard');
      return;
    }

    const fetchAndRedirect = async () => {
      const {
        data: { user },
      } = await supabase.auth.getUser();

      if (!user?.id) {
        router.replace('/login');
        return;
      }

      const { data: profile, error } = await supabase
        .from('user_profiles')
        .select('role')
        .eq('user_id', user.id)
        .maybeSingle();

      const role = profile?.role ?? 'viewer';
      const redirectTo = roleRoutes[role] ?? fallbackRoute;

      if (process.env.NODE_ENV === 'development') {
        console.log(`[üîÅ AutoRedirect] Role "${role}" ‚Üí ${redirectTo}`);
      }

      router.replace(redirectTo);
    };

    fetchAndRedirect();
  }, [router, roleRoutes, fallbackRoute, enableTestBypass]);
}

 // hooks/useAutosaveTemplate.ts
import { useEffect, useRef, useState } from 'react';
import { toast } from 'react-hot-toast';

export function useAutosaveTemplate(template: any, rawJson: string) {
  const [status, setStatus] = useState<'idle' | 'saving' | 'saved'>('idle');
  const lastSave = useRef('');

  useEffect(() => {
    if (!template?.id || template.id.length < 8) return; // üõ°Ô∏è Don't autosave yet

    const timeout = setTimeout(() => {
      if (rawJson !== lastSave.current) {
        setStatus('saving');
        localStorage.setItem(`draft-${template.id}`, rawJson);
        lastSave.current = rawJson;
        setStatus('saved');
        // toast.success('Changes autosaved');
      }
    }, 2000);

    return () => clearTimeout(timeout);
  }, [rawJson, template?.id]); // üëà also track id in case it gets populated later

  const restore = () => {
    if (!template?.id || template.id.length < 8) return '';
    return localStorage.getItem(`draft-${template.id}`) || '';
  };

  const clear = () => {
    if (!template?.id || template.id.length < 8) return;
    localStorage.removeItem(`draft-${template.id}`);
  };

  return { status, restore, clear };
}

import { useEffect } from 'react';
import toast from 'react-hot-toast';

export function useBrandingAutoSuggest({
  template,
  brandingProfiles,
  selectedProfileId,
  setSelectedProfileId,
}: {
  template: any;
  brandingProfiles: any[];
  selectedProfileId: string | null;
  setSelectedProfileId: (id: string) => void;
}) {
  useEffect(() => {
    if (!template || !brandingProfiles?.length || selectedProfileId) return;

    const match = brandingProfiles.find(
      (p) =>
        (template?.industry && p.name.toLowerCase().includes(template?.industry.toLowerCase())) ||
        (template.layout && p.name.toLowerCase().includes(template.layout.toLowerCase()))
    );

    if (match) {
      setSelectedProfileId(match.id);
      toast.success(`Auto-selected branding profile: ${match.name}`);
    }
  }, [template?.industry, template?.layout, brandingProfiles, selectedProfileId]);
}

'use client';

import { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import dayjs from 'dayjs';
import { supabase } from '@/admin/lib/supabaseClient';
import { useLeads } from './useLeads';

function slugify(name: string): string {
  return name
    .toLowerCase()
    .replace(/[^a-z0-9]+/g, '-')
    .replace(/^-|-$/g, '');
}

export function useCampaignForm(city: string, state: string, initialLeadIds: string[] = []) {
  const router = useRouter();

  const [name, setName] = useState('');
  const [nameWasManuallySet, setNameWasManuallySet] = useState(false);
  const [alt1, setAlt1] = useState('');
  const [alt2, setAlt2] = useState('');
  const [startsAt, setStartsAt] = useState(dayjs().toISOString().slice(0, 16));
  const [endsAt, setEndsAt] = useState(dayjs().add(3, 'day').toISOString().slice(0, 16));
  const [silentMode, setSilentMode] = useState(false);
  const [email, setEmail] = useState('');
  const [error, setError] = useState<string | null>(null);
  const [validationErrors, setValidationErrors] = useState<Record<string, string>>({});
  const { leads, loading: leadsLoading } = useLeads({ city, state, industry: 'towing' });

  const [selectedLeads, setSelectedLeads] = useState<string[]>([]);

  function toggleLead(id: string) {
    setSelectedLeads((prev) =>
      prev.includes(id) ? prev.filter((l) => l !== id) : [...prev, id]
    );
  }

  useEffect(() => {
    if (!nameWasManuallySet && city && selectedLeads.length >= 2 && startsAt) {
      const a = leads.find((l) => l.id === selectedLeads[0])?.business_name?.split(' ')[0] || 'A';
      const b = leads.find((l) => l.id === selectedLeads[1])?.business_name?.split(' ')[0] || 'B';
      const date = dayjs(startsAt).format('YYYY-MM-DD');
      setName(`${city} Towing: ${a} vs ${b} ${date}`);
    }
  }, [city, selectedLeads, nameWasManuallySet, startsAt, leads]);

  useEffect(() => {
    if (selectedLeads.length >= 2) {
      const leadA = leads.find((l) => l.id === selectedLeads[0]);
      const leadB = leads.find((l) => l.id === selectedLeads[1]);

      if (leadA && !alt1) setAlt1(`${slugify(leadA.business_name || '')}.com`);
      if (leadB && !alt2) setAlt2(`${slugify(leadB.business_name || '')}.com`);
    }
  }, [selectedLeads, leads, alt1, alt2]);

  useEffect(() => {
    supabase.auth.getUser().then(({ data }) => {
      setEmail(data?.user?.email || '');
    });
  }, []);

  function validateFields(): boolean {
    const errors: Record<string, string> = {};

    if (!name.trim()) errors.name = 'Campaign name is required.';
    if (selectedLeads.length < 2) errors.leads = 'Select at least 2 leads.';
    if (dayjs(startsAt).isAfter(dayjs(endsAt)))
      errors.dates = 'Start date must be before end date.';

    setValidationErrors(errors);
    return Object.keys(errors).length === 0;
  }

  const submit = async (mode: 'draft' | 'submit' = 'submit') => {
    setError(null);
    if (!validateFields()) return;

    const status = mode === 'draft' ? 'draft' : 'active';

    const { data, error: insertError } = await supabase
      .from('campaigns')
      .insert([
        {
          name,
          city,
          state,
          starts_at: startsAt,
          ends_at: endsAt,
          lead_ids: selectedLeads,
          alt_domains: [alt1, alt2],
          created_by: email,
          status,
        },
      ])
      .select()
      .single();

    if (insertError) {
      setError(insertError.message);
      return;
    }

    if (mode === 'submit') {
      await supabase.from('leads').update({ campaign_id: data.id }).in('id', selectedLeads);

      if (!silentMode) {
        await supabase.from('user_action_logs').insert([
          {
            action_type: 'campaign_created',
            triggered_by: email,
            notes: `Created campaign: ${name}`,
          },
        ]);
      }

      router.push(`/admin/campaigns?new=${data.id}`);
    } else {
      router.push(`/admin/start-campaign?draftId=${data.id}&city=${city}&state=${state}&initialLeadIds=${initialLeadIds.join(',')}`);
    }
  };

  return {
    name,
    setName,
    nameWasManuallySet,
    setNameWasManuallySet,
    alt1,
    alt2,
    setAlt1,
    setAlt2,
    startsAt,
    setStartsAt,
    endsAt,
    setEndsAt,
    silentMode,
    setSilentMode,
    email,
    error,
    validationErrors,
    submit,
    leads,
    selectedLeads,
    toggleLead,
    leadsLoading,
    initialLeadIds,
  };
}

// /hooks/useCurrentUser.tsx (enhanced with mock support and role helpers)
import { useContext, useEffect, useState } from 'react';
import {
  CurrentUserContext,
  CurrentUserContextType,
} from '@/components/admin/context/current-user-provider';
import { supabase } from '@/admin/lib/supabaseClient';

export function useCurrentUser(): CurrentUserContextType & {
  role: string;
  roleSource: string;
  isLoading: boolean;
  ready: boolean;
  isAdmin: boolean;
  isOwner: boolean;
  isReseller: boolean;
  isViewer: boolean;
  isMocked: boolean;
} {
  const context = useContext(CurrentUserContext);

  const [fetchedRole, setFetchedRole] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [roleSource, setRoleSource] = useState<'simulated' | 'session' | 'db' | 'cache'>('session');
  const [mockUser, setMockUser] = useState<{ id: string; email: string } | null>(null);

  useEffect(() => {
    const fetchRole = async () => {
      const devMockRole = typeof window !== 'undefined' ? (window as any).__DEV_MOCK_ROLE__ : null;
      const devMockUser = typeof window !== 'undefined' ? (window as any).__DEV_MOCK_USER__ : null;
      if (devMockRole && devMockUser) {
        setFetchedRole(devMockRole);
        setRoleSource('simulated');
        setMockUser(devMockUser);
        setIsLoading(false);
        return;
      }

      const email = context.user?.email;
      if (!email) {
        setIsLoading(false);
        return;
      }

      const cacheKey = `cached-role-${email}`;

      const cached = localStorage.getItem(cacheKey);
      if (cached) {
        console.debug('üì¶ Using cached fallback role:', cached);
        setFetchedRole(cached);
        setRoleSource('cache');
        setIsLoading(false);
        return;
      }

      const { data, error } = await supabase
        .from('user_roles')
        .select('new_role')
        .eq('user_email', email)
        .order('updated_at', { ascending: false })
        .limit(1)
        .single();

      if (error) {
        console.warn('üîç Role fetch error', error.message);
      } else if (data?.new_role?.trim()) {
        console.debug('‚úÖ Fallback role from DB:', data.new_role);
        setFetchedRole(data.new_role);
        setRoleSource('db');
        localStorage.setItem(cacheKey, data.new_role);
      }

      setIsLoading(false);
    };

    fetchRole();
  }, [context.user?.email]);

  const role: string =
    (fetchedRole as 'viewer' | 'admin' | 'editor' | 'owner' | 'reseller') ||
    context.user?.role ||
    'viewer';

  const isMocked = roleSource === 'simulated';

  return {
    ...context,
    user: isMocked && mockUser ? { ...mockUser, role: role as 'viewer' | 'admin' | 'editor' | 'owner' } : context.user,
    role: role as 'viewer' | 'admin' | 'editor' | 'owner',
    roleSource,
    isLoading,
    ready: !isLoading,
    isMocked,
    isAdmin: role === 'admin',
    isOwner: role === 'owner',
    isReseller: role === 'reseller',
    isViewer: role === 'viewer',
  };
}

// hooks/useDashboardLayout.ts

import { useEffect, useState } from 'react';
import { supabase } from '../admin/lib/supabaseClient';

type Block = { id: string; title: string };
type Settings = Record<string, Record<string, any>>;

export function useDashboardLayout(userId: string | null, dashboardId?: string) {
  const [order, setOrder] = useState<Block[]>([]);
  const [hidden, setHidden] = useState<string[]>([]);
  const [settings, setSettings] = useState<Settings>({});
  const [dashboards, setDashboards] = useState<any[]>([]);
  const [activeDashboardId, setActiveDashboardId] = useState<string | null>(dashboardId || null);
  const [loaded, setLoaded] = useState(false);
  const [loading, setLoading] = useState(false);

  // Fetch dashboards for the user
  useEffect(() => {
    if (!userId) return;
    setLoading(true);
    supabase
      .from('dashboard_user_layouts')
      .select('*')
      .eq('user_id', userId)
      .then(({ data }) => {
        setDashboards(data || []);
        if (!activeDashboardId && data?.[0]) {
          setActiveDashboardId(data[0].dashboard_id);
        }
        setLoading(false);
      });
  }, [userId]);

  // Load selected layout
  useEffect(() => {
    if (!userId || !activeDashboardId) return;
    setLoading(true);
    supabase
      .from('dashboard_user_layouts')
      .select('layout, hidden, settings')
      .eq('user_id', userId)
      .eq('dashboard_id', activeDashboardId)
      .single()
      .then(({ data }) => {
        if (data?.layout) setOrder(data.layout);
        if (data?.hidden) setHidden(data.hidden);
        if (data?.settings) setSettings(data.settings);
        setLoaded(true);
        setLoading(false);
      });

    const channel = supabase
      .channel('dashboard_sync')
      .on(
        'postgres_changes',
        {
          event: 'UPDATE',
          schema: 'public',
          table: 'dashboard_user_layouts',
          filter: `user_id=eq.${userId}`,
        },
        (payload) => {
          const updated = payload.new;
          if (updated.dashboard_id === activeDashboardId) {
            if (updated.layout) setOrder(updated.layout);
            if (updated.hidden) setHidden(updated.hidden);
            if (updated.settings) setSettings(updated.settings);
          }
        }
      )
      .subscribe();

    return () => {
      supabase.removeChannel(channel);
    };
  }, [userId, activeDashboardId]);

  const save = async (layout: Block[], hiddenList: string[], newSettings = settings) => {
    if (!userId || !activeDashboardId) return;
    setLoading(true);
    await supabase.from('dashboard_user_layouts').upsert({
      user_id: userId,
      dashboard_id: activeDashboardId,
      layout,
      hidden: hiddenList,
      settings: newSettings,
      updated_at: new Date().toISOString(),
    });
    setOrder(layout);
    setHidden(hiddenList);
    setSettings(newSettings);
    setLoading(false);
  };

  const updateBlockSetting = (blockId: string, key: string, value: any) => {
    const newSettings = {
      ...settings,
      [blockId]: {
        ...(settings[blockId] || {}),
        [key]: value,
      },
    };
    save(order, hidden, newSettings);
  };
  const createDashboard = async (name: string) => {
    if (!userId) return;
    setLoading(true);
    const { data } = await supabase
      .from('dashboard_user_layouts')
      .insert({
        user_id: userId,
        name,
        layout: 'DEFAULT_LAYOUT',
        hidden: [],
        settings: {},
      })
      .select()
      .single();

    if (data) {
      setDashboards((prev) => [...prev, data]);
      setActiveDashboardId(data.dashboard_id);
      setOrder(data.layout);
      setHidden([]);
      setSettings({});
      setLoading(false);
    }
  };

  return {
    order,
    hidden,
    settings,
    dashboards,
    activeDashboardId,
    setActiveDashboardId,
    loaded,
    save,
    updateBlockSetting,
    createDashboard,
    loading,
  };
}

'use client';
import { useRouter, useSearchParams } from 'next/navigation';
import { useMemo } from 'react';

export function useDateRange() {
  const searchParams = useSearchParams();

  const start = useMemo(() => {
    if (typeof searchParams?.get('start') === 'string') return searchParams.get('start');
    return '';
  }, [searchParams?.get('start')]);

  const end = useMemo(() => {
    if (typeof searchParams?.get('end') === 'string') return searchParams.get('end');
    return '';
  }, [searchParams?.get('end')]);

  return { start, end };
}

import { useEffect } from 'react';
import { useRouter, useSearchParams } from 'next/navigation';
import { toast, ToastOptions } from 'react-hot-toast';

type ToastType = 'success' | 'error' | 'loading' | 'custom';

type UseFlashToastOptions = {
  type?: ToastType;
  prefix?: string;
  duration?: number;
  icon?: string;
  className?: string;
};

export function useFlashToast(
  key: string,
  { type = 'success', prefix = '', duration = 3000, icon, className }: UseFlashToastOptions = {}
) {
  const searchParams = useSearchParams();
  const value = searchParams?.get(key);

  useEffect(() => {
    if (value && typeof value === 'string') {
      const message = `${prefix}${value}`;

      const toastOptions: ToastOptions = {
        duration,
        icon,
        className,
      };

      switch (type) {
        case 'error':
          toast.error(message, toastOptions);
          break;
        case 'loading':
          toast.loading(message, toastOptions);
          break;
        case 'custom':
          toast(message, toastOptions);
          break;
        default:
          toast.success(message, toastOptions);
      }

      // Remove the flash param from the URL
      const rest = new URLSearchParams(searchParams?.toString() || '');
      rest.delete(key);
      window.location.href = `${window.location.pathname}?${rest.toString()}`;
    }
  }, [value]);
}

// hooks/useGeoLocation.ts
import { useEffect, useState } from 'react';
import { getCachedGeo, setCachedGeo } from '../lib/geoCache.js';
import staticGeo from '../public/staticGeo.json';

// USAGE:
// const { lat, lon, loading, error } = useGeoLocation('Franklin', 'WI');
// if (loading) return <p>Loading map...</p>;
// if (error) return <p>Error: {error}</p>;

function getStaticFallback(city: string, state: string): { lat: number; lon: number } | undefined {
  const key = `${city.trim().toLowerCase()},${state.trim().toLowerCase()}`;
  return staticGeo[key as keyof typeof staticGeo];
}

export function useGeoLocation(city: string, state: string) {
  const [coords, setCoords] = useState<{ lat: number; lon: number } | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    if (!city || !state) return;

    const tryGeoSources = async () => {
      setLoading(true);
      const key = `${city}, ${state}`;

      // 1. Local memory cache
      const memoryHit = getCachedGeo(city, state);
      if (memoryHit) {
        setCoords(memoryHit);
        setLoading(false);
        return;
      }

      // 2. Static fallback JSON
      const staticHit = getStaticFallback(city, state);
      if (staticHit) {
        setCachedGeo(city, state, staticHit.lat, staticHit.lon);
        setCoords(staticHit);
        setLoading(false);
        return;
      }

      // 3. Remote API
      try {
        const res = await fetch(
          `/api/geocode?city=${encodeURIComponent(city)}&state=${encodeURIComponent(state)}`
        );
        if (!res.ok) throw new Error(`API failed: ${res.status}`);
        const data = await res.json();
        const lat = Number(data.lat || 0);
        const lon = Number(data.lon || 0);
        setCachedGeo(city, state, lat, lon);
        setCoords({ lat, lon });
      } catch (err: any) {
        setError(err.message || 'Unknown error');
        setCoords({ lat: 0, lon: 0 });
      } finally {
        setLoading(false);
      }
    };

    tryGeoSources();
  }, [city, state]);

  return { lat: coords?.lat, lon: coords?.lon, loading, error };
}

