// /hooks/useThemeContext.ts (now includes full theme + provider + glow sync)
import { createContext, useContext, useEffect, useState } from 'react';
import { createClient } from '@supabase/supabase-js';
import type { Database } from '@/types/supabase';

export type GlowConfig = {
  size: 'sm' | 'md' | 'lg' | 'xl';
  intensity: number;
  colors: string[];
};

export type SiteTheme = {
  glow: GlowConfig[];
  fontFamily?: string;
  borderRadius?: string;
  accentColor?: string;
  // add other themable options here
};

const defaultGlow: GlowConfig[] = [
  { size: 'xl', intensity: 0.2, colors: ['from-purple-600', 'via-blue-500', 'to-pink-500'] },
];

const defaultTheme: SiteTheme = {
  glow: defaultGlow,
  fontFamily: 'sans',
  borderRadius: 'lg',
  accentColor: 'indigo-600',
};

const ThemeContext = createContext<{
  theme: SiteTheme;
  setTheme: (t: SiteTheme) => void;
  siteSlug: string;
}>({
  theme: defaultTheme,
  setTheme: () => {},
  siteSlug: 'default',
});

export function ThemeProvider({ children, siteSlug = 'default' }: { children: React.ReactNode; siteSlug?: string }) {
  const supabase = createClient<Database>(process.env.NEXT_PUBLIC_SUPABASE_URL!, process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!);
  const [theme, setThemeState] = useState<SiteTheme>(defaultTheme);

  useEffect(() => {
    const loadTheme = async () => {
      if (typeof window === 'undefined') return;

      const key = `theme-config::${siteSlug}`;
      const cached = localStorage.getItem(key);
      if (cached) {
        try {
          const parsed = JSON.parse(cached);
          setThemeState(parsed);
          return;
        } catch {
          console.warn('‚ö†Ô∏è Failed to parse cached theme');
        }
      }

      const userId = (window as any).__DEV_MOCK_USER__?.id;
      if (userId) {
        const { data } = await supabase
          .from('user_site_settings')
          .select('glow_config, theme_font, theme_radius, theme_accent')
          .eq('user_id', userId)
          .eq('site_slug', siteSlug)
          .single();

        if (data) {
          setThemeState({
            glow: Array.isArray(data.glow_config) ? data.glow_config : [data.glow_config],
            fontFamily: data.theme_font || defaultTheme.fontFamily,
            borderRadius: data.theme_radius || defaultTheme.borderRadius,
            accentColor: data.theme_accent || defaultTheme.accentColor,
          });
          return;
        }
      }

      setThemeState(defaultTheme);
    };

    loadTheme();
  }, [siteSlug]);

  const setTheme = async (value: SiteTheme) => {
    setThemeState(value);

    const userId = (window as any).__DEV_MOCK_USER__?.id;
    if (typeof window !== 'undefined') {
      const key = `theme-config::${siteSlug}`;
      try {
        localStorage.setItem(key, JSON.stringify(value));

        if (userId) {
          await supabase.from('user_site_settings').upsert({
            user_id: userId,
            site_slug: siteSlug,
            glow_config: value.glow,
            theme_font: value.fontFamily,
            theme_radius: value.borderRadius,
            theme_accent: value.accentColor,
          });
        }
      } catch (err) {
        console.warn('‚ö†Ô∏è Failed to persist theme', err);
      }
    }
  };

  return (
    <ThemeContext.Provider value={{ theme, setTheme, siteSlug }}>
      {children}
    </ThemeContext.Provider>
  );
}

export function useTheme() {
  return useContext(ThemeContext);
}

import { useState } from 'react';
import { supabase } from '../admin/lib/supabaseClient';

interface UseImageUploaderOptions {
  siteId: string;
  templateId: string;
  dbField: string;
  folder?: string;
  label?: string;
  initialUrl?: string;
  onChange?: (url: string | null) => void;
}

export function useImageUploader({
  siteId,
  templateId,
  dbField,
  folder = 'uploads',
  label,
  initialUrl,
  onChange,
}: UseImageUploaderOptions) {
  const [preview, setPreview] = useState<string | null>(initialUrl ?? null);
  const [uploading, setUploading] = useState(false);

  function sanitizeFilename(filename: string): string {
    const parts = filename.split('.');
    const ext = parts.pop(); // file extension
    const base = parts.join('-').replace(/\s+/g, '-');
    const timestamp = Date.now(); // or use dayjs() for formatted date
    return `${base}-${timestamp}.${ext}`.toLowerCase();
  }

  const uploadImage = async (file: File) => {
    if (!file) return;

    const cleanName = sanitizeFilename(file.name);
    const filePath = `${folder}/${siteId}/${cleanName}`;
    setUploading(true);
    setPreview(URL.createObjectURL(file));

    const { error: uploadError } = await supabase.storage
      .from('site-images')
      .upload(filePath, file, { upsert: true });

    if (uploadError) {
      console.error('Upload error:', uploadError.message);
      setUploading(false);
      return;
    }

    const { data: publicData } = supabase.storage.from('site-images').getPublicUrl(filePath);

    const publicUrl = publicData?.publicUrl;
    if (!publicUrl) return;

    const { error: updateError } = await supabase
      .from('templates')
      .update({
        [dbField]: publicUrl,
        [`${dbField}_meta`]: {
          originalName: file.name,
          path: filePath,
          uploadedAt: new Date().toISOString(),
        },
      })
      .eq('id', templateId);

    if (updateError) {
      console.error('DB update error:', updateError.message);
    } else {
      setPreview(publicUrl);
      onChange?.(publicUrl);
    }

    setUploading(false);
  };

  const removeImage = async () => {
    if (!preview) return;

    const { data: bucketInfo } = supabase.storage.from('site-images').getPublicUrl('');
    const bucketUrl = bucketInfo?.publicUrl;
    const path = preview.replace(`${bucketUrl}/`, '');

    const { error: deleteError } = await supabase.storage.from('site-images').remove([path]);

    if (deleteError) {
      console.error('Storage delete error:', deleteError.message);
      return;
    }

    const { error: updateError } = await supabase
      .from('templates')
      .update({ [dbField]: null })
      .eq('id', templateId);

    if (updateError) {
      console.error('DB remove error:', updateError.message);
    } else {
      setPreview(null);
      onChange?.(null);
    }
  };

  return {
    preview,
    uploading,
    uploadImage,
    removeImage,
  };
}

import { useEffect, useState } from 'react';
import { supabase } from '../admin/lib/supabaseClient';

type Filter = { column: keyof any; operator: 'eq' | 'neq'; value: any };
type Sort = { column: keyof any; ascending?: boolean };

export function useLiveTable<T extends { id: string | number }>(
  table: string,
  filter?: Filter,
  sort?: Sort
): T[] {
  const [rows, setRows] = useState<T[]>([]);

  useEffect(() => {
    let mounted = true;

    const fetchInitial = async () => {
      let query = supabase.from(table).select('*');

      if (filter) {
        query = query.filter(filter.column as string, filter.operator, filter.value);
      }

      if (sort) {
        query = query.order(sort.column as string, {
          ascending: sort.ascending ?? true,
        });
      }

      const { data, error } = await query;
      if (!error && mounted) setRows((data as T[]) || []);
    };

    fetchInitial();

    const subscription = supabase
      .channel(`realtime:${table}`)
      .on('postgres_changes', { event: '*', schema: 'public', table }, (payload) => {
        const applyFilter = (row: T) => {
          if (!filter) return true;
          switch (filter.operator) {
            case 'eq':
              return row[filter.column as keyof T] === filter.value;
            case 'neq':
              return row[filter.column as keyof T] !== filter.value;
            default:
              return true;
          }
        };

        setRows((prev) => {
          const updated = [...prev];
          const index = updated.findIndex((r) => r.id === (payload.new as T)?.id);

          switch (payload.eventType) {
            case 'INSERT':
              return applyFilter(payload.new as T) ? [...updated, payload.new as T] : updated;
            case 'UPDATE':
              if (index !== -1 && applyFilter(payload.new as T)) {
                updated[index] = payload.new as T;
              }
              return updated;
            case 'DELETE':
              return updated.filter((r) => r.id !== (payload.old as T)?.id);
            default:
              return updated;
          }
        });
      })
      .subscribe();

    return () => {
      mounted = false;
      supabase.removeChannel(subscription);
    };
  }, [table, filter?.column, filter?.operator, filter?.value, sort?.column, sort?.ascending]);

  return rows;
}

'use client';

import { useEffect, useState } from 'react';

export function useLiveTableWrapper<T>(table: string, filter: any, sort: any): T[] {
  const [data, setData] = useState<T[]>([]);

  useEffect(() => {
    let mounted = true;

    const fetchData = async () => {
      const mod = await import('./useLiveTable.jsx');
      const result = mod.useLiveTable<{ id: string | number } & T>(table, filter, sort);
      if (mounted) setData(result);
    };

    fetchData();

    return () => {
      mounted = false;
    };
  }, [table, JSON.stringify(filter), JSON.stringify(sort)]);

  return data;
}

// hooks/useLoginStatus.ts
'use client';

import { useEffect, useState } from 'react';
import { createClient } from '@supabase/supabase-js';
import type { Session } from '@supabase/supabase-js';
import { Database } from '@/types/supabase';

export function useLoginStatus() {
  const [session, setSession] = useState<Session | null>(null);
  const [loading, setLoading] = useState(true);

  const supabase = createClient<Database>(process.env.NEXT_PUBLIC_SUPABASE_URL!, process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!);

  useEffect(() => {
    const fetch = async () => {
      const { data, error } = await supabase.auth.getSession();
      if (!error) setSession(data.session);
      setLoading(false);
    };

    const { data: listener } = supabase.auth.onAuthStateChange((_, newSession) => {
      setSession(newSession);
    });

    fetch();

    return () => {
      listener?.subscription.unsubscribe();
    };
  }, [supabase]);

  return {
    isAuthenticated: !!session,
    session,
    loading,
    user: session?.user ?? null,
  };
}

// hooks/useMockGeolocation.ts
'use client';

import { useEffect, useState } from 'react';

export function useMockGeolocation() {
  const [mockGeo, setMockGeo] = useState<{ lat: number; lon: number } | null>(null);

  useEffect(() => {
    const cookie = document.cookie
      .split('; ')
      .find((row) => row.startsWith('mock-geo='));

    if (!cookie) {
      setMockGeo(null);
      return;
    }

    try {
      const value = decodeURIComponent(cookie.split('=')[1]);
      const [lat, lon] = value.split(',').map(Number);
      if (!isNaN(lat) && !isNaN(lon)) {
        setMockGeo({ lat, lon });
      }
    } catch {
      setMockGeo(null);
    }
  }, []);

  return mockGeo;
}

import { useEffect, useState } from 'react';
import { useRouter, useSearchParams } from 'next/navigation';

export function useQueryFlash(key: string): string | null {
  const searchParams = useSearchParams();
  const [value, setValue] = useState<string | null>(null);

  useEffect(() => {
    const param = searchParams?.get(key);
    if (param && typeof param === 'string') {
      setValue(param);

      // Strip it from the URL without full reload
      const rest = new URLSearchParams(searchParams?.toString() || '');
      rest.delete(key);
      window.location.href = `${window.location.pathname}?${rest.toString()}`;
    }
  }, [searchParams?.get(key)]); // re-run if query changes

  return value;
}

