import { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';
import { supabase } from '@/lib/supabase/client';

export function useResolvedRole(): [string | null, () => Promise<void>] {
  const [role, setRole] = useState<string | null>(null);

  const resolveRole = async () => {
    const { data: sessionData } = await supabase.auth.getSession();
    const user = sessionData.session?.user;

    if (!user) {
      setRole(null);
      return;
    }

    const email = user.email ?? '';
    const cacheKey = `cached-role-${email}`;
    let resolvedRole = localStorage.getItem(cacheKey) || 'viewer';

    const { data: profile } = await supabase
      .from('user_profiles')
      .select('role')
      .eq('user_id', user.id)
      .maybeSingle();

    if (profile?.role) {
      resolvedRole = profile.role;
      localStorage.setItem(cacheKey, resolvedRole);
    }

    setRole(resolvedRole);
  };

  useEffect(() => {
    resolveRole();
  }, []);

  return [role, resolveRole];
}

export function useRoleAccessGuard(allowedRoles: string[]) {
  const [role, resolveRole] = useResolvedRole();
  const router = useRouter();

  useEffect(() => {
    if (!role) return;

    const email = localStorage.getItem('cached-email') ?? '';
    const isOwner = email === 'sandonjurowski@gmail.com';

    if (!allowedRoles.includes(role) && !isOwner) {
      router.push('/unauthorized');
    }
  }, [role, allowedRoles, router]);

  return role;
}

export async function getServerResolvedRole(userId: string): Promise<string> {
  const { data: profile } = await supabase
    .from('user_profiles')
    .select('role')
    .eq('user_id', userId)
    .maybeSingle();

  return profile?.role ?? 'viewer';
}

// hooks/useRouteQuery.ts
'use client';

import { useEffect, useMemo } from 'react';
import { useRouter } from 'next/navigation';
import { z, ZodObject, ZodRawShape } from 'zod';
import { parseQueryParams } from '@/lib/parseQueryParams';
import { stringifyQueryParams } from '@/lib/stringifyQueryParams';

export function useRouteQuery<T extends ZodRawShape>(
  schema: ZodObject<T>
): {
  params: z.infer<typeof schema>;
  setParam: (key: keyof z.infer<typeof schema>, value: any) => void;
  clearParam: (key: keyof z.infer<typeof schema>) => void;
} {
  const router = useRouter();

  const searchParams = useMemo(() => {
    if (typeof window === 'undefined') return new URLSearchParams();
    return new URLSearchParams(window.location.search);
  }, []);

  const params = useMemo(() => parseQueryParams(schema, searchParams), [schema, searchParams]);

  const setParam = (key: keyof typeof params, value: any) => {
    const next = { ...params, [key]: value };
    const query = stringifyQueryParams(schema, next);
    const newUrl = `${window.location.pathname}?${query}`;
    router.replace(newUrl);
  };

  const clearParam = (key: keyof typeof params) => {
    const updated = new URLSearchParams(window.location.search);
    updated.delete(key as string);
    const newUrl = `${window.location.pathname}?${updated.toString()}`;
    router.replace(newUrl);
  };

  // Inject defaults once on mount
  useEffect(() => {
    if (typeof window === 'undefined') return;

    const parsed = parseQueryParams(schema, searchParams);
    const urlParams = new URLSearchParams(window.location.search);
    let changed = false;

    for (const key in parsed) {
      const val = parsed[key];
      const raw = urlParams.get(key);
      const norm = Array.isArray(val) ? val.join(',') : String(val);
      if (!raw && val !== undefined && val !== null) {
        urlParams.set(key, norm);
        changed = true;
      }
    }

    if (changed) {
      router.replace(`${window.location.pathname}?${urlParams.toString()}`);
    }
  }, []);

  return { params, setParam, clearParam };
}

// hooks/useSafeAuth.ts
'use client';

import { useEffect, useState } from 'react';
import { createClient } from '@supabase/supabase-js';
import type { Database } from '@/types/supabase';
import type { SafeUser } from '@/types/safe-user';

type SafeAuth = {
  user: SafeUser | null;
  role: string;
  isLoggedIn: boolean;
  ip?: string;
  userAgent?: string;
};

export function useSafeAuth(): SafeAuth {
  const [auth, setAuth] = useState<SafeAuth>({
    user: null,
    role: 'guest',
    isLoggedIn: false,
    ip: undefined,
    userAgent: undefined,
  });

  useEffect(() => {
    const supabase = createClient<Database>(process.env.NEXT_PUBLIC_SUPABASE_URL!, process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!);

    const readFromBody = () => {
      if (typeof window === 'undefined') return null;
      const body = document?.body?.dataset;
      const id = body?.userId ?? '';
      const email = body?.userEmail ?? '';
      const role = body?.userRole ?? 'guest';
      const avatar_url = body?.userAvatarUrl ?? '';
      const name = body?.userName ?? '';

      if (!id || !email) return null;

      return {
        user: {
          id,
          email,
          avatar_url,
          name,
        },
        role,
        isLoggedIn: true,
      };
    };

    const init = async () => {
      const headerInfo = readFromBody();

      if (headerInfo) {
        setAuth({
          ...headerInfo,
          ip: undefined,
          userAgent: typeof navigator !== 'undefined' ? navigator.userAgent : undefined,
        });
        return;
      }

      // üß± Fallback: try Supabase if headers not available
      const { data: { user }, error } = await supabase.auth.getUser();

      if (error) {
        console.warn('[useSafeAuth] Supabase auth error:', error.message);
      }

      setAuth({
        user: user
          ? {
              id: user.id,
              email: user.email ?? '',
              avatar_url: user.user_metadata?.avatar_url ?? '',
              name: user.user_metadata?.name ?? '',
            }
          : null,
        role: user?.user_metadata?.role ?? 'guest',
        isLoggedIn: !!user,
        ip: undefined,
        userAgent: typeof navigator !== 'undefined' ? navigator.userAgent : undefined,
      });
    };

    init();
  }, []);

  return auth;
}

import { useEffect, useState } from 'react';

const DRAFT_KEY = 'campaign-draft';

type DraftData = {
  city?: string;
  state?: string;
  industry?: string;
  selectedLeadIds?: string[];
  silentMode?: boolean;
  alt1?: string;
  alt2?: string;
};

export function useSavedCampaignDraft(): [
  DraftData,
  (draft: Partial<DraftData>) => void,
  () => void
] {
  const [draft, setDraft] = useState<DraftData>({});

  // Load on mount
  useEffect(() => {
    const raw = localStorage.getItem(DRAFT_KEY);
    if (raw) {
      try {
        setDraft(JSON.parse(raw));
      } catch {
        localStorage.removeItem(DRAFT_KEY);
      }
    }
  }, []);

  // Save subset
  const updateDraft = (partial: Partial<DraftData>) => {
    const next = { ...draft, ...partial };
    setDraft(next);
    localStorage.setItem(DRAFT_KEY, JSON.stringify(next));
  };

  const clearDraft = () => {
    setDraft({});
    localStorage.removeItem(DRAFT_KEY);
  };

  return [draft, updateDraft, clearDraft];
}

export function useSelectedLeadsWrapper(
    selectedLeads: string[],
    toggleLead: (id: string) => void
  ): {
    setSelectedLeadIds: (ids: string[]) => void;
  } {
    return {
      setSelectedLeadIds: (ids: string[]) => {
        const current = new Set(selectedLeads);
        const next = new Set(ids);

        // Deselect leads not in new list
        for (const id of current) {
          if (!next.has(id)) toggleLead(id);
        }

        // Select new leads not already selected
        for (const id of next) {
          if (!current.has(id)) toggleLead(id);
        }
      },
    };
  }

'use client';

import { useEffect, useState } from 'react';
import { supabase } from '@/lib/supabase/client';

type SessionUser = {
  id: string;
  email: string;
  role: string;
};

export function useSession() {
  const [user, setUser] = useState<SessionUser | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const getUser = async () => {
      const { data: authData, error: authError } = await supabase.auth.getUser();

      if (authError || !authData?.user) {
        setUser(null);
        setLoading(false);
        return;
      }

      const { id, email } = authData.user;

      // Optional: fetch role from `user_profiles` table
      const { data: profile, error: profileError } = await supabase
        .from('user_profiles')
        .select('role')
        .eq('user_id', id)
        .maybeSingle();

      const role = profile?.role || 'viewer';

      setUser({ id, email: email ?? '', role });
      setLoading(false);
    };

    getUser();
  }, []);

  return { user, loading };
}

// hooks/useSetSessionFromHash.ts
'use client';

import { useEffect } from 'react';
import { supabase } from '@/admin/lib/supabaseClient';
import { useRouter } from 'next/navigation';

export function useSetSessionFromHash() {
  const router = useRouter();

  useEffect(() => {
    const hash = window.location.hash;

    if (!hash) return;

    const params = new URLSearchParams(hash.substring(1));
    const accessToken = params.get('access_token');
    const refreshToken = params.get('refresh_token');

    if (!accessToken || !refreshToken) return;

    console.log('[üîê Extracted tokens from hash]', { accessToken, refreshToken });

    supabase.auth.setSession({ access_token: accessToken, refresh_token: refreshToken })
      .then(({ error }) => {
        if (error) {
          console.error('[‚ùå Failed to set session]', error);
        } else {
          console.log('[‚úÖ Supabase session restored]');
          router.replace('/'); // üîÑ Clean up the hash from URL
        }
      });
  }, []);
}

import { useState } from 'react';
import { v4 as uuidv4 } from 'uuid';
import type { Template } from '@/types/template';

export function useTemplateInsert(
  setTemplate: (updater: (prev: Template) => Template) => void
) {
  const [recentlyInsertedBlockId, setRecentlyInsertedBlockId] = useState<string | null>(null);
  const [lastInsertedBlock, setLastInsertedBlock] = useState<{ pageIndex: number; blockIndex: number } | null>(null);

  const insertBlock = (text: string, pageIndex = 0) => {
    const newId = uuidv4();
    setTemplate((prev) => {
      const updated = { ...prev };
      const blocks = updated.data.pages[pageIndex].content_blocks;
      const blockIndex = blocks.length;

      blocks.push({
        _id: newId,
        type: 'text',
        content: { value: text },
        tags: ['ai'],
      });

      setLastInsertedBlock({ pageIndex, blockIndex });
      return updated;
    });

    setRecentlyInsertedBlockId(newId);
  };

  const undoInsert = () => {
    if (!lastInsertedBlock) return;

    setTemplate((prev) => {
      const updated = { ...prev };
      updated.data.pages[lastInsertedBlock.pageIndex].content_blocks.splice(lastInsertedBlock.blockIndex, 1);
      return updated;
    });

    setRecentlyInsertedBlockId(null);
    setLastInsertedBlock(null);
  };

  return {
    insertBlock,
    undoInsert,
    recentlyInsertedBlockId,
    hasUndo: !!lastInsertedBlock,
  };
}

// hooks/useTemplateMeta.ts
import { useEffect, useState } from 'react';
import { debounce } from 'lodash';
import { stripTimestampFromName, slugify } from '@/lib/utils/slug';
import { supabase } from '@/lib/supabase';

export function useTemplateMeta(name: string, currentId?: string) {
  const clean = stripTimestampFromName(name);
  const [inputValue, setInputValue] = useState(clean);
  const [slugPreview, setSlugPreview] = useState('');
  const [nameExists, setNameExists] = useState(false);

  useEffect(() => {
    const handler = setTimeout(() => {
      setSlugPreview(slugify(inputValue));
    }, 150);
    return () => clearTimeout(handler);
  }, [inputValue]);

  useEffect(() => {
    if (!inputValue.trim()) return;
    const check = debounce(async () => {
      const query = supabase
        .from('templates')
        .select('id')
        .eq('template_name', inputValue.trim());

      // Only apply the filter if currentId is a valid UUID string
      if (currentId && currentId.length >= 8) {
        query.neq('id', currentId);
      }

      const { data } = await query.maybeSingle();
      setNameExists(!!data);
    }, 300);

    check();
    return () => check.cancel?.();
  }, [inputValue, currentId]);

  return {
    inputValue,
    setInputValue,
    slugPreview,
    nameExists,
  };
}

// hooks/useTemplateSave.ts
import { supabase } from '@/lib/supabase';
import { toast } from 'react-hot-toast';
import type { ValidatedTemplate } from '@/admin/lib/zod/templateSaveSchema';
import { TemplateSaveSchema } from '@/admin/lib/zod/templateSaveSchema';

export async function useTemplateSave(template: ValidatedTemplate): Promise<boolean> {
  const validated = TemplateSaveSchema.safeParse(template);
  if (!validated.success) {
    toast.error('Template is invalid. Cannot save.');
    console.warn('[Validation Error]', validated.error.format());
    return false;
  }

  const { data, error } = await supabase
    .from('templates')
    .update(validated.data)
    .eq('id', template.id);

  if (error) {
    toast.error('Save failed');
    return false;
  }

  toast.success('Template saved!');
  return true;
}

// /hooks/useThemeContext.tsx
import { createContext, useContext, useEffect, useState } from 'react';
import { createClient } from '@supabase/supabase-js';
import type { Database } from '@/types/supabase';

export type GlowConfig = {
  size: 'sm' | 'md' | 'lg' | 'xl';
  intensity: number;
  colors: string[];
};

export type SiteTheme = {
  glow: GlowConfig[];
  fontFamily?: string;
  borderRadius?: string;
  accentColor?: string;
};

const defaultGlow: GlowConfig[] = [
  { size: 'xl', intensity: 0.2, colors: ['from-purple-600', 'via-blue-500', 'to-pink-500'] },
];

const defaultTheme: SiteTheme = {
  glow: defaultGlow,
  fontFamily: 'sans',
  borderRadius: 'lg',
  accentColor: 'indigo-600',
};

const ThemeContext = createContext<{
  theme: SiteTheme;
  setTheme: (t: SiteTheme) => void;
  siteSlug: string;
}>({
  theme: defaultTheme,
  setTheme: () => {},
  siteSlug: 'default',
});

export function ThemeProvider({
  children,
  siteSlug = 'default',
}: {
  children: React.ReactNode;
  siteSlug?: string;
}) {
  const supabase = createClient<Database>(process.env.NEXT_PUBLIC_SUPABASE_URL!, process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!);
  const [theme, setThemeState] = useState<SiteTheme>(defaultTheme);

  useEffect(() => {
    const loadTheme = async () => {
      if (typeof window === 'undefined') return;

      const key = `theme-config::${siteSlug}`;
      const cached = localStorage.getItem(key);
      if (cached) {
        try {
          const parsed = JSON.parse(cached);
          setThemeState(parsed);
          return;
        } catch {
          console.warn('‚ö†Ô∏è Failed to parse cached theme');
        }
      }

      const userId = (window as any).__DEV_MOCK_USER__?.id;
      if (userId) {
        const { data } = await supabase
          .from('user_site_settings')
          .select('glow_config, theme_font, theme_radius, theme_accent')
          .eq('user_id', userId)
          .eq('site_slug', siteSlug)
          .single();

        if (data) {
          setThemeState({
            glow: Array.isArray(data.glow_config) ? data.glow_config : [data.glow_config],
            fontFamily: data.theme_font || defaultTheme.fontFamily,
            borderRadius: data.theme_radius || defaultTheme.borderRadius,
            accentColor: data.theme_accent || defaultTheme.accentColor,
          });
          return;
        }
      }

      setThemeState(defaultTheme);
    };

    loadTheme();
  }, [siteSlug]);

  const setTheme = async (value: SiteTheme) => {
    setThemeState(value);

    const userId = (window as any).__DEV_MOCK_USER__?.id;
    if (typeof window !== 'undefined') {
      const key = `theme-config::${siteSlug}`;
      try {
        localStorage.setItem(key, JSON.stringify(value));

        if (userId) {
          await supabase.from('user_site_settings').upsert({
            user_id: userId,
            site_slug: siteSlug,
            glow_config: value.glow,
            theme_font: value.fontFamily,
            theme_radius: value.borderRadius,
            theme_accent: value.accentColor,
          });
        }
      } catch (err) {
        console.warn('‚ö†Ô∏è Failed to persist theme', err);
      }
    }
  };

  return (
    <ThemeContext.Provider value={{ theme, setTheme, siteSlug }}>
      {children}
    </ThemeContext.Provider>
  );
}

export function useTheme() {
  return useContext(ThemeContext);
}

'use client';

import { useEffect, useMemo } from 'react';
import { useRouter } from 'next/navigation';
import { z, ZodObject, ZodRawShape } from 'zod';
import { parseQueryParams } from '@/lib/parseQueryParams';
import { stringifyQueryParams } from '@/lib/stringifyQueryParams';

type ParamTools<T> = {
  params: T;
  setParam: (key: keyof T, value: any) => void;
  clearParam: (key: keyof T) => void;
};

export function useTypedQuery<T extends ZodRawShape>(
  schema: ZodObject<T>
): ParamTools<z.infer<typeof schema>> {
  const router = useRouter();
  const queryParams = useMemo(() => {
    return typeof window !== 'undefined'
      ? new URLSearchParams(window.location.search)
      : new URLSearchParams();
  }, []);

  const params = useMemo(() => parseQueryParams(schema, queryParams), [schema, queryParams]);

  const setParam = (key: keyof typeof params, value: any) => {
    const next = { ...params, [key]: value };
    const query = stringifyQueryParams(schema, next);
    const newUrl = `${window.location.pathname}?${query}`;
    router.replace(newUrl);
  };

  const clearParam = (key: keyof typeof params) => {
    const updated = new URLSearchParams(window.location.search);
    updated.delete(key as string);
    const newUrl = `${window.location.pathname}?${updated.toString()}`;
    router.replace(newUrl);
  };

  // Inject defaults only once on mount
  useEffect(() => {
    if (typeof window === 'undefined') return;
    const parsed = parseQueryParams(schema, queryParams);
    const urlParams = new URLSearchParams(window.location.search);
    let changed = false;

    for (const key in parsed) {
      const val = parsed[key];
      const raw = urlParams.get(key);
      const norm = Array.isArray(val) ? val.join(',') : String(val);
      if (!raw && val !== undefined && val !== null) {
        urlParams.set(key, norm);
        changed = true;
      }
    }

    if (changed) {
      router.replace(`${window.location.pathname}?${urlParams.toString()}`);
    }
  }, []);

  return { params, setParam, clearParam };
}

'use client';

import { useEffect } from 'react';
import { useRouter } from 'next/navigation';
import { z } from 'zod';
import { parseTypedQueryValue } from '@/admin/lib/query/parseTypedQueryValue';

type InferParamType<T> =
  T extends 'string[]' ? string[] :
  T extends 'number[]' ? number[] :
  T extends 'string' ? string :
  T extends 'number' ? number :
  T extends 'date' ? Date :
  T extends 'date[]' ? Date[] :
  T extends 'json' ? Record<string, any> :
  T extends 'json[]' ? Record<string, any>[] :
  T extends 'boolean' ? boolean :
  never;

type ParamReturn<T> = [
  InferParamType<T>,
  (value: any) => void,
  () => void
];

function getSearchParams(): URLSearchParams {
  return typeof window !== 'undefined'
    ? new URLSearchParams(window.location.search)
    : new URLSearchParams();
}

function isArrayType(type: string): boolean {
  return ['string[]', 'number[]', 'date[]', 'json[]'].includes(type);
}

export default function useTypedQueryParam<
  T extends
    | 'string'
    | 'number'
    | 'boolean'
    | 'string[]'
    | 'number[]'
    | 'date'
    | 'date[]'
    | 'json'
    | 'json[]'
>(
  key: string,
  fallback: any,
  type: T,
  schema?: z.ZodTypeAny
): ParamReturn<T> {
  const router = useRouter();
  const searchParams = getSearchParams();

  // Set default param if missing (only in browser)
  useEffect(() => {
    if (typeof window === 'undefined') return;

    if (!searchParams.has(key) && fallback !== undefined && type !== 'json') {
      const updated = new URLSearchParams(window.location.search);
      updated.set(key, String(fallback));
      const newUrl = `${window.location.pathname}?${updated.toString()}`;
      router.replace(newUrl);
    }
  }, [key, fallback, router, type]);

  const setParam = (value: any) => {
    const newParams = new URLSearchParams(window.location.search);

    if (Array.isArray(value)) {
      newParams.delete(key);
      value.forEach((v) => {
        const serialized =
          typeof v === 'object'
            ? encodeURIComponent(JSON.stringify(v))
            : String(v);
        newParams.append(key, serialized);
      });
    } else if (value instanceof Date) {
      newParams.set(key, value.toISOString());
    } else if (typeof value === 'object' && value !== null) {
      newParams.set(key, encodeURIComponent(JSON.stringify(value)));
    } else {
      newParams.set(key, String(value));
    }

    const newUrl = `${window.location.pathname}?${newParams.toString()}`;
    router.replace(newUrl);
  };

  const clearParam = () => {
    const newParams = new URLSearchParams(window.location.search);
    newParams.delete(key);
    const newUrl = `${window.location.pathname}?${newParams.toString()}`;
    router.replace(newUrl);
  };

  const values = searchParams.getAll(key);
  const raw = isArrayType(type) ? values : searchParams.get(key);

  const parsed: InferParamType<T> = parseTypedQueryValue(
    key,
    raw,
    fallback,
    type,
    schema as any,
    router as any
  );

  return [parsed, setParam, clearParam];
}

import { useEffect, useState } from 'react';
import { useRouter, useSearchParams } from 'next/navigation';
import { createClient } from '@supabase/supabase-js';
import { ZodTypeAny } from 'zod';
import { defaultSchema } from '@/admin/lib/defaultSchema';
import { jsonSchemaToZod } from '@/admin/utils/jsonSchemaToZod';
import { zodToJsonSchema } from '@/admin/utils/zodToJsonSchema';
import { useCurrentUser } from '@/hooks/useCurrentUser';
import { logEmbedView } from '@/admin/lib/logEmbedView';

const supabase = createClient(
  process.env.NEXT_PUBLIC_SUPABASE_URL!,
  process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!
);

export function useZodPlaygroundState() {
  const router = useRouter();
  const params = useSearchParams();
  const { user } = useCurrentUser();

  // was: ts-expect-error TS2589: deep type inference
  const [schema, setSchema] = useState<ZodTypeAny>(defaultSchema);
  const [error, setError] = useState<string | null>(null);
  const [jsonExport, setJsonExport] = useState<string | null>(null);
  const [shortLink, setShortLink] = useState<string | null>(null);
  const [slackUsername, setSlackUsername] = useState('');

  const isEmbed =
    typeof window !== 'undefined' &&
    new URLSearchParams(window.location.search).get('embed') === '1';

  const loadSchemaFromQueryParam = () => {
    const encoded = params?.get('schema');
    if (!encoded) return;

    try {
      const decoded = decodeURIComponent(encoded);
      const parsed = JSON.parse(decoded);
      const zodified = jsonSchemaToZod(parsed);
      // was: @ts-expect-error TS2589: deep type inference
      setSchema(zodified);
      setJsonExport(decoded);
    } catch (err: any) {
      setError('Invalid schema from query param: ' + err.message);
    }
  };

  useEffect(() => {
    const schemaId = params?.get('schema_id');
    if (schemaId) {
      supabase
        .from('schema_links')
        .select('*')
        .eq('id', schemaId)
        .single()
        .then(({ data, error }) => {
          if (error || !data) {
            router.push('/admin/links?error=schema_id_not_found');
            return;
          }

          try {
            const parsed = JSON.parse(data.json);
            const zodified = jsonSchemaToZod(parsed);
            setSchema(zodified);
            setJsonExport(data.json);
            if (isEmbed) logEmbedView(schemaId);
          } catch (err: any) {
            setError('Failed to parse schema from ID: ' + err.message);
          }
        });

      return;
    }

    loadSchemaFromQueryParam();
  }, [params]);

  const exportJsonSchema = () => {
    try {
      // was: @ts-expect-error TS2589: deep type inference
      const json = zodToJsonSchema(schema);
      const stringified = JSON.stringify(json, null, 2);
      setJsonExport(stringified);

      const blob = new Blob([stringified], { type: 'application/json' });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'schema.json';
      a.click();
      URL.revokeObjectURL(url);
    } catch (err: any) {
      setError('Failed to export schema: ' + err.message);
    }
  };

  const handleCreateShortLink = async () => {
    if (!user || !jsonExport) return;

    const { data, error } = await supabase
      .from('schema_links')
      .insert({
        user_id: user.id,
        json: jsonExport,
        slack_username: slackUsername,
      })
      .select()
      .single();

    if (error) return alert('Failed to create short link');
    setShortLink(`${window.location.origin}/admin/zod-playground?schema_id=${data.id}`);
  };

  const handleDeploy = (payload: Record<string, any>) => {
    const encoded = encodeURIComponent(JSON.stringify(payload));
    router.push(`/launch?params=${encoded}`);
  };

  return {
    schema,
    setSchema,
    error,
    setError,
    jsonExport,
    setJsonExport,
    shortLink,
    setShortLink,
    slackUsername,
    setSlackUsername,
    exportJsonSchema,
    handleCreateShortLink,
    handleDeploy,
    isEmbed,
  };
}

