// lib/leads/distance.ts
import { Lead } from '@/types/lead.types';

export function getDistanceMiles(lat1: number, lon1: number, lat2: number, lon2: number): number {
    const R = 3958.8; // Earth radius in miles
    const toRad = (deg: number) => deg * (Math.PI / 180);

    const dLat = toRad(lat2 - lat1);
    const dLon = toRad(lon2 - lon1);

    const a =
      Math.sin(dLat / 2) ** 2 +
      Math.cos(toRad(lat1)) * Math.cos(toRad(lat2)) * Math.sin(dLon / 2) ** 2;

    return R * 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  }

  export function sortLeadsByDistance(
    leads: Lead[],
    targetLat: number,
    targetLon: number
  ): Lead[] {
    return leads
      .filter((l) => l.address_lat !== null && l.address_lon !== null)
      .sort((a, b) => {
        const distA = getDistanceMiles(targetLat, targetLon, a.address_lat!, a.address_lon!);
        const distB = getDistanceMiles(targetLat, targetLon, b.address_lat!, b.address_lon!);
        return distA - distB;
      });
  }

import { z } from 'zod';
import type { Lead as BaseLead } from '@/types/lead.types';
import { getDistanceMiles } from './distance';

export const zLead = z.object({
  id: z.string(),
  business_name: z.string(),
  campaign_id: z.string().nullable(),
  draft_sites: z
    .object({
      domain: z.string().optional(),
      is_claimed: z.boolean().optional(),
    })
    .nullable()
    .optional(),
  users: z
    .object({
      email: z.string().email().optional(),
    })
    .nullable()
    .optional(),
  address_lat: z.number().nullable().optional(),
  address_lon: z.number().nullable().optional(),
});

export type EnrichedLead = BaseLead & {
  draft_sites?: {
    domain?: string;
    is_claimed?: boolean;
  };
  users?: {
    email?: string;
  };
  address_lat?: number | null;
  address_lon?: number | null;
  link_type: 'üìé campaign_id' | 'üì¶ lead_ids[]' | null;
  distance_km?: number;
};

export function enrichLead(
  lead: any,
  campaignMap: Map<string, string[]>
): EnrichedLead | null {
  const parsed = zLead.safeParse(lead);
  if (!parsed.success) {
    console.warn('‚ùå Invalid lead skipped:', parsed.error.format());
    return null;
  }

  const cleaned = parsed.data;
  let link_type: EnrichedLead['link_type'] = null;

  for (const [campId, ids] of campaignMap.entries()) {
    if (ids.includes(cleaned.id)) {
      link_type = cleaned.campaign_id === campId ? 'üìé campaign_id' : 'üì¶ lead_ids[]';
      break;
    }
  }

  return {
    ...lead,
    link_type,
  };
}

export function enrichLeadWithDistance(
  lead: any,
  campaignMap: Map<string, string[]>,
  campaignLat: number,
  campaignLon: number
): EnrichedLead | null {
  const base = enrichLead(lead, campaignMap);
  if (!base) return null;

  const lat = base.address_lat;
  const lon = base.address_lon;

  if (lat !== null && lon !== null && typeof lat === 'number' && typeof lon === 'number') {
    const distMiles = getDistanceMiles(campaignLat, campaignLon, lat, lon);
    return { ...base, distance_km: Math.round(distMiles * 1.60934 * 10) / 10 }; // round to 1 decimal
  }

  return base;
}

import { supabase } from '@/admin/lib/supabaseClient';

export type LeadSummary = {
  id: string;
  business_name: string | null;
  address_city: string | null;
  address_state?: string | null;
  created_at?: string | null;
  industry?: string | null;
  current_campaign_id?: string | null;
  current_campaign_expires_at?: string | null;
};

type LoadLeadsOptions = {
  city?: string;
  state?: string;
  industry?: string;
  sortBy?: 'created_at' | 'business_name';
  sortOrder?: 'asc' | 'desc';
};

const _leadCache = new Map<string, LeadSummary[]>();

function makeCacheKey(opts: LoadLeadsOptions) {
  return JSON.stringify(opts);
}

export async function loadLeads(options: LoadLeadsOptions = {}): Promise<LeadSummary[]> {
  const cacheKey = makeCacheKey(options);
  if (_leadCache.has(cacheKey)) return _leadCache.get(cacheKey)!;

  const { data, error } = await supabase
    .from('leads')
    .select(`
      id,
      business_name,
      address_city,
      address_state,
      created_at,
      industry,
      current_campaign_id,
      current_campaign_expires_at
    `);

  if (error) {
    console.error('Error loading leads:', error.message);
    return [];
  }

  let leads = data || [];

  if (options.city) {
    leads = leads.filter((l) => l.address_city?.toLowerCase() === options.city!.toLowerCase());
  }

  if (options.state) {
    leads = leads.filter((l) => l.address_state?.toLowerCase() === options.state!.toLowerCase());
  }

  if (options.industry) {
    leads = leads.filter((l) => l.industry?.toLowerCase() === options.industry!.toLowerCase());
  }

  if (options.sortBy) {
    leads.sort((a, b) => {
      const aVal = a[options.sortBy!] ?? '';
      const bVal = b[options.sortBy!] ?? '';
      return options.sortOrder === 'desc'
        ? String(bVal).localeCompare(String(aVal))
        : String(aVal).localeCompare(String(bVal));
    });
  }

  _leadCache.set(cacheKey, leads);
  return leads;
}

export async function getTopTwoLeads(
  options: LoadLeadsOptions
): Promise<[LeadSummary, LeadSummary] | null> {
  const leads = await loadLeads(options);
  if (leads.length >= 2) {
    return [leads[0], leads[1]];
  }
  return null;
}

// lib/parseQueryParams.ts
import { z, ZodObject, ZodRawShape } from 'zod';

type ParamSource = URLSearchParams | Record<string, string | undefined>;

function isURLSearchParams(input: ParamSource): input is URLSearchParams {
  return typeof (input as URLSearchParams).getAll === 'function';
}

function parseNestedParams(source: URLSearchParams): Record<string, any> {
  const result: Record<string, any> = {};

  for (const [key, value] of source.entries()) {
    // Handle array notation: tags[]=x
    const arrayMatch = key.match(/^(.+)\[\]$/);
    if (arrayMatch) {
      const base = arrayMatch[1];
      if (!Array.isArray(result[base])) {
        result[base] = [];
      }
      result[base].push(value);
      continue;
    }

    // Handle nested keys: filter[priceMin]
    const nestedMatch = key.match(/^([^\[\]]+)\[([^\[\]]+)\]$/);
    if (nestedMatch) {
      const [_, parent, child] = nestedMatch;
      result[parent] = {
        ...(result[parent] || {}),
        [child]: value,
      };
      continue;
    }

    // Default flat assignment
    result[key] = value;
  }

  return result;
}

export function parseQueryParams<T extends ZodRawShape>(
  schema: ZodObject<T>,
  input: ParamSource
): z.infer<ZodObject<T>> {
  let parsedInput: Record<string, any>;

  if (isURLSearchParams(input)) {
    parsedInput = parseNestedParams(input);
  } else {
    parsedInput = input;
  }

  const result = schema.safeParse(parsedInput);
  if (!result.success) {
    console.warn('[parseQueryParams] failed:', result.error.format());
    return schema.parse({});
  }

  return result.data;
}

