// lib/utils/distance.ts

export function haversineDistance(
    lat1: number,
    lon1: number,
    lat2: number,
    lon2: number
  ): number {
    const R = 6371; // km
    const toRad = (deg: number) => (deg * Math.PI) / 180;

    const dLat = toRad(lat2 - lat1);
    const dLon = toRad(lon2 - lon1);

    const a =
      Math.sin(dLat / 2) ** 2 +
      Math.cos(toRad(lat1)) *
        Math.cos(toRad(lat2)) *
        Math.sin(dLon / 2) ** 2;

    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
  }

  export function getDistanceMiles(
    lat1: number,
    lon1: number,
    lat2: number,
    lon2: number
  ): number {
    return haversineDistance(lat1, lon1, lat2, lon2) * 0.621371;
  }

export async function getLatLonForCityState(
    city: string,
    state?: string
  ): Promise<{ lat: number; lon: number } | null> {
    const query = `${city}${state ? `, ${state}` : ''}`;
    const url = `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(query)}`;

    try {
      const res = await fetch(url, {
        headers: {
          'User-Agent': 'QuickSites-Geocoder/1.0 (admin@quicksites.ai)',
          'Accept': 'application/json',
        },
      });

      const data = await res.json();
      if (Array.isArray(data) && data.length > 0) {
        return {
          lat: parseFloat(data[0].lat),
          lon: parseFloat(data[0].lon),
        };
      }
    } catch (e) {
      console.warn(`üåê Failed to geocode city: ${query}`, e);
    }

    return null;
  }

