export async function getLatLonFromQuery(query: string): Promise<{ lat: number; lon: number } | null> {
    if (!query.trim()) return null;

    try {
      const response = await fetch(`https://nominatim.openstreetmap.org/search?q=${encodeURIComponent(query)}&format=json&limit=1`);
      const data = await response.json();

      if (data && data.length > 0) {
        return {
          lat: parseFloat(data[0].lat),
          lon: parseFloat(data[0].lon),
        };
      }
    } catch (error) {
      console.warn('[üåê Geolocation API Error]', error);
    }

    return null;
  }

// lib/utils/slug.ts

export function slugify(str: string): string {
    return str
      .toLowerCase()
      .trim()
      .replace(/[^\w\s-]/g, '') // remove special chars
      .replace(/[\s_-]+/g, '-') // normalize dashes
      .replace(/^-+|-+$/g, ''); // trim edges
  }

  export function generateTimestampSlug(): string {
    const now = new Date();
    const parts = [
      now.getFullYear(),
      String(now.getMonth() + 1).padStart(2, '0'),
      String(now.getDate()).padStart(2, '0'),
      String(now.getHours()).padStart(2, '0'),
      String(now.getMinutes()).padStart(2, '0'),
      String(now.getSeconds()).padStart(2, '0'),
    ];
    return parts.join('-');
  }

  export function generateUniqueTemplateName(base: string): string {
    return `${slugify(base)}-${generateTimestampSlug()}`;
  }

  export function stripTimestampFromName(name: string): string {
    return name.replace(/-\d{4}-\d{2}-\d{2}-\d{2}-\d{2}-\d{2}$/, '');
  }

import { cookies } from 'next/headers';
import type { ReadonlyRequestCookies } from 'next/dist/server/web/spec-extension/adapters/request-cookies';
import type { ResponseCookies } from 'next/dist/compiled/@edge-runtime/cookies';

export type CookieAccessType = 'readonly' | 'writable';
export type SafeCookies = ReadonlyRequestCookies | ResponseCookies;

export interface SafeCookiesResult {
  cookies: SafeCookies;
  cookieMode: CookieAccessType;
}

/**
 * Provides a consistent, type-safe cookies() interface for both
 * server components (readonly) and route handlers (writable).
 */
export function useSafeCookies(): SafeCookiesResult {
  const raw = cookies() as unknown;

  const isWritable =
    typeof raw === 'object' &&
    raw !== null &&
    'set' in raw &&
    'delete' in raw;

  return {
    cookies: isWritable ? (raw as ResponseCookies) : (raw as ReadonlyRequestCookies),
    cookieMode: isWritable ? 'writable' : 'readonly',
  };
}

