    const raw = readFileSync(lintFile, "utf-8");
    results = JSON.parse(raw);
    if (!Array.isArray(results)) throw new Error("Expected an array of lint results");
  } catch (err) {
    console.error(`\u274C Failed to read or parse ${lintFile}:`, err);
    process.exit(1);
  }
  const filtered = results.filter(
    (entry) => entry.filePath.includes("/quicksites-v2/") && !entry.filePath.includes("/.venv/") && !entry.filePath.includes("/node_modules/")
  ).map((entry) => ({
    file: entry.filePath,
    errors: entry.errorCount,
    warnings: entry.warningCount,
    total: entry.errorCount + entry.warningCount,
    messages: entry.messages.map((m) => ({
      line: m.line,
      column: m.column,
      message: m.message,
      ruleId: m.ruleId,
      severity: m.severity === 2 ? "error" : "warning"
    }))
