  const grouped = /* @__PURE__ */ new Map();
  for (const entry of filtered) {
    const folder = getTopFolder(entry.file);
    if (!grouped.has(folder)) grouped.set(folder, []);
    grouped.get(folder).push(entry);
  }
  for (const entries of grouped.values()) {
    entries.sort((a, b) => {
      if (a.errors > 0 && b.errors === 0) return -1;
      if (a.errors === 0 && b.errors > 0) return 1;
      return b.total - a.total;
    });
  }
  const txtLines = [];
  txtLines.push(`Found ${filtered.length} files with issues:
`);
  for (const [folder, entries] of [...grouped.entries()].sort()) {
    txtLines.push(`${folder}/`);
    for (const entry of entries) {
      txtLines.push(
        `  ${entry.total} issues (${entry.errors} errors, ${entry.warnings} warnings): ${entry.file}`
      );
    }
    txtLines.push("");
  }
  console.log("\u{1F4C4} Writing lint-summary.txt...");
  writeFileSync(txtOut, txtLines.join("\n"));
  const mdLines = [];
  mdLines.push(`# ESLint Report`);
  mdLines.push(`Found **${filtered.length}** files with issues.
`);
  for (const [folder, entries] of [...grouped.entries()].sort()) {
    mdLines.push(`## \u{1F4C1} \`${folder}/\``);
    for (const entry of entries) {
      mdLines.push(`### \u{1F539} \`${entry.file}\``);
      mdLines.push(
        `- **${entry.total} issues**: ${entry.errors} error(s), ${entry.warnings} warning(s)
`
      );
      for (const m of entry.messages) {
        mdLines.push(
          `  - [${m.severity}] Line ${m.line}:${m.column} \u2013 ${m.message} (${m.ruleId || "unknown"})`
        );
      }
      mdLines.push("");
    }
  }
  console.log("\u{1F4C4} Writing lint-summary.md...");
  writeFileSync(mdOut, mdLines.join("\n"));
  if (!existsSync(txtOut) || !existsSync(mdOut)) {
    console.error("\u274C Failed to write summary files.");
    process.exit(1);
  }
  console.log(`\u2705 Summary complete \u2014 ${grouped.size} folders, ${filtered.length} files`);
}

