    console.error('Assets for search still loading');
    return;
  }

  var resultDocuments = [];
  var results = index.search(query);
  for (var i = 0; i < results.length; i++) {
    var result = results[i];
    doc = documents[result.ref];
    doc.summary = doc.text.substring(0, 200);
