function getSearchTerm() {
  var sPageURL = window.location.search.substring(1);
  var sURLVariables = sPageURL.split('&');
  for (var i = 0; i < sURLVariables.length; i++) {
    var sParameterName = sURLVariables[i].split('=');
    if (sParameterName[0] == 'q') {
      return sParameterName[1];
    }
  }
}

function applyTopPadding() {
  // Update various absolute positions to match where the main container
  // starts. This is necessary for handling multi-line nav headers, since
  // that pushes the main container down.
  var container = document.querySelector('body > .container');
  var offset = container.offsetTop;

  document.documentElement.style.scrollPaddingTop = offset + 'px';
  document.querySelectorAll('.bs-sidebar.affix').forEach(function (sidebar) {
    sidebar.style.top = offset + 'px';
  });
}

document.addEventListener('DOMContentLoaded', function () {
  var search_term = getSearchTerm();
  var search_modal = new bootstrap.Modal(
    document.getElementById('mkdocs_search_modal')
  );
  var keyboard_modal = new bootstrap.Modal(
    document.getElementById('mkdocs_keyboard_modal')
  );

  if (search_term) {
    search_modal.show();
  }

  // make sure search input gets autofocus every time modal opens.
  document
    .getElementById('mkdocs_search_modal')
    .addEventListener('shown.bs.modal', function () {
      document.getElementById('mkdocs-search-query').focus();
    });

  // Close search modal when result is selected
  // The links get added later so listen to parent
  document
    .getElementById('mkdocs-search-results')
    .addEventListener('click', function (e) {
      if (e.target.tagName === 'A') {
        search_modal.hide();
      }
    });

  // Populate keyboard modal with proper Keys
  document.querySelector('.help.shortcut kbd').innerHTML =
    keyCodes[shortcuts.help];
  document.querySelector('.prev.shortcut kbd').innerHTML =
    keyCodes[shortcuts.previous];
  document.querySelector('.next.shortcut kbd').innerHTML =
    keyCodes[shortcuts.next];
  document.querySelector('.search.shortcut kbd').innerHTML =
    keyCodes[shortcuts.search];

