'use client';

import { useEffect, useState } from 'react';
import { supabase } from '@/lib/supabaseClient.js';
import { Button } from '@/components/ui/button';
import { Template } from '@/types/template';

export default function TemplateUserViewer() {
  type Assignment = {
    user_id: string;
    email: string;
    template_id: string;
    template: string;
  };
  type TemplateOption = { id: string; name: string };
  const [templates, setTemplates] = useState<TemplateOption[]>([]);
  const [selectedTemplate, setSelectedTemplate] = useState<string>('');
  const [assignments, setAssignments] = useState<Assignment[]>([]);
  const [filtered, setFiltered] = useState<Assignment[]>([]);
  const [search, setSearch] = useState('');
  const [filterTemplate, setFilterTemplate] = useState('');
  const [selected, setSelected] = useState('');

  useEffect(() => {
    loadData();
  }, []);

  useEffect(() => {
    setFiltered(
      assignments.filter(
        (a) =>
          (!search || a.email.toLowerCase().includes(search.toLowerCase())) &&
          (!filterTemplate || a.template === filterTemplate)
      )
    );
  }, [search, filterTemplate, assignments]);

  const loadData = async () => {
    const { data: map } = await supabase
      .from('dashboard_user_layouts')
      .select('user_id, template_id');

    const { data: users } = await supabase
      .from('auth.users')
      .select('id, email');

    const { data: templates } = await supabase
      .from('dashboard_layout_templates')
      .select('id, name');

    const joined =
      map?.map((row) => ({
        user_id: row.user_id,
        email: users?.find((u) => u.id === row.user_id)?.email || '—',
        template_id: row.template_id,
        template:
          templates?.find((t) => t.id === row.template_id)?.name || '(deleted)',
      })) || [];

    setAssignments(joined);
    setTemplates(templates || []);
    setFiltered(joined);
  };

  const bulkAssign = async () => {
    const { data: users } = await supabase.from('auth.users').select('id');
    const inserts = users?.map((u) => ({
      user_id: u.id,
      template_id: selected,
    }));
    await supabase.from('dashboard_user_layouts').upsert(inserts || []);
    loadData();
    alert('All users assigned!');
  };

  const revertUser = async (user_id: string) => {
    await supabase
      .from('dashboard_user_layouts')
      .delete()
      .eq('user_id', user_id);
    loadData();
  };

  return (
    <div className="p-4 border rounded bg-white shadow max-w-3xl mt-8">
      <h2 className="text-lg font-semibold mb-3">
        User → Template Assignments
      </h2>

      <div className="flex flex-wrap gap-3 items-center mb-4">
        <input
          type="text"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          placeholder="Search user email..."
          className="border p-2 text-sm"
        />
        <select
          value={filterTemplate}
          onChange={(e) => setFilterTemplate(e.target.value)}
          className="border p-2 text-sm"
        >
          <option value="">All templates</option>
          {templates.map((tpl) => (
            <option key={tpl.id} value={tpl.name}>
              {tpl.name}
            </option>
          ))}
        </select>
        <select
          value={selected}
          onChange={(e) => setSelected(e.target.value)}
          className="border p-2 text-sm"
        >
          <option value="">Select template to assign all</option>
          {templates.map((tpl) => (
            <option key={tpl.id} value={tpl.id}>
              {tpl.name}
            </option>
          ))}
        </select>
        <Button onClick={bulkAssign}>Assign to All</Button>
      </div>

      <table className="w-full text-sm border">
        <thead className="bg-gray-100">
          <tr>
            <th className="text-left p-2">User Email</th>
            <th className="text-left p-2">Template</th>
            <th className="text-left p-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {filtered.map((a) => (
            <tr key={a.user_id} className="border-b">
              <td className="p-2">{a.email}</td>
              <td className="p-2">{a.template}</td>
              <td className="p-2">
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => revertUser(a.user_id)}
                >
                  Revert
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

import { ReactNode } from 'react';

interface TooltipProps {
  placement?: 'top' | 'bottom' | 'left' | 'right';
  content: ReactNode;
  children: ReactNode;
  labelledById?: string;
  clickable?: boolean;
}

import { useEffect, useRef, useState } from 'react';

export default function Tooltip({
  content,
  children,
  clickable = false,
  placement = 'top',
  labelledById = `tooltip-${Math.random().toString(36).slice(2, 10)}`,
}: TooltipProps) {
  const ref = useRef<HTMLDivElement>(null);
  const [autoPlacement, setAutoPlacement] = useState<
    'top' | 'bottom' | 'left' | 'right'
  >(placement);
  const [prevPlacement, setPrevPlacement] = useState<
    'top' | 'bottom' | 'left' | 'right'
  >(placement);

  useEffect(() => {
    if (!ref.current) return;
    setPrevPlacement(autoPlacement);
    const rect = ref.current.getBoundingClientRect();
    const buffer = 20;
    if (placement === 'top' && rect.top < buffer) setAutoPlacement('bottom');
    else if (
      placement === 'bottom' &&
      rect.bottom > window.innerHeight - buffer
    )
      setAutoPlacement('top');
    else if (placement === 'left' && rect.left < buffer)
      setAutoPlacement('right');
    else if (placement === 'right' && rect.right > window.innerWidth - buffer)
      setAutoPlacement('left');
  }, [placement]);

  const tooltipPosition =
    autoPlacement === 'top'
      ? 'bottom-full mb-1 -top-6'
      : autoPlacement === 'bottom'
        ? 'top-full mt-1 translate-y-1'
        : autoPlacement === 'left'
          ? 'right-full mr-1 -left-6 top-1/2 -translate-y-1/2'
          : 'left-full ml-1 -right-6 top-1/2 -translate-y-1/2';

  const arrowStyles =
    autoPlacement === 'top'
      ? 'before:-bottom-1 before:border-t-zinc-700'
      : autoPlacement === 'bottom'
        ? 'before:-top-1 before:border-b-zinc-700'
        : autoPlacement === 'left'
          ? 'before:-right-1 before:border-l-zinc-700'
          : 'before:-left-1 before:border-r-zinc-700';

  return (
    <div
      ref={ref}
      className="group relative inline-block touch-none"
      aria-describedby={labelledById}
      onTouchStart={() =>
        ref.current
          ?.querySelector('[role=tooltip]')
          ?.classList.add('opacity-100')
      }
    >
      <span
        role="tooltip"
        id={labelledById}
        aria-live="polite"
        aria-hidden="false"
        tabIndex={0}
        onKeyDown={(e) => {
          if (e.key === 'Escape') {
            (e.target as HTMLElement).blur();
          }
        }}
        className={`opacity-0 group-hover:opacity-100 group-hover:translate-y-0 absolute left-1/2 -translate-x-1/2 transition-[top,left] duration-300 ease-in-out bg-zinc-700 text-white text-xs rounded px-2 py-1 whitespace-nowrap z-10 transition-opacity transition-transform duration-300 ${clickable ? 'pointer-events-auto cursor-pointer' : 'pointer-events-none'} ${tooltipPosition} before:content-[''] before:absolute before:left-1/2 before:-translate-x-1/2 before:border-4 before:border-transparent ${arrowStyles}`}
      >
        {content}
      </span>
      {children}
    </div>
  );
}

