type TemplateSaveBarProps = {
  template: any;
  rawJson: string;
  commitMessage: string;
  setSaveStatus: Dispatch<SetStateAction<'idle' | 'saved' | 'error'>>;
  setHighlightErrors: Dispatch<SetStateAction<boolean>>;
};

export default function TemplateSaveBar({
  template,
  rawJson,
  commitMessage,
  setSaveStatus,
  setHighlightErrors,
}: TemplateSaveBarProps) {
  const [saveStatus, setLocalSaveStatus] = useState<'idle' | 'saved' | 'error'>(
    'idle'
  );

  const handleSave = async () => {
    if (
      !template?.template_name ||
      !template?.industry ||
      !template?.layout ||
      !template?.data?.pages?.length
    ) {
      setHighlightErrors(true);
      return;
    }

    try {
      const { data: user } = await supabase.auth.getUser();
      const editor_id = user?.user?.email || 'unknown';
      const payload = { ...template, data: JSON.parse(rawJson) };

      const res = await fetch('/api/templates/update', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          ...payload,
          commit_message: commitMessage,
          editor_id,
        }),
      });

      if (res.ok) {
        setLocalSaveStatus('saved');
        setSaveStatus('saved');
        toast.success('Template saved successfully.');
        setTimeout(() => {
          setLocalSaveStatus('idle');
          setSaveStatus('idle');
        }, 3000);
      } else {
        setLocalSaveStatus('error');
        setSaveStatus('error');
        toast.error('Failed to save template.');
      }
    } catch (err) {
      setLocalSaveStatus('error');
      setSaveStatus('error');
      toast.error('Failed to save template.');
    }
  };

  return (
    <div className="flex justify-between items-center fixed bottom-0 left-0 right-0 bg-background border-t p-4">
      <div className="flex items-center gap-2">
        <Button onClick={handleSave}>Save Changes</Button>
        {saveStatus === 'saved' && (
          <span className="text-green-500 text-xs animate-pulse">✓ Saved</span>
        )}
        {saveStatus === 'error' && (
          <span className="text-red-500 text-xs">⚠️ Error</span>
        )}
      </div>
    </div>
  );
}

import { useEffect, useState } from 'react';
import { Button } from '@/components/ui/button';
import { supabase } from '@/admin/lib/supabaseClient';
import VersionCompareDropdowns from '@/components/admin/templates/VersionCompareDropdowns';
import { renderVersionDiff } from '@/admin/utils/renderVersionDiff';

export default function VersionHistoryPanel({
  slug,
  onRestore,
}: {
  slug: string;
  onRestore: (snapshotId: string, brandingProfileId: string) => void;
}) {
  const [versions, setVersions] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [currentVersion, setCurrentVersion] = useState<string | null>(null);
  const [confirmingDelete, setConfirmingDelete] = useState<string | null>(null);

  useEffect(() => {
    const fetchVersions = async () => {
      const [versionRes, currentRes] = await Promise.all([
        supabase
          .from('published_versions')
          .select(
            `id, created_at, snapshot_id, label, slug, branding_profile_id, branding_profiles ( id, name, primary_color, secondary_color, font_family )`
          )
          .eq('slug', slug)
          .order('created_at', { ascending: false }),
        supabase
          .from('published_sites')
          .select('snapshot_id')
          .eq('slug', slug)
          .maybeSingle(),
      ]);

      if (!versionRes.error) setVersions(versionRes.data || []);
      if (!currentRes.error && currentRes.data)
        setCurrentVersion(currentRes.data.snapshot_id);
      setLoading(false);
    };

    fetchVersions();
  }, [slug]);

  if (loading)
    return <p className="text-sm text-gray-500">Loading versions...</p>;
  if (versions.length === 0)
    return (
      <p className="text-sm text-gray-500">No version history available.</p>
    );

  return (
    <div className="mt-4 space-y-2">
      <h3 className="font-semibold text-base">Version History</h3>
      <VersionCompareDropdowns slug={slug} onRestore={onRestore} />
      <ul className="space-y-2">
        {versions.map((v) => {
          const isCurrent = v.snapshot_id === currentVersion;
          return (
            <li
              key={v.id}
              className={`border rounded p-2 flex justify-between items-center ${isCurrent ? 'border-green-500' : 'border-gray-700'}`}
            >
              <div>
                <div className="text-sm text-gray-100">
                  Snapshot: <code>{v.snapshot_id}</code>
                </div>
                <div className="text-xs text-gray-400">
                  Label: {v.label || 'No label'}
                </div>
                <div className="text-xs text-gray-500">
                  Published: {new Date(v.created_at).toLocaleString()}
                </div>
                <div
                  className="text-xs mt-1"
                  style={{
                    fontFamily:
                      v.branding_profiles?.font_family || 'sans-serif',
                  }}
                >
                  Heading Preview in{' '}
                  {v.branding_profiles?.font_family || 'Default'}
                </div>
              </div>
              <div className="flex gap-2">
                {!isCurrent && (
                  <Button
                    size="sm"
                    onClick={() =>
                      onRestore(v.snapshot_id, v.branding_profile_id)
                    }
                  >
                    Restore
                  </Button>
                )}
                {confirmingDelete === v.id ? (
                  <Button
                    size="sm"
                    variant="destructive"
                    onClick={async () => {
                      const { error } = await supabase
                        .from('published_versions')
                        .delete()
                        .eq('id', v.id);
                      if (!error)
                        setVersions(versions.filter((ver) => ver.id !== v.id));
                      setConfirmingDelete(null);
                    }}
                  >
                    Confirm Delete
                  </Button>
                ) : (
                  <Button
                    size="sm"
                    variant="outline"
                    onClick={() => setConfirmingDelete(v.id)}
                  >
                    Delete
                  </Button>
                )}
              </div>
            </li>
          );
        })}
      </ul>
    </div>
  );
}

// hooks/useGeoLocation.ts
import { useEffect, useState } from 'react';
import { getCachedGeo, setCachedGeo } from '../lib/geoCache.js';
import staticGeo from '../public/staticGeo.json';

// USAGE:
// const { lat, lon, loading, error } = useGeoLocation('Franklin', 'WI');
// if (loading) return <p>Loading map...</p>;
// if (error) return <p>Error: {error}</p>;

function getStaticFallback(
  city: string,
  state: string
): { lat: number; lon: number } | undefined {
  const key = `${city.trim().toLowerCase()},${state.trim().toLowerCase()}`;
  return staticGeo[key as keyof typeof staticGeo];
}

export function useGeoLocation(city: string, state: string) {
  const [coords, setCoords] = useState<{ lat: number; lon: number } | null>(
    null
  );
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    if (!city || !state) return;

    const tryGeoSources = async () => {
      setLoading(true);
      const key = `${city}, ${state}`;

      // 1. Local memory cache
      const memoryHit = getCachedGeo(city, state);
      if (memoryHit) {
        setCoords(memoryHit);
        setLoading(false);
        return;
      }

      // 2. Static fallback JSON
      const staticHit = getStaticFallback(city, state);
      if (staticHit) {
        setCachedGeo(city, state, staticHit.lat, staticHit.lon);
        setCoords(staticHit);
        setLoading(false);
        return;
      }

      // 3. Remote API
      try {
        const res = await fetch(
          `/api/geocode?city=${encodeURIComponent(city)}&state=${encodeURIComponent(state)}`
        );
        if (!res.ok) throw new Error(`API failed: ${res.status}`);
        const data = await res.json();
        const lat = Number(data.lat || 0);
        const lon = Number(data.lon || 0);
        setCachedGeo(city, state, lat, lon);
        setCoords({ lat, lon });
      } catch (err: any) {
        setError(err.message || 'Unknown error');
        setCoords({ lat: 0, lon: 0 });
      } finally {
        setLoading(false);
      }
    };

    tryGeoSources();
  }, [city, state]);

  return { lat: coords?.lat, lon: coords?.lon, loading, error };
}

import { useState } from 'react';
import { supabase } from '../lib/supabaseClient.js';

interface UseImageUploaderOptions {
  siteId: string;
  templateId: string;
  dbField: string;
  folder?: string;
  label?: string;
  initialUrl?: string;
  onChange?: (url: string | null) => void;
}

export function useImageUploader({
  siteId,
  templateId,
  dbField,
  folder = 'uploads',
  label,
  initialUrl,
  onChange,
}: UseImageUploaderOptions) {
  const [preview, setPreview] = useState<string | null>(initialUrl ?? null);
  const [uploading, setUploading] = useState(false);

  function sanitizeFilename(filename: string): string {
    const parts = filename.split('.');
    const ext = parts.pop(); // file extension
    const base = parts.join('-').replace(/\s+/g, '-');
    const timestamp = Date.now(); // or use dayjs() for formatted date
    return `${base}-${timestamp}.${ext}`.toLowerCase();
  }

  const uploadImage = async (file: File) => {
    if (!file) return;

    const cleanName = sanitizeFilename(file.name);
    const filePath = `${folder}/${siteId}/${cleanName}`;
    setUploading(true);
    setPreview(URL.createObjectURL(file));

    const { error: uploadError } = await supabase.storage
      .from('site-images')
      .upload(filePath, file, { upsert: true });

    if (uploadError) {
      console.error('Upload error:', uploadError.message);
      setUploading(false);
      return;
    }

    const { data: publicData } = supabase.storage
      .from('site-images')
      .getPublicUrl(filePath);

    const publicUrl = publicData?.publicUrl;
    if (!publicUrl) return;

    const { error: updateError } = await supabase
      .from('templates')
      .update({
        [dbField]: publicUrl,
        [`${dbField}_meta`]: {
          originalName: file.name,
          path: filePath,
          uploadedAt: new Date().toISOString(),
        },
      })
      .eq('id', templateId);

    if (updateError) {
      console.error('DB update error:', updateError.message);
    } else {
      setPreview(publicUrl);
      onChange?.(publicUrl);
    }

    setUploading(false);
  };

  const removeImage = async () => {
    if (!preview) return;

    const { data: bucketInfo } = supabase.storage
      .from('site-images')
      .getPublicUrl('');
    const bucketUrl = bucketInfo?.publicUrl;
    const path = preview.replace(`${bucketUrl}/`, '');

    const { error: deleteError } = await supabase.storage
      .from('site-images')
      .remove([path]);

    if (deleteError) {
      console.error('Storage delete error:', deleteError.message);
      return;
    }

    const { error: updateError } = await supabase
      .from('templates')
      .update({ [dbField]: null })
      .eq('id', templateId);

    if (updateError) {
      console.error('DB remove error:', updateError.message);
    } else {
      setPreview(null);
      onChange?.(null);
    }
  };

  return {
    preview,
    uploading,
    uploadImage,
    removeImage,
  };
}

