'use client';

import { useRouter } from 'next/navigation.js';
import { useMemo, useState } from 'react';
import { parseISO, isAfter, isBefore } from 'date-fns';
import { motion, AnimatePresence } from 'framer-motion';
import SmartLinkGrid from '../../../../components/admin/SmartLinkGrid.jsx';
import SmartLinkGallery from '../../../../components/admin/SmartLinkGallery.jsx';
import { SmartLinkProvider } from '../../../../components/admin/SmartLinkProvider.jsx';
import { mockSmartLinks } from '../../../../tests/mocks/mocks/mockSmartLinks.jsx';
import type { SmartLinkItem } from '../../../../types/SmartLinkItem.jsx';

export default function SmartLinkGridPage() {
  const items: SmartLinkItem[] = mockSmartLinks;
  const router = useRouter();

  const searchParams = new URLSearchParams(
    typeof window !== 'undefined' ? window.location.search : ''
  );
  const sort = searchParams.get('sort') || '';
  const view = searchParams.get('view') || 'grid';
  const tag = searchParams.get('tag') || '';

  const updateSearchParam = (key: string, value: string) => {
    const params = new URLSearchParams(window.location.search);
    params.set(key, value);
    return `?${params.toString()}`;
  };

  const sorted = [...items].sort((a, b) => {
    if (sort === 'type') return a.type.localeCompare(b.type);
    if (sort === 'theme') return (a.theme || '').localeCompare(b.theme || '');
    return 0;
  });

  return (
    <SmartLinkProvider>
      <main className="p-6">
        <div className="flex items-center justify-between flex-wrap gap-4 mb-4">
          <h1 className="text-xl font-bold text-white">
            🧱 SmartLink Grid View
          </h1>
          <div className="flex flex-wrap gap-2 items-center">
            <button
              className="text-xs px-3 py-1 rounded bg-zinc-800 hover:bg-zinc-700 border border-zinc-700"
              onClick={() => router.push(updateSearchParam('sort', 'type'))}
            >
              Sort by Type
            </button>
            <button
              className="text-xs px-3 py-1 rounded bg-zinc-800 hover:bg-zinc-700 border border-zinc-700"
              onClick={() => router.push(updateSearchParam('sort', 'theme'))}
            >
              Sort by Theme
            </button>
          </div>

          <div className="flex gap-2 flex-wrap">
            {['template', 'snapshot'].map((t) => (
              <button
                key={t}
                className={`text-xs px-3 py-1 rounded border border-zinc-600 hover:bg-zinc-700 ${
                  tag === t
                    ? 'bg-blue-600 text-white'
                    : 'bg-zinc-800 text-zinc-300'
                }`}
                onClick={() => router.push(updateSearchParam('tag', t))}
              >
                {t}
              </button>
            ))}
            <button
              className={`text-xs px-3 py-1 rounded border ${
                view === 'grid'
                  ? 'bg-blue-700 text-white'
                  : 'bg-zinc-800 text-zinc-300'
              } border-zinc-600`}
              onClick={() => router.push(updateSearchParam('view', 'grid'))}
            >
              Grid
            </button>
            <button
              className={`text-xs px-3 py-1 rounded border ${
                view === 'gallery'
                  ? 'bg-blue-700 text-white'
                  : 'bg-zinc-800 text-zinc-300'
              } border-zinc-600`}
              onClick={() => router.push(updateSearchParam('view', 'gallery'))}
            >
              Gallery
            </button>
          </div>
        </div>

        <AnimatePresence mode="wait">
          {view === 'gallery' ? (
            <motion.div
              key="gallery"
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -10 }}
              transition={{ duration: 0.2 }}
            >
              <SmartLinkGallery items={sorted} />
            </motion.div>
          ) : (
            <motion.div
              key="grid"
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -10 }}
              transition={{ duration: 0.2 }}
            >
              <SmartLinkGrid items={sorted} />
            </motion.div>
          )}
        </AnimatePresence>
      </main>
    </SmartLinkProvider>
  );
}

'use client';
import { useEffect, useState } from 'react';
import { json } from '@/lib/api/json';
import { useRouter } from 'next/router';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';

export default function NewTemplatePage() {
  const router = useRouter();
  const [loading, setLoading] = useState(true);
  const [template, setTemplate] = useState({
    template_name: '',
    industry: '',
    layout: 'default',
    color_scheme: 'blue-yellow',
    data: `{
  "pages": [
    {
      "slug": "index",
      "title": "{{business_name}}",
      "meta_description": "",
      "content_blocks": []
    }
  ]
}`,
  });

  // Load existing template if ?copy=template_name
  useEffect(() => {
    const { copy } = router.query;
    if (copy && typeof copy === 'string') {
      fetch(`/api/templates/${copy}`)
        .then((res) => json())
        .then((data) => {
          setTemplate({
            template_name: `${data.template_name}-copy`,
            industry: data.industry,
            layout: data.layout,
            color_scheme: data.color_scheme,
            data: JSON.stringify(data.data, null, 2),
          });
        })
        .finally(() => setLoading(false));
    } else {
      setLoading(false);
    }
  }, [router.query]);

  const handleChange = (field: string, value: string) => {
    setTemplate((prev) => ({ ...prev, [field]: value }));
  };

  const handleCreate = async () => {
    try {
      const payload = {
        ...template,
        data: JSON.parse(template.data),
      };

      const res = await fetch('/api/templates/create', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });

      if (res.ok) {
        router.push('/admin/templates');
      } else {
        const err = await json();
        alert('Error: ' + err.error);
      }
    } catch (e: any) {
      alert('Invalid JSON: ' + e.message);
    }
  };

  if (loading) return <p className="p-6">Loading template...</p>;

  return (
    <div className="p-6 max-w-2xl space-y-6">
      <h2 className="text-xl font-bold">Create New Template</h2>

      <div className="grid gap-4">
        <div>
          <Label>Template Name</Label>
          <Input
            value={template.template_name}
            onChange={(e) => handleChange('template_name', e.target.value)}
          />
        </div>

        <div>
          <Label>Industry</Label>
          <Input
            value={template?.industry || ''}
            onChange={(e) => handleChange('industry', e.target.value)}
          />
        </div>

        <div>
          <Label>Layout</Label>
          <Input
            value={template.layout}
            onChange={(e) => handleChange('layout', e.target.value)}
          />
        </div>

        <div>
          <Label>Color Scheme</Label>
          <Input
            value={template.color_scheme}
            onChange={(e) => handleChange('color_scheme', e.target.value)}
          />
        </div>

        <div>
          <Label>Template JSON (data)</Label>
          <Textarea
            rows={12}
            className="font-mono text-sm"
            value={template.data}
            onChange={(e) => handleChange('data', e.target.value)}
          />
        </div>

        <Button onClick={handleCreate}>Save Template</Button>
      </div>
    </div>
  );
}

import { useEffect, useState } from 'react';
