            <Button>Create New</Button>
          </Link>
        </div>
      </div>

      {selectedTemplate && (
        <div className="border rounded overflow-hidden mt-4">
          <TemplateEditor templateName={selectedTemplate} />
        </div>
      )}
    </div>
  );
}

import { createClient } from '@supabase/supabase-js';
