}

export const getServerSideProps: GetServerSideProps = async (context) => {
  const slug = context.params?.slug;
  const { data, error } = await supabase
    .from('templates')
    .select('data')
    .eq('template_name', slug)
    .single();

  return {
    props: {
      slug,
      data: data?.data || null,
    },
  };
};

