};

// pages/embed/[id].tsx
import { GetServerSideProps } from 'next';
import Head from 'next/head';
import dynamic from 'next/dynamic';
import { supabase } from '@/lib/supabaseClient.js';
import { createClient } from '@supabase/supabase-js';

// const PlaygroundFrame = dynamic(() => import('@/components/admin/zod-playground/PlaygroundFrame'), { ssr: false });

export const getServerSideProps: GetServerSideProps = async (ctx) => {
  const id = ctx.params?.id as string;
  const supabase = createClient(
    process.env.NEXT_PUBLIC_SUPABASE_URL!,
    process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!
  );

  const { data, error } = await supabase
    .from('schema_links')
    .select('*')
    .eq('id', id)
    .eq('visibility', 'public')
    .single();

  if (error || !data) {
    return {
      notFound: true,
    };
  }

  return {
    props: {
      id,
    },
  };
};

export default function EmbedPage({ id }: { id: string }) {
  const url = `/admin/zod-playground?schema_id=${id}&embed=1`;
  return (
    <>
      <Head>
        <title>Embedded Schema</title>
        <meta name="robots" content="noindex" />
      </Head>
      <div className="min-h-screen">
        <iframe
          src={url}
          width="100%"
          height="1000"
          style={{ border: 'none' }}
          title={`Zod Playground - ${id}`}
        />
      </div>
    </>
  );
}

// pages/launch.tsx
'use client';

import { useEffect, useState } from 'react';
import { useSearchParams, useRouter } from 'next/navigation';
import { publishSite } from '@/admin/utils/publishSite';
import { logEvent } from '@/admin/utils/logEvent';
import dynamic from 'next/dynamic';

const SitePreview = dynamic(() => import('@/components/admin/SitePreview'), {
  ssr: false,
});

export default function LaunchPage() {
  const params = useSearchParams();
  const router = useRouter();
  const [status, setStatus] = useState<
    'idle' | 'confirm' | 'publishing' | 'done' | 'error'
  >('idle');
  const [siteParams, setSiteParams] = useState<Record<string, any> | null>(
    null
  );
  const [resultUrl, setResultUrl] = useState<string | null>(null);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const raw = params.get('params');
    if (!raw) return;
    try {
      const parsed = JSON.parse(decodeURIComponent(raw));
      setSiteParams(parsed);
      setStatus('confirm');
    } catch (err: any) {
      setError('Invalid parameters.');
      setStatus('error');
    }
  }, [params]);

  const handlePublish = async () => {
    if (!siteParams) return;
    setStatus('publishing');
    try {
      const url = await publishSite(siteParams as any);
      await logEvent('deploy_site', {
        params: siteParams,
        resultUrl: url,
      });
      setResultUrl(url);
      setStatus('done');
    } catch (err: any) {
      setError(err.message || 'Failed to publish');
      setStatus('error');
    }
  };

  return (
    <div className="p-10 max-w-2xl mx-auto text-center space-y-6">
      <h1 className="text-3xl font-bold">🚀 Launch QuickSite</h1>

      {status === 'confirm' && siteParams && (
        <div className="space-y-6">
          <p className="text-gray-700">
            Please confirm the following parameters:
          </p>
          <pre className="text-left text-sm bg-gray-100 p-4 rounded border overflow-auto max-h-96">
            {JSON.stringify(siteParams, null, 2)}
          </pre>

          <div className="border rounded bg-white p-4 shadow">
            <p className="text-left text-sm font-medium mb-2">Preview:</p>
            <div className="border rounded overflow-hidden">
              {/* <SitePreview params={siteParams} /> */}
              <iframe
                src={`/admin/preview?params=${encodeURIComponent(JSON.stringify(siteParams))}`}
                className="w-full h-full"
              />
            </div>
          </div>

          <button
            onClick={handlePublish}
            className="bg-purple-600 text-white px-4 py-2 rounded text-sm"
          >
            ✅ Confirm and Deploy
          </button>
        </div>
      )}

      {status === 'publishing' && <p>Publishing your site, please wait...</p>}

      {status === 'done' && resultUrl && (
        <div className="space-y-4">
          <p className="text-green-600 font-semibold">
            Site published successfully!
          </p>
          <a
            href={resultUrl}
            target="_blank"
            rel="noopener noreferrer"
            className="underline text-blue-600 hover:text-blue-800"
          >
            View your live site
          </a>
        </div>
      )}

      {status === 'error' && <p className="text-red-600">Error: {error}</p>}
    </div>
  );
}

import { useEffect, useState, useRef } from 'react';
import { supabase } from '@/lib/supabaseClient.js';
import Papa from 'papaparse';
import type { CSVLeadRow, Lead } from '@/types/lead.types';
import { createLeadFromPhoto } from '@/lib/leads/photoProcessor';

const CONFIDENCE_THRESHOLD = 0.85;
const LEADS_PER_PAGE = 20;

export default function LeadsPage() {
  const [leads, setLeads] = useState<Lead[]>([]);
  const [summary, setSummary] = useState({
    total: 0,
    matchedDomains: 0,
    matchedCampaigns: 0,
    duplicates: 0,
  });
  const [nextAction, setNextAction] = useState(false);
  const [error, setError] = useState('');
  const [sortField, setSortField] = useState<'created_at' | 'address_city'>(
    'created_at'
  );

  const fileInputRef = useRef<HTMLInputElement>(null);
  const dropRef = useRef<HTMLDivElement>(null);
  const loaderRef = useRef<HTMLDivElement>(null);

  const [previewUrl, setPreviewUrl] = useState<string | null>(null);
  const [filterSource, setFilterSource] = useState<string>('all');
  const [filterStatus, setFilterStatus] = useState<string>('all');
  const [reviewLead, setReviewLead] = useState<any | null>(null);
  const [reviewImage, setReviewImage] = useState<string | null>(null);
  const [editingLeadId, setEditingLeadId] = useState<number | null>(null);
  const [editingLead, setEditingLead] = useState<any | null>(null);
  const [page, setPage] = useState(0);
  const [loading, setLoading] = useState(false);
  const [selectedIds, setSelectedIds] = useState<number[]>([]);

  useEffect(() => {
    fetchLeads(true);
  }, [filterSource, filterStatus]);

  useEffect(() => {
    const observer = new IntersectionObserver(([entry]) => {
      if (entry.isIntersecting && !loading) {
        fetchLeads();
      }
    });
    if (loaderRef.current) observer.observe(loaderRef.current);
    return () => observer.disconnect();
  }, [loaderRef.current, loading]);

  const fetchLeads = async (reset = false) => {
    setLoading(true);
    let query = supabase
      .from('leads')
      .select('*')
      .order(sortField, { ascending: sortField === 'address_city' });
    if (filterSource !== 'all') query = query.eq('source', filterSource);
    if (filterStatus !== 'all') query = query.eq('status', filterStatus);

    const from = reset ? 0 : page * LEADS_PER_PAGE;
    const to = from + LEADS_PER_PAGE - 1;
    const { data } = await query.range(from, to);

    setLeads((prev) => (reset ? data || [] : [...prev, ...(data || [])]));
    setPage((prev) => (reset ? 1 : prev + 1));
    setLoading(false);
  };

  const toggleSelectLead = (id: number) => {
    setSelectedIds((prev) =>
      prev.includes(id) ? prev.filter((x) => x !== id) : [...prev, id]
    );
  };

  const bulkUpdateStatus = async (status: string) => {
    if (selectedIds.length === 0) return;
    await supabase.from('leads').update({ status }).in('id', selectedIds);
    setSelectedIds([]);
    fetchLeads(true);
  };

  const sendLeadEmail = async (lead: any) => {
    const res = await fetch('/api/send-lead-email', {
      method: 'POST',
      body: JSON.stringify({
        to: 'your-team@example.com',
        subject: `New Lead: ${lead.phone || 'No phone'}`,
        text: `Lead Details:\n\n${JSON.stringify(lead, null, 2)}`,
      }),
      headers: { 'Content-Type': 'application/json' },
    });
    alert(res.ok ? 'Email sent!' : 'Failed to send');
  };

  useEffect(() => {
    fetchLeads();
  }, [filterSource, filterStatus]);

  const markAsReviewed = async (id: number) => {
    await supabase.from('leads').update({ status: 'reviewed' }).eq('id', id);
    fetchLeads();
  };

  const updateLead = async () => {
    if (!editingLeadId || !editingLead) return;
    await supabase.from('leads').update(editingLead).eq('id', editingLeadId);
    setEditingLeadId(null);
    setEditingLead(null);
    fetchLeads();
  };

  const processFile = async (file: File) => {
    const preview = URL.createObjectURL(file);
    setReviewImage(preview);

    const photoPath = `leads/photos/${Date.now()}-${file.name}`;
    const { data: uploadData, error: uploadError } = await supabase.storage
      .from('leads')
      .upload(photoPath, file);

    if (uploadError) {
      console.error('Upload error:', uploadError);
      alert('Failed to upload photo');
      return;
    }

    const photoUrl = supabase.storage.from('leads').getPublicUrl(photoPath)
      .data.publicUrl;

    const { leadData, confidence }: { leadData: any; confidence: number } =
      await createLeadFromPhoto(file);
    leadData.id = crypto.randomUUID();
    leadData.confidence = confidence || 0;
    leadData.photo_url = photoUrl || null;
    leadData.status = 'reviewed';
    leadData.address_street = null;
    leadData.address_zip = null;
    leadData.address_country = null;

    if (confidence >= CONFIDENCE_THRESHOLD) {
      await supabase.from('leads').insert([leadData]);
      fetchLeads();
    } else {
      setReviewLead({ ...leadData, confidence });
    }
  };

  const handlePhotoUpload = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = e.target.files;
    if (!files || files.length === 0) return;
    await processFile(files[0]);
  };

  const handleDrop = async (e: React.DragEvent) => {
    e.preventDefault();
    if (e.dataTransfer.files.length > 0) {
      await processFile(e.dataTransfer.files[0]);
    }
  };

  const saveReviewedLead = async () => {
    if (!reviewLead) return;
    const reviewedCopy = { ...reviewLead };
    if (!reviewedCopy.status) reviewedCopy.status = 'needs_review';
    await supabase.from('leads').insert([reviewedCopy]);
    setReviewLead(null);
    setReviewImage(null);
    fetchLeads();
  };

  const exportCsv = () => {
    const headers = ['phone', 'industry', 'city', 'state', 'source', 'status'];
    const csv = [headers.join(',')];
    for (const lead of leads) {
      const row = headers
        .map((h) => JSON.stringify(lead[h as keyof Lead] ?? ''))
        .join(',');
      csv.push(row);
    }
    const blob = new Blob([csv.join('\n')], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'leads.csv';
    a.click();
  };

  const load = async () => {
    const { data, error } = await supabase
      .from('leads')
      .select('*')
      .order('created_at', { ascending: false });
    if (error) setError(error.message);
    else setLeads(data || []);
  };

  useEffect(() => {
    load();
  }, []);

  const handleCSV = async (e: any) => {
    const file = e.target.files?.[0];
    if (!file) return;

    const { data: domains } = await supabase.from('domains').select('*');
    const { data: campaigns } = await supabase.from('campaigns').select('*');
    const { data: existingLeads } = await supabase.from('leads').select('*');

    let duplicates = 0;
    let matchedDomains = 0;
    let matchedCampaigns = 0;

    Papa.parse<CSVLeadRow>(file, {
      header: true,
      skipEmptyLines: true,
      complete: async (results) => {
        const rows: Lead[] = [];

        for (const row of results.data) {
          const tags = row.Tags || '';
          const parts = tags.split(',').map((s) => s.trim());

          let city = row.City || '';
          let state = row.State || '';
          let industry = row.Industry || '';

          for (const part of parts) {
            const lower = part.toLowerCase();
            if (lower.includes('towing') || lower.includes('concrete')) {
              const words = part.split(' ');
              industry = words[0]?.toLowerCase() || '';
              city = words.slice(1).join(' ');
            }
          }

          if (parts.length >= 2) {
            state = parts[parts.length - 1];
            if (!city) city = parts[parts.length - 2];
          }

          const matchedDomain =
            domains?.find(
              (d) =>
                d.domain?.toLowerCase().includes(city?.toLowerCase()) ||
                d.city?.toLowerCase() === city?.toLowerCase()
            ) || null;
          const matchedCampaign =
            campaigns?.find(
              (c) =>
                c.city?.toLowerCase() === city?.toLowerCase() &&
                c.status !== 'ended'
            ) || null;

          const isDuplicate = existingLeads?.some(
            (l) =>
              l.business_name === row.BusinessName?.trim() &&
              (l.phone === row.Phone?.trim() || l.address_city === city)
          );

          if (isDuplicate) {
            duplicates++;
            continue;
          }

          if (matchedDomain) matchedDomains++;
          if (matchedCampaign) matchedCampaigns++;

          const phoneMissing = !row.Phone;
          const rawName = row.BusinessName?.trim() || '';
          const cleanName = rawName.replace(/&amp;/g, '&');

          rows.push({
            id: crypto.randomUUID(),
            business_name: cleanName,
            contact_name: row.ContactName || '',
            phone: phoneMissing ? '' : row.Phone?.trim() || '',
            email: row.Email || '',
            notes: phoneMissing
              ? 'Needs contact info – phone missing'
              : `Imported on ${new Date().toISOString()}`,
            address_city: city,
            address_state: state,
            industry,
            domain_id: matchedDomain?.id || null,
            campaign_id: matchedCampaign?.id || null,
            outreach_status: phoneMissing ? 'research_needed' : 'not_contacted',
            date_created: new Date().toISOString(),
            created_at: new Date().toISOString(),
            owner_id: null,
            photo_url: null,
            confidence: null,
            status: null,
            address_street: null,
            address_zip: null,
            address_country: null,
            address_lat: null,
            address_lon: null,
            address_full: null,
          });
        }

        const { error } = await supabase.from('leads').insert(rows);
        if (error) {
          console.error('❌ Insert error:', error);
          setError(`Upload failed: ${error.message}`);
          return;
        }

        setSummary({
          total: rows.length + duplicates,
          matchedDomains,
          matchedCampaigns,
          duplicates,
        });
        setNextAction(true);
        load();
      },
    });
  };

  return (
    <div className="p-6 text-white">
      <h1 className="text-2xl font-bold mb-4">Lead Tracker</h1>
