import { useEffect, useState } from 'react';
import { useRouter } from 'next/router';
import { createClient } from '@supabase/supabase-js';
import TemplateEditor from '@/components/admin/templates/TemplateEditor';

const supabase = createClient(
  process.env.NEXT_PUBLIC_SUPABASE_URL!,
  process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!
);

export default function NewTemplatePage() {
  const router = useRouter();
  const { from } = router.query;
  const [initialData, setInitialData] = useState<any | null>(null);

  useEffect(() => {
    const loadSnapshot = async () => {
      if (!from || typeof from !== 'string') return;

      const { data: snapshot, error } = await supabase
        .from('snapshots')
        .select('data, theme, brand, color_scheme')
        .eq('id', from)
        .single();

      if (error) {
        console.warn('Could not load snapshot:', error.message);
        return;
      }

      // Optional: record remix
      const user = await supabase.auth.getUser();
      const userId = user?.data?.user?.id;

      if (userId) {
        await supabase.from('remix_events').insert([
          {
            original_snapshot_id: from,
            user_id: userId,
          },
        ]);
      }

      setInitialData({
        template_name: 'Untitled (Remix)',
        layout: 'default',
        color_scheme: snapshot.color_scheme,
        theme: snapshot.theme,
        brand: snapshot.brand,
        data: snapshot.data,
      });
    };

    loadSnapshot();
  }, [from]);

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-4">New Template</h1>
      <TemplateEditor templateName="new-template" />
    </div>
  );
}

'use client';
import { useEffect, useState } from 'react';
import { json } from '@/lib/api/json';
import { AdminAnalyticsToolbar } from '@/components/admin/analytics/AdminAnalyticsToolbar';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
  CartesianGrid,
} from 'recharts';
import { useDateRange } from '@/hooks/useDateRange';

type SignalDataPoint = {
  date: string;
  views: number;
  feedback: number;
};

export default function AnalyticsChartView() {
  const [view, setView] = useState<'daily' | 'weekly' | 'monthly'>('daily');
  const [stats, setStats] = useState<{ label: string; value: number }[]>([]);
  const [data, setData] = useState<SignalDataPoint[]>([]);
  const { start, end } = useDateRange();

  const fetchData = async () => {
    if (!start || !end) return;

    const res = await fetch(`/api/analytics/signals?start=${start}&end=${end}`);
    const rows: SignalDataPoint[] = await json();
    setData(rows);

    const totals = rows.reduce(
      (acc, row) => {
        acc.views += row.views;
        acc.feedback += row.feedback;
        return acc;
      },
      { views: 0, feedback: 0 }
    );

    setStats([
      { label: 'Views', value: totals.views },
      { label: 'Feedback', value: totals.feedback },
    ]);
  };

  const downloadCSV = () => {
    const rows = [
      'Date,Views,Feedback',
      ...data.map((r) => `${r.date},${r.views},${r.feedback}`),
    ];
    const blob = new Blob([rows.join('\n')], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'signals.csv';
    a.click();
    window.URL.revokeObjectURL(url);
  };

  useEffect(() => {
    fetchData();
  }, [view, start, end]);

  return (
    <div className="p-6 max-w-6xl mx-auto text-white">
      <AdminAnalyticsToolbar
        title="Odessa Daily Signals"
        view={view}
        setView={setView}
        onRefresh={fetchData}
        stats={stats}
      >
        <button
          onClick={downloadCSV}
          className="px-3 py-1 bg-blue-700 hover:bg-blue-600 rounded text-white"
        >
          ⬇️ Export CSV
        </button>
      </AdminAnalyticsToolbar>

      <div className="bg-zinc-800 rounded p-6 mt-6">
        <ResponsiveContainer width="100%" height={300}>
          <LineChart
            data={data}
            onClick={(e) => {
              const clicked = e?.activeLabel;
              if (clicked) alert(`Drilldown for ${clicked}`);
            }}
          >
            <CartesianGrid strokeDasharray="3 3" stroke="#444" />
            <XAxis dataKey="date" stroke="#999" />
            <YAxis stroke="#999" />
            <Tooltip
              contentStyle={{ backgroundColor: '#222', borderColor: '#555' }}
              labelStyle={{ color: '#fff' }}
              formatter={(val, key) => [
                val,
                key === 'views' ? 'Views' : 'Feedback',
              ]}
            />
            <Line
              type="monotone"
              dataKey="views"
              stroke="#8884d8"
              strokeWidth={2}
            />
            <Line
              type="monotone"
              dataKey="feedback"
              stroke="#82ca9d"
              strokeWidth={2}
            />
          </LineChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
}

