import { createClient } from '@supabase/supabase-js';
import { json } from '@/lib/api/json';
import { NextApiRequest, NextApiResponse } from 'next';

const supabase = createClient(
  process.env.NEXT_PUBLIC_SUPABASE_URL!,
  process.env.SUPABASE_SERVICE_ROLE_KEY!
);

export default async function handler(
  req: NextApiRequest,
  res: NextApiResponse
) {
  const { id, name, description, template_id, preview, data } = JSON.parse(
    req.body
  );

  const { error } = await supabase.from('starter_templates').upsert({
    id,
    name,
    description,
    template_id,
    preview,
    data: JSON.parse(data),
  });

  if (error) {
    return json({ error: error.message });
  }

  return json({ success: true });
}

import { writeFile } from 'fs/promises';
import { json } from '@/lib/api/json';
import { NextApiRequest, NextApiResponse } from 'next';
import { join } from 'path';

export const config = {
  api: {
    bodyParser: false,
  },
};

export default async function handler(
  req: NextApiRequest,
  res: NextApiResponse
) {
  const buffers = [];
  for await (const chunk of req) buffers.push(chunk);
  const boundary = Buffer.concat(buffers);
  const match = /filename="(.+?)"/.exec(boundary.toString());
  const filename = match?.[1] || 'upload.png';
  const path = join(process.cwd(), 'public', filename);

  await writeFile(path, boundary);

  return json({ success: true });
}

