);

export default async function handler(
  req: NextApiRequest,
  res: NextApiResponse
) {
  const { user_id } = req.query;
  if (!user_id) return json({ error: 'Missing user_id' });

  const [feedback, checkins] = await Promise.all([
    supabase
      .from('block_feedback')
      .select('block_id, action, created_at, message')
      .in(
        'block_id',
        (await supabase.from('blocks').select('id').eq('owner_id', user_id))
          .data || []
      )
      .order('created_at', { ascending: false })
      .limit(100),

    supabase
      .from('tracking_checkins')
      .select('slug, created_at')
      .eq('user_id', user_id)
      .order('created_at', { ascending: false })
      .limit(100),
  ]);

  if (feedback.error) return json({ error: feedback.error });
  if (checkins.error) return json({ error: checkins.error });

  const summary = {
    received_feedback: feedback.data,
    checkin_history: checkins.data,
  };

  json(summary);
}

// pages/api/geocode.ts
import { supabase } from '@/lib/supabaseClient.js';
import { json } from '@/lib/api/json';
import type { NextApiRequest, NextApiResponse } from 'next';

export default async function handler(
  req: NextApiRequest,
  res: NextApiResponse
) {
  const { city, state } = req.query;

  if (
    !city ||
    !state ||
    typeof city !== 'string' ||
    typeof state !== 'string'
  ) {
    return json({ error: 'Missing city or state' });
  }

  const cleanCity = city.trim();
  const cleanState = state.trim();

  // 1. Try to fetch from Supabase cache
  const { data: cached, error: fetchError } = await supabase
    .from('geo_cache')
    .select('lat, lon')
    .eq('city', cleanCity)
    .eq('state', cleanState)
    .maybeSingle();

  if (cached) {
    console.log(`🌍 Cache hit for ${cleanCity}, ${cleanState}`);
    return json({ lat: Number(cached.lat), lon: Number(cached.lon) });
  }

  // 2. Fallback to Nominatim
  const query = `${cleanCity}, ${cleanState}, USA`;
  const url = `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(query)}`;

  try {
    const geoRes = await fetch(url, {
      headers: {
        'User-Agent': 'QuickSites/1.0 (admin@quicksites.ai)', // customize contact per Nominatim policy
      },
    });

    const results = await geoRes.json();

    if (!Array.isArray(results) || results.length === 0) {
      return json({ lat: 0, lon: 0, error: 'Not found' });
    }

    const { lat, lon } = results[0];
    const parsedLat = parseFloat(lat);
    const parsedLon = parseFloat(lon);

    // 3. Insert into Supabase cache (will auto-generate UUID)
    await supabase.from('geo_cache').insert([
      {
        city: cleanCity,
        state: cleanState,
        lat: parsedLat,
        lon: parsedLon,
      },
    ]);

    console.log(
      `🌍 Cached ${cleanCity}, ${cleanState} -> ${parsedLat}, ${parsedLon}`
    );
    return json({ lat: parsedLat, lon: parsedLon });
  } catch (err) {
    console.error('🌍 Geocode error:', err);
    return json({ lat: 0, lon: 0, error: 'Internal error' });
  }
}

