  const [edges, setEdges] = useState([]);

  useEffect(() => {
    fetch('/api/remix-dag')
      .then((res) => json())
      .then(({ nodes, edges }) => {
        setNodes(nodes);
        setEdges(edges);
      });
  }, []);

  return (
    <div className="h-screen bg-zinc-900 text-white">
      <h1 className="text-2xl font-bold p-4">🌐 Remix DAG</h1>
      <div className="h-[90%]">
        <ReactFlow nodes={nodes} edges={edges} fitView>
          <Background />
          <Controls />
        </ReactFlow>
      </div>
    </div>
  );
}

'use client';
import { useEffect, useState } from 'react';
import { json } from '@/lib/api/json';
import { useUser, useSession } from '@supabase/auth-helpers-react';

export default function ShareSettings() {
  const user = useUser();
  const session = useSession();
  const accessToken = session?.access_token;

  const [profile, setProfile] = useState<any>(null);
  const [bio, setBio] = useState('');
  const [emoji, setEmoji] = useState('🌱');
  const [tags, setTags] = useState('');
  const [visible, setVisible] = useState(false);
  const [status, setStatus] = useState<'idle' | 'saving' | 'done' | 'error'>(
    'idle'
  );

  useEffect(() => {
    if (!user) return;
    fetch('/api/public-profile?handle=' + user.user_metadata.handle)
      .then((res) => json())
      .then((data) => {
        setProfile(data);
        setBio(data.bio || '');
        setEmoji(data.emoji || '🌱');
        setTags((data.goal_tags || []).join(', '));
        setVisible(data.visible);
      });
  }, [user]);

  const handleSave = async () => {
    if (!accessToken || !user) return;
    setStatus('saving');
    const res = await fetch('/api/update-profile', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${accessToken}`,
      },
      body: JSON.stringify({
        bio,
        emoji,
        visible,
        goal_tags: tags.split(',').map((t) => t.trim()),
        handle: user.user_metadata.handle,
      }),
    });
    setStatus(res.ok ? 'done' : 'error');
  };

  return (
    <div className="p-6 text-white max-w-xl mx-auto space-y-4">
      <h1 className="text-2xl font-bold">📡 Share My World</h1>
      <p className="text-zinc-400 text-sm">
        This page controls what others can see at /world/[handle]/share
      </p>

      <div>
        <label className="block text-sm mb-1">Emoji</label>
        <input
          value={emoji}
          onChange={(e) => setEmoji(e.target.value)}
          className="text-black w-full p-1 rounded"
        />
      </div>

      <div>
        <label className="block text-sm mb-1">Short Bio</label>
        <textarea
          value={bio}
          onChange={(e) => setBio(e.target.value)}
          rows={2}
          className="text-black w-full p-1 rounded"
        />
      </div>

      <div>
        <label className="block text-sm mb-1">
          Goal Tags (comma separated)
        </label>
        <input
          value={tags}
          onChange={(e) => setTags(e.target.value)}
          className="text-black w-full p-1 rounded"
        />
      </div>

      <div className="flex items-center space-x-2">
        <input
          type="checkbox"
          checked={visible}
          onChange={() => setVisible(!visible)}
        />
        <label>Make my page public</label>
      </div>

      <button
        onClick={handleSave}
        className="px-4 py-2 bg-blue-600 hover:bg-blue-700 rounded"
      >
        {status === 'saving' ? 'Saving...' : 'Save Changes'}
      </button>
      {status === 'done' && (
        <p className="text-green-400 text-sm">✅ Updated</p>
      )}
      {status === 'error' && (
        <p className="text-red-500 text-sm">⚠️ Something went wrong</p>
      )}
    </div>
  );
}

'use client';
import { useEffect, useState } from 'react';
import { json } from '@/lib/api/json';
import { useRouter } from 'next/navigation';
import { useUser, useSession } from '@supabase/auth-helpers-react';

export default function StartPage() {
  const user = useUser();
  const session = useSession();
  const router = useRouter();

  const [templates, setTemplates] = useState([]);
  const [loc, setLoc] = useState<GeolocationCoordinates | null>(null);
  const [selected, setSelected] = useState('');
  const [status, setStatus] = useState<'idle' | 'saving' | 'done' | 'error'>(
    'idle'
  );

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (pos) => setLoc(pos.coords),
      () => setLoc(null)
    );
    fetch('/api/habit-templates')
      .then((res) => json())
      .then(setTemplates);
  }, []);

  const handleAdd = async () => {
    if (!selected || !user || !session || !loc) return;
    setStatus('saving');

    const accessToken = session.access_token;

    const res = await fetch('/api/create-block', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${accessToken}`,
      },
      body: JSON.stringify({
        lat: loc.latitude,
        lon: loc.longitude,
        title: selected.charAt(0).toUpperCase() + selected.slice(1),
        message: `Tracking: ${selected}`,
        emoji: selected === 'floss' ? '🦷' : selected === 'water' ? '💧' : '🧘',
        slug: selected,
        type: 'tracking',
        actions: [{ label: 'Check In', type: 'check-in', target: selected }],
      }),
    });

    if (res.ok) {
      setStatus('done');
      router.push('/world/me');
    } else {
      setStatus('error');
    }
  };

  return (
    <div className="text-white p-6 max-w-xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">
        👋 Welcome to Your AR Habit Tracker
      </h1>
      <p className="text-zinc-400 mb-4">
        Choose a habit to start tracking, and we'll place it in your world:
      </p>
      <ul className="space-y-3">
        {templates.map((t: any) => (
          <li key={t.slug}>
            <label className="flex items-center space-x-3 cursor-pointer">
              <input
                type="radio"
                name="habit"
                value={t.slug}
                checked={selected === t.slug}
                onChange={() => setSelected(t.slug)}
                className="form-radio"
              />
              <span>
                {t.emoji} {t.title}
              </span>
            </label>
          </li>
        ))}
      </ul>
      <button
        onClick={handleAdd}
        disabled={!selected || !loc || status === 'saving'}
        className="mt-6 px-4 py-2 rounded bg-blue-600 hover:bg-blue-700 disabled:opacity-50"
      >
        {status === 'saving' ? 'Creating...' : '➕ Add to My World'}
      </button>
      {status === 'error' && (
        <p className="text-red-500 mt-4">
          Something went wrong. Please try again.
        </p>
      )}
    </div>
  );
}

'use client';

import { useRouter } from 'next/router';
import { json } from '@/lib/api/json';
import { useEffect, useState } from 'react';
import { CelebrationModal } from '@/components/embed/CelebrationModal';

export default function SupportCampaign() {
  const { query } = useRouter();
  const [campaign, setCampaign] = useState<any>(null);
  const [showBadgeCelebration, setShowBadgeCelebration] = useState(false);

  useEffect(() => {
    if (!query.slug) return;
    fetch('/api/campaign?slug=' + query.slug)
      .then((res) => json())
      .then(setCampaign);
  }, [query.slug]);

  useEffect(() => {
    if (query.badge === 'done') {
      setShowBadgeCelebration(true);
    }
  }, [query.badge]);

  if (!campaign) return <div className="p-6 text-white">Loading...</div>;

  const pct = Math.min(
    100,
    Math.round((campaign.count / campaign.goal_count) * 100)
  );

  return (
    <div className="max-w-xl mx-auto p-6 text-white space-y-6">
      <h1 className="text-2xl font-bold">{campaign.headline}</h1>
      <p className="text-zinc-400 text-sm">
        Goal: {campaign.goal_count} {campaign.target_action}s
      </p>
      <div className="bg-zinc-700 h-4 rounded overflow-hidden">
        <div className="bg-green-500 h-full" style={{ width: pct + '%' }}></div>
      </div>
      <p className="text-sm text-green-300">
        {campaign.count} {campaign.target_action}s received
      </p>

      {showBadgeCelebration && (
        <CelebrationModal
          type="badge"
          slug={query.slug as string}
          onClose={() => setShowBadgeCelebration(false)}
        />
      )}
    </div>
  );
}

'use client';
import { useEffect, useState } from 'react';
import { json } from '@/lib/api/json';
import { useRouter } from 'next/router';

export default function EditCampaign() {
  const { query } = useRouter();
  const [data, setData] = useState<any>(null);
  const [message, setMessage] = useState('');

  useEffect(() => {
    if (!query.slug) return;
    fetch('/api/campaign?slug=' + query.slug)
      .then((res) => json())
      .then(setData);
  }, [query.slug]);

  const submit = async () => {
    const res = await fetch('/api/update-campaign', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(data),
    });
    const json = await json();
    setMessage(json.error ? 'Error: ' + json.error : '✅ Updated!');
  };

  if (!data) return <div className="p-6 text-white">Loading...</div>;

  return (
    <div className="max-w-xl mx-auto p-6 text-white space-y-4">
      <h1 className="text-2xl font-bold">✏️ Edit Campaign</h1>
      <input
        value={data.headline}
        onChange={(e) => setData({ ...data, headline: e.target.value })}
        className="w-full p-2 text-black rounded"
      />
      <input
        type="number"
        value={data.goal_count}
        onChange={(e) =>
          setData({ ...data, goal_count: parseInt(e.target.value) })
        }
        className="w-full p-2 text-black rounded"
      />
      <select
        value={data.target_action}
        onChange={(e) => setData({ ...data, target_action: e.target.value })}
        className="w-full p-2 text-black rounded"
      >
        <option value="cheer">🎉 Cheer</option>
        <option value="echo">🔁 Echo</option>
        <option value="reflect">🪞 Reflect</option>
        <option value="checkin">✅ Check-in</option>
      </select>
      <input
        value={data.block_id}
        onChange={(e) => setData({ ...data, block_id: e.target.value })}
        className="w-full p-2 text-black rounded"
      />
      <button
        onClick={submit}
        className="bg-green-600 hover:bg-green-700 px-4 py-2 rounded"
      >
        Save
      </button>
      {message && <p className="text-green-300 text-sm">{message}</p>}
    </div>
  );
}

'use client';
import { useState } from 'react';
import { json } from '@/lib/api/json';

export default function PublicCampaignCreate() {
  const [slug, setSlug] = useState('');
  const [headline, setHeadline] = useState('');
  const [goal, setGoal] = useState(10);
  const [action, setAction] = useState('cheer');
  const [blockId, setBlockId] = useState('');
  const [submitted, setSubmitted] = useState('');

  const submit = async () => {
    const res = await fetch('/api/create-campaign', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        slug,
        headline,
        goal_count: goal,
        target_action: action,
        blockId,
      }),
    });

    const json = await json();
    setSubmitted(json.error ? 'Error: ' + json.error : '✅ Campaign created!');
  };

  return (
    <div className="max-w-xl mx-auto p-6 text-white space-y-4">
      <h1 className="text-2xl font-bold">📣 Launch a Campaign</h1>
      <p className="text-sm text-zinc-400">
        Create a public support goal around a habit or mission.
      </p>
      <input
        value={slug}
        onChange={(e) => setSlug(e.target.value)}
        placeholder="Slug (e.g. floss)"
        className="p-2 text-black w-full rounded"
      />
      <input
        value={headline}
        onChange={(e) => setHeadline(e.target.value)}
        placeholder="Headline"
        className="p-2 text-black w-full rounded"
      />
      <input
        type="number"
        value={goal}
        onChange={(e) => setGoal(Number(e.target.value))}
        className="p-2 text-black w-full rounded"
      />
      <select
        value={action}
        onChange={(e) => setAction(e.target.value)}
        className="p-2 text-black w-full rounded"
      >
        <option value="cheer">🎉 Cheer</option>
        <option value="echo">🔁 Echo</option>
        <option value="reflect">🪞 Reflect</option>
        <option value="checkin">✅ Check-in</option>
      </select>
      <input
        value={blockId}
        onChange={(e) => setBlockId(e.target.value)}
        placeholder="Block ID (optional)"
        className="p-2 text-black w-full rounded"
      />
      <button
        onClick={submit}
        className="bg-green-600 hover:bg-green-700 px-4 py-2 rounded"
      >
        Create Campaign
      </button>
      {submitted && <p className="text-green-300 text-sm">{submitted}</p>}
    </div>
  );
}

