import fs from 'fs';
import path from 'path';

const PROJECT_ROOT = process.cwd();
const TARGET_EXT = ['.ts'];
const SEARCH_DIRS = ['app/api', 'app', 'pages', 'admin'];

const args = process.argv.slice(2);
const makeBackup = args.includes('--backup');

function walk(dir: string): string[] {
  if (!fs.existsSync(dir)) return [];
  const entries = fs.readdirSync(dir, { withFileTypes: true });
  return entries.flatMap((entry) => {
    const full = path.join(dir, entry.name);
    return entry.isDirectory() ? walk(full) : [full];
  });
}

function cleanFile(filePath: string) {
  let content = fs.readFileSync(filePath, 'utf-8');
  const original = content;
  let changed = false;

  const isMiddleware = filePath.endsWith('middleware.ts');
  const isRoute = filePath.endsWith('route.ts');

  // Remove next/server imports
  content = content.replace(
    /import\s+{[^}]*Next(Request|Response)[^}]*}\s+from\s+['"]next\/server['"];\s*/g,
    () => {
      changed = true;
      return '';
    }
  );

  // Fix req.json() to await req.json()
  content = content.replace(
    /([^a-zA-Z0-9_])req\.json\s*\(\s*\)/g,
    (match, prefix) => {
      if (match.includes('await')) return match;
      changed = true;
      return `${prefix}await req.json()`;
    }
  );

  // Fix route signatures (GET, POST, etc.)
  content = content.replace(
    /export\s+async\s+function\s+(GET|POST|PUT|PATCH|DELETE)\s*\(\s*req\s*\)/g,
    (_match, method) => {
      changed = true;
      return `export async function ${method}(req: Request): Promise<Response>`;
    }
  );

  // Fix middleware signature
  content = content.replace(
    /export\s+function\s+middleware\s*\(\s*req\s*\)/g,
    () => {
      changed = true;
      return `export function middleware(req: Request): Response`;
    }
  );

  // Fix NextResponse.json
  content = content.replace(/NextResponse\.json\s*\(/g, () => {
    changed = true;
    return 'json(';
  });

  // Fix NextResponse.next() → new Response()
  content = content.replace(/NextResponse\.next\s*\(\s*\)/g, () => {
    changed = true;
    return 'new Response()';
  });

  // Add runtime = edge if missing
  if (!/export\s+const\s+runtime\s*=/.test(content)) {
    content = `export const runtime = 'edge';\n\n` + content;
    changed = true;
  }

  // Add import { json } if needed
  if (changed && !content.includes("from '@/lib/api/json'")) {
    content = content.replace(
      /(^|\n)(import .+?;)+/s,
      `$&\nimport { json } from '@/lib/api/json';`
    );
  }

  if (changed && content !== original) {
    if (makeBackup) {
      fs.writeFileSync(filePath + '.bak', original, 'utf8');
    }
    fs.writeFileSync(filePath, content, 'utf8');
    console.log(`✅ Modernized: ${filePath}`);
  }
}

const allFiles = SEARCH_DIRS.flatMap((dir) =>
  walk(path.join(PROJECT_ROOT, dir)).filter(
    (file) =>
      TARGET_EXT.includes(path.extname(file)) &&
      (file.endsWith('route.ts') || file.endsWith('middleware.ts'))
  )
);

if (allFiles.length === 0) {
  console.log('⚠️  No matching files found.');
  process.exit(0);
}

console.log(`🔧 Modernizing ${allFiles.length} file(s)...\n`);
allFiles.forEach(cleanFile);
console.log('\n🏁 Done.');

import fs from 'fs';
import path from 'path';

const PROJECT_ROOT = process.cwd();
const TARGET_EXT = ['.ts', '.tsx'];
const SEARCH_DIRS = ['app', 'pages', 'admin', 'src'];

const args = process.argv.slice(2);
const isPreview = args.includes('--preview');
const isBackup = args.includes('--backup');

function walk(dir: string): string[] {
  const entries = fs.readdirSync(dir, { withFileTypes: true });
  return entries.flatMap((entry) => {
    const full = path.join(dir, entry.name);
    return entry.isDirectory() ? walk(full) : [full];
  });
}

function replaceInFile(filePath: string) {
  let content = fs.readFileSync(filePath, 'utf-8');
  const original = content;
  let changed = false;

  // Remove NextResponse import
  content = content.replace(
    /import\s+{[^}]*NextResponse[^}]*}\s+from\s+['"]next\/server['"];/g,
    () => {
      changed = true;
      return '';
    }
  );

  // Replace NextResponse.json(...) → json(...)
  content = content.replace(/NextResponse\.json\s*\(/g, () => {
    changed = true;
    return 'json(';
  });

  // Replace res.status(###).json(...) → json(..., ###)
  content = content.replace(
    /res\.status\s*\(\s*(\d{3})\s*\)\.json\s*\(/g,
    (_m, code) => {
      changed = true;
      return `json(`; // Could inline status later
    }
  );

  // Replace res.json(...) → json(...)
  content = content.replace(/res\.json\s*\(/g, () => {
    changed = true;
    return 'json(';
  });

  // Ensure req.json() → await req.json()
  content = content.replace(
    /([^a-zA-Z0-9_])req\.json\s*\(\s*\)/g,
    (match, prefix) => {
      if (match.includes('await')) return match;
      changed = true;
      return `${prefix}await req.json()`;
    }
  );

  // Add json import if needed
  if (changed && !content.includes("from '@/lib/api/json'")) {
    content = content.replace(
      /(^|\n)(import .+?;)+/s,
      `$&\nimport { json } from '@/lib/api/json';`
    );
  }

  if (changed && content !== original) {
    if (isPreview) {
      console.log(`🔍 [PREVIEW] Would update: ${filePath}`);
    } else {
      if (isBackup) {
        fs.writeFileSync(filePath + '.bak', original, 'utf-8');
      }
      fs.writeFileSync(filePath, content, 'utf-8');
      console.log(`✅ Updated: ${filePath}`);
    }
  }
}

let updatedCount = 0;
for (const dir of SEARCH_DIRS) {
  const fullDir = path.join(PROJECT_ROOT, dir);
  if (!fs.existsSync(fullDir)) continue;

  const files = walk(fullDir).filter((f) =>
    TARGET_EXT.includes(path.extname(f))
  );

  for (const file of files) {
    const before = fs.readFileSync(file, 'utf-8');
    replaceInFile(file);
    const after = fs.readFileSync(file, 'utf-8');
    if (before !== after) updatedCount++;
  }
}

console.log(
  `\n🏁 Done. ${isPreview ? 'Would update' : 'Updated'} ${updatedCount} file(s).`
);

import fs from 'fs';
import path from 'path';
import fetch from 'node-fetch';
import { createClient } from '@supabase/supabase-js';

const supabase = createClient(
  process.env.SUPABASE_URL!,
  process.env.SUPABASE_SERVICE_ROLE_KEY!
);

const logPath = './reports/activity.log';
const contents = fs.readFileSync(logPath, 'utf-8').trim().split('\n');
const lastLine = contents.at(-1) || '';

if (!lastLine.includes('Exported')) {
  console.log('No export event found, skipping webhook');
  process.exit(0);
}

const { data, error } = await supabase
  .from('report_webhooks')
  .select('*')
  .eq('enabled', true);

if (error) {
  console.error('Error fetching webhooks', error);
  process.exit(1);
}

for (const hook of data) {
  try {
    await fetch(hook.url, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        ...(hook.secret_token && {
          Authorization: `Bearer ${hook.secret_token}`,
        }),
      },
      body: JSON.stringify({
        event: 'export.success',
        summary: lastLine,
        timestamp: new Date().toISOString(),
      }),
    });
    console.log(`📤 Notified ${hook.url}`);
  } catch (e: any) {
    console.error(
      `❌ Failed to notify ${hook.url}:`,
      e instanceof Error ? e.message : String(e)
    );
  }
}

import 'dotenv/config';
import { execSync } from 'child_process';
import { createClient } from '@supabase/supabase-js';

const supabase = createClient(
  process.env.NEXT_PUBLIC_SUPABASE_URL!,
  process.env.SUPABASE_SERVICE_ROLE_KEY!
);

async function run() {
  const { data: job, error } = await supabase
    .from('regeneration_queue')
    .select('*')
    .eq('status', 'queued')
    .order('created_at', { ascending: true })
    .limit(1)
    .maybeSingle();

  if (!job) {
    console.log('✅ No queued jobs.');
    return;
  }

  console.log('🔄 Running job for:', job.domain);
  await supabase
    .from('regeneration_queue')
    .update({ status: 'processing', started_at: new Date().toISOString() })
    .eq('id', job.id);

  try {
    const cmd = `node ../init.js "${job.city}" "${job.state}" "${job.template_id}" --push-to-github`;
    const output = execSync(cmd, { encoding: 'utf-8' });

    await supabase
      .from('regeneration_queue')
      .update({
        status: 'done',
        finished_at: new Date().toISOString(),
        log: output,
      })
      .eq('id', job.id);

    await supabase.from('user_action_logs').insert([
      {
        domain_id: job.domain_id,
        action_type: 'regeneration_complete',
        triggered_by: job.triggered_by || 'queue_bot',
      },
    ]);

    console.log('✅ Job complete.');
  } catch (err: any) {
    console.error('❌ Job failed:', err.message);

    await supabase
      .from('regeneration_queue')
      .update({
        status: 'error',
        finished_at: new Date().toISOString(),
        log: err.message,
      })
      .eq('id', job.id);
  }
}

run();

