export default function ReferralsPage() {
  const [users, setUsers] = useState<any[]>([]);
  const [profiles, setProfiles] = useState<Record<string, any>>({});
  const [error, setError] = useState('');
  const { role } = useCanonicalRole();
  const router = useRouter();

  useEffect(() => {
    if (role === null) return;

    if (role !== 'admin') {
      router.push('/login?error=unauthorized');
      return;
    }

    const fetchData = async () => {
      if (DEBUG) console.log('[üë• Fetching Users + Profiles]');

      const { data: userData, error: userError } = await supabase.auth.admin.listUsers();
      if (userError) {
        setError(userError.message);
        return;
      }
      setUsers(userData?.users || []);

      const { data: profileData } = await supabase
        .from('user_profiles')
        .select('user_id, role, referrer_id');

      const profileMap: Record<string, any> = {};
      profileData?.forEach((p) => {
        profileMap[p.user_id] = { role: p.role, referrer_id: p.referrer_id };
      });
      setProfiles(profileMap);

      if (DEBUG) console.log('[‚úÖ Profiles Loaded]', profileMap);
    };

    fetchData();
  }, [role]);

  const referrers = users.filter((u) => profiles[u.id]?.role === 'affiliate_referrer');

  return (
