import { createClient } from '@supabase/supabase-js';
import { json } from '@/lib/api/json';
import { NextApiRequest, NextApiResponse } from 'next';

const supabase = createClient(
  process.env.NEXT_PUBLIC_SUPABASE_URL!,
  process.env.SUPABASE_SERVICE_ROLE_KEY!
);

function groupByDate(data: any[], field: string) {
  const groups: Record<string, number> = {};
  for (const row of data) {
    const date = row[field].slice(0, 10); // YYYY-MM-DD
    groups[date] = (groups[date] || 0) + 1;
  }
  return Object.entries(groups).map(([date, count]) => ({ date, count }));
}

export default async function handler(req: NextApiRequest, res: NextApiResponse) {
  const { start, end } = req.query;

  const viewRange = {
    from: start ? new Date(start as string).toISOString() : undefined,
    to: end ? new Date(end as string).toISOString() : undefined,
  };

  const [views, feedback] = await Promise.all([
    supabase
      .from('published_site_views')
      .select('viewed_at')
      .gte('viewed_at', viewRange.from || '')
      .lte('viewed_at', viewRange.to || ''),
    supabase
      .from('block_feedback')
      .select('created_at')
      .gte('created_at', viewRange.from || '')
      .lte('created_at', viewRange.to || ''),
  ]);

  if (views.error || feedback.error) {
    return json({ error: 'Error loading analytics' });
  }

  const byViews = groupByDate(views.data, 'viewed_at');
  const byFeedback = groupByDate(feedback.data, 'created_at');

  const merged: Record<string, any> = {};
  byViews.forEach(({ date, count }) => {
    merged[date] = { date, views: count, feedback: 0 };
  });
  byFeedback.forEach(({ date, count }) => {
    merged[date] = { ...(merged[date] || { date, views: 0 }), feedback: count };
  });

  const result = Object.values(merged).sort((a, b) => a.date.localeCompare(b.date));
  json(result);
}

import { createCanvas, registerFont } from 'canvas';
import QRCode from 'qrcode';
import crypto from 'node:crypto';
import path from 'node:path';
import fs from 'node:fs/promises';
import { mkdirSync } from 'node:fs';
import { NextRequest } from 'next/server';

export const runtime = 'nodejs';

// Load fonts if present
try {
  registerFont(path.resolve('./public/fonts/Inter.ttf'), { family: 'Inter' });
  registerFont(path.resolve('./public/fonts/Arial.ttf'), { family: 'Arial' });
} catch (err) {
  console.warn('‚ö†Ô∏è Font registration failed:', err);
}

function hash(input: string): string {
  return crypto.createHash('sha1').update(input).digest('hex');
}

export async function POST(req: NextRequest) {
  const {
    blockId,
    handle,
    label = `block-${blockId}`,
    size = 512,
    font = 'Inter',
    fontSize = 20,
    color = '#000000',
    background = '#ffffff',
    format = 'png',
  } = await req.json();

  if (!blockId || !handle) {
    return new Response(JSON.stringify({ error: 'Missing blockId or handle' }), {
      status: 400,
      headers: { 'Content-Type': 'application/json' },
    });
  }

  const url = `https://quicksites.ai/world/${handle}#block-${blockId}`;
  const qrCanvas = createCanvas(size, size);
  await QRCode.toCanvas(qrCanvas, url, {
    margin: 1,
    width: size,
    color: {
      dark: color,
      light: background,
    },
  });

  const canvas = createCanvas(size, size + fontSize + 20);
  const ctx = canvas.getContext('2d');

  ctx.fillStyle = background;
  ctx.fillRect(0, 0, canvas.width, canvas.height);
  ctx.drawImage(qrCanvas, 0, 0, size, size);
  ctx.fillStyle = color;
  ctx.font = `${fontSize}px "${font}"`;
  ctx.textAlign = 'center';
  ctx.fillText(label, canvas.width / 2, canvas.height - 10);

  const buffer =
    format === 'png'
      ? canvas.toBuffer()
      : (() => {
          throw new Error(`Unsupported format: ${format}`);
        })();

  // üîí Save to disk
  const dir = path.resolve('public', 'generated-qr', handle);
  mkdirSync(dir, { recursive: true });
  const filename = `${blockId}.${format}`;
  const filepath = path.join(dir, filename);
  await fs.writeFile(filepath, buffer);

  const publicUrl = `/generated-qr/${handle}/${filename}`;
  return new Response(JSON.stringify({ ok: true, path: publicUrl }), {
    headers: { 'Content-Type': 'application/json' },
  });
}

