  const { data: user } = await supabase.auth.getUser();

  const ip =
    headerStore.get('x-forwarded-for') ||
    headerStore.get('x-real-ip') ||
    'unknown';

  const userAgent = headerStore.get('user-agent') || 'unknown';

  const { data: inserted, error } = await supabase
    .from('nav_events')
