import { supabase } from '@/admin/lib/supabaseClient';
import { NextRequest } from 'next/server';

export async function GET(_req: NextRequest) {
  const baseUrl = 'https://quicksites.ai';

  const { data: domains } = await supabase
    .from('domains')
    .select('domain, lang')
    .eq('is_claimed', true);

  const langGroups = (domains || []).reduce<
    Record<string, Array<{ domain: string; lang: string }>>
  >((acc, d) => {
    const base = d.domain.replace(/\.[a-z]+$/, '');
    if (!acc[base]) acc[base] = [];
    acc[base].push({ ...d, lang: d.lang || 'en' });
    return acc;
  }, {});

  const xml = `<?xml version="1.0" encoding="UTF-8"?>
<urlset
  xmlns="http://www.sitemaps.org/schemas/sitemap/0.9"
  xmlns:xhtml="http://www.w3.org/1999/xhtml"
>
${Object.entries(langGroups)
  .map(([base, versions]) => {
    const defaultLang = versions.find((v) => v.lang === 'en') || versions[0];
    const loc = `${baseUrl}/${defaultLang.lang}/site/${defaultLang.domain}`;
    const links = versions
      .map(
        (v) =>
          `<xhtml:link rel="alternate" hreflang="${v.lang}" href="${baseUrl}/${v.lang}/site/${v.domain}" />`
      )
      .join('\n    ');
    return `<url>
  <loc>${loc}</loc>
  ${links}
</url>`;
  })
  .join('\n')}
</urlset>`;

  return new Response(xml, {
    headers: {
      'Content-Type': 'application/xml',
    },
  });
}

export const runtime = 'nodejs';

import { json } from '@/lib/api/json';
import { generateBaseSlug } from '@/lib/slugHelpers';
import { getSupabase } from '@/lib/supabase/server';
import type { SupabaseClient } from '@supabase/supabase-js';

function baseSlug(businessName: string, location?: string): string {
  const name = businessName.toLowerCase().replace(/[^a-z0-9]+/g, '-');
  const loc =
    location
      ?.toLowerCase()
      .split(',')[0]
      .replace(/[^a-z0-9]+/g, '-') || '';
  const raw = `${name}-${loc}`.replace(/^-+|-+$/g, '');
  return raw.replace(/--+/g, '-');
}

export async function POST(req: Request) {
  const body = await req.json();
  const { template_version_id, business_name, location, domain, slug: clientSlug, email } = body;

  const supabase = await getSupabase();

  const base = generateBaseSlug(business_name, location);
  const slug = clientSlug || (await generateUniqueSlug(base, supabase));

  const {
    data: { user },
  } = await supabase.auth.getUser();

  if (!user) {
    return json({ error: 'Unauthorized' }, { status: 401 });
  }

  const recent = await supabase
    .from('sites')
    .select('created_at')
    .eq('created_by', user.id)
    .order('created_at', { ascending: false })
    .limit(1)
    .maybeSingle();

  if (recent?.data && Date.now() - new Date(recent.data.created_at).getTime() < 10 * 60 * 1000) {
    return json({ error: 'Too soon ‚Äî please wait 10 minutes before creating another site.' });
  }

  const { data: versions, error: fetchError } = await supabase
    .from('template_versions')
    .select('*')
    .eq('id', template_version_id)
    .limit(1);

  if (fetchError || !versions?.length) {
    return json({ error: 'Template not found' });
  }

  const full_data = versions[0].full_data;
  if (!full_data) {
    return json({ error: 'Template data missing' });
  }

  try {
    const { data: newSite, error: insertError } = await supabase
      .from('sites')
      .insert([
        {
          slug,
          domain,
          business_name,
          location,
          template_version_id,
          content: full_data,
          created_by: user.id,
          claimed_email: email || null,
        },
      ])
      .select()
      .single();

    if (insertError) {
      throw insertError;
    }

    if (email) {
      try {
        const response = await fetch(
          `${process.env.NEXT_PUBLIC_BASE_URL}/api/resend-welcome-email`,
          {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
              email,
              name: business_name,
              slug: newSite.slug,
              templateName: versions[0].template_name || 'a QuickSites template',
            }),
          }
        );

        if (!response.ok) {
          console.warn('‚ö†Ô∏è Failed to send welcome email:', await response.text());
        }
      } catch (err) {
        console.warn('‚ö†Ô∏è Error sending welcome email:', err);
      }
    }

    return json(newSite);
  } catch (insertError: any) {
    console.error('Insert failed:', insertError.message);

    await supabase.from('deploy_errors').insert({
      context: 'site_create',
      payload: {
        email,
        domain,
        business_name,
        location,
        template_version_id,
        error: insertError.message,
        user_id: user.id,
      },
    });

    return json({ error: 'Failed to create site' }, { status: 500 });
  }
}

