// Helper updated to take Supabase client as a parameter
async function generateUniqueSlug(
  base: string,
  supabase: SupabaseClient
): Promise<string> {
  let slug = base;
  let attempt = 1;
  while (true) {
    const { data } = await supabase
      .from('sites')
      .select('id')
      .eq('slug', slug)
      .maybeSingle();
    if (!data) break;
    attempt += 1;
    slug = `${base}-${attempt}`;
  }
  return slug;
}

