'use client';

import { useEffect, useRef, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { LogOut, UserCircle } from 'lucide-react';
import md5 from 'blueimp-md5';
import { RoleBadge } from './role-badge';

export function AvatarMenu({
  email,
  avatarUrl,
  role,
  source,
  onLogout,
}: {
  email?: string;
  avatarUrl?: string;
  role?: string;
  source?: string;
  onLogout: () => void;
}) {
  const [open, setOpen] = useState(false);
  const avatar =
    avatarUrl ||
    `https://gravatar.com/avatar/${email ? md5(email.trim().toLowerCase()) : 'unknown'}?d=identicon`;
  const menuRef = useRef<HTMLDivElement>(null);

  // Close on outside click
  useEffect(() => {
    function handleClickOutside(e: MouseEvent) {
      if (menuRef.current && !menuRef.current.contains(e.target as Node)) {
        setOpen(false);
      }
    }
    if (open) {
      document.addEventListener('mousedown', handleClickOutside);
    }
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [open]);

  return (
    <div className="relative" ref={menuRef}>
      <button
        onClick={() => setOpen(!open)}
        className="focus:outline-none"
        aria-label="Avatar menu"
      >
        <div className="w-6 h-6 sm:w-5 sm:h-5 rounded-full overflow-hidden border border-white">
          <img src={avatar} alt="avatar" className="w-full h-full object-cover" />
        </div>
      </button>

      <AnimatePresence>
        {open && (
          <motion.div
            initial={{ opacity: 0, scale: 0.95, y: -5 }}
            animate={{ opacity: 1, scale: 1, y: 0 }}
            exit={{ opacity: 0, scale: 0.95, y: -5 }}
            transition={{ duration: 0.15 }}
            className="absolute right-0 mt-2 w-48 bg-zinc-900 border border-zinc-700 rounded shadow-xl text-xs z-50 p-2 space-y-2"
          >
            <div className="flex items-center gap-2 text-gray-300">
              <UserCircle size={14} />
              <span className="truncate">{email}</span>
            </div>
            <RoleBadge role={role} source={source} />
            <button
              onClick={onLogout}
              className="flex items-center gap-2 text-red-400 hover:underline text-left w-full"
            >
              <LogOut size={14} />
              Log Out
            </button>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

'use client';

import { usePathname } from 'next/navigation';
import { useSmartNav } from '../../../hooks/useSmartNav';
import SafeLink from '../../ui/safe-link';
import { NavBadge } from '../../ui/nav-badge';
import { useLiveAdminStats } from '../../../hooks/useLiveAdminStats';

export function NavSections() {
  const pathname = usePathname();
  const { nav, loading } = useSmartNav();
  const { unclaimed, errors } = useLiveAdminStats();

  const matches = (prefix: string) => pathname?.startsWith(prefix);

  if (loading) return null;

  return (
    <nav className="flex flex-col gap-6 text-sm">
      {/* üñ• Desktop */}
      <div className="hidden md:flex flex-wrap gap-4 items-center">
        {nav.map((section) => (
          <details
            key={section.label}
            className="group"
            open={section.routes.some((r) => matches(r.href))}
          >
            <summary
              className={`cursor-pointer font-semibold ${section.color} group-open:underline`}
            >
              {section.label}
            </summary>
            <div className="ml-2 flex flex-wrap gap-2 mt-1">
              {section.routes.map((r) => {
                const isDynamic = r.href.includes('[slug]') || r.href.includes(':');
                const labelWithCount =
                  r.href === '/admin/leads' && unclaimed && unclaimed > 0
                    ? `${r.label} (${unclaimed})`
                    : r.href === '/admin/drafts' && unclaimed && unclaimed > 0
                      ? `${r.label} (${unclaimed})`
                      : r.label;

                return (
                  <SafeLink
                    key={r.href}
                    href={r.href}
                    target={r.external ? '_blank' : undefined}
                    title={r.title}
                    onClick={() => {
                      fetch('/api/log-event', {
                        method: 'POST',
                        body: JSON.stringify({ href: r.href, type: 'nav_click' }),
                      });
                    }}
                  >
                    {labelWithCount}
                    <NavBadge flag={r.flags?.[0]} />
                  </SafeLink>
                );
              })}
            </div>
          </details>
        ))}
      </div>

      {/* üì± Mobile */}
      <div className="md:hidden">
        <label htmlFor="mobile-nav" className="block mb-1 font-medium text-muted-foreground">
          Navigate:
        </label>
        <select
          id="mobile-nav"
          className="w-full p-2 border rounded text-sm"
          onChange={(e) => {
            if (e.target.value) {
              fetch('/api/log-event', {
                method: 'POST',
                body: JSON.stringify({ href: e.target.value, type: 'nav_click' }),
              });
              window.location.href = e.target.value;
            }
          }}
        >
          <option value="">Select a page</option>
          {nav.flatMap((section) => [
            <optgroup key={section.label} label={section.label}>
              {section.routes.map((r) => (
                <option key={r.href} value={r.href}>
                  {r.label.replace(/^[^\w\s]+ /, '')}
                </option>
              ))}
            </optgroup>,
          ])}
        </select>
      </div>
    </nav>
  );
}

