'use client';

import { useDropzone } from 'react-dropzone';
import { useImageUploader } from '@/hooks/useImageUploader';

interface ImageUploaderProps {
  siteId: string;
  templateId: string;
  dbField: string;
  folder?: string;
  label: string;
  initialUrl?: string;
  onUpload?: (url: string | null) => void;
}

export default function ImageUploader(props: ImageUploaderProps) {
  const { preview, uploading, uploadImage, removeImage } = useImageUploader({
    siteId: props.siteId,
    templateId: props.templateId,
    dbField: props.dbField,
    folder: props.folder,
    label: props.label,
    initialUrl: props.initialUrl,
    onChange: props.onUpload,
  });

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    accept: { 'image/*': [] },
    onDrop: (acceptedFiles) => {
      const file = acceptedFiles[0];
      if (file) uploadImage(file);
    },
  });

  const getDropzoneSizeClass = (label?: string) => {
    if (!label) return 'h-40 w-full';

    const lower = label.toLowerCase();

    if (lower.includes('banner')) return 'h-32 w-full aspect-[4/1]';
    if (lower.includes('hero')) return 'h-64 w-full aspect-[2.5/1]';
    if (lower.includes('logo')) return 'h-32 w-32 aspect-square';
    if (lower.includes('team')) return 'h-48 w-full aspect-[3/2]';

    return 'h-40 w-full';
  };

  return (
    <div className="space-y-2">
      {props.label && <p className="text-xs text-white/70 font-medium">{props.label}</p>}

      <div
        {...getRootProps()}
        className={`border-2 border-dashed rounded-lg text-center cursor-pointer transition flex items-center justify-center
          ${isDragActive ? 'border-blue-500 bg-blue-50' : 'border-gray-300'}
          ${getDropzoneSizeClass(props.label)}
        `}
      >
        <input {...getInputProps()} />
        <p className="text-sm text-gray-400">
          {isDragActive ? 'Drop the image here‚Ä¶' : 'Drag & drop an image, or click to select one'}
        </p>
      </div>

      {uploading && <p className="text-sm text-gray-500">Uploading...</p>}

      {preview && (
        <div className="relative max-w-xs">
          <img src={preview} alt="Uploaded" className="rounded border max-h-48" />
          <button
            type="button"
            onClick={removeImage}
            className="absolute top-1 right-1 bg-red-500 text-white px-2 py-1 text-xs rounded"
          >
            Remove
          </button>
        </div>
      )}
    </div>
  );
}

'use client';

import { useEffect, useState } from 'react';
import { supabase } from '@/admin/lib/supabaseClient';

export default function TemplateImageGallery({ templateId }: { templateId: string }) {
  const [images, setImages] = useState<any[]>([]);

  useEffect(() => {
    if (!templateId) return;
    supabase
      .from('template_images')
      .select('*')
      .eq('template_id', templateId)
      .order('uploaded_at', { ascending: false })
      .then(({ data, error }) => {
        if (error) console.error('Failed to fetch image history:', error);
        else setImages(data);
      });
  }, [templateId]);

  if (!images.length) return null;

  return (
    <div className="mt-4 space-y-2">
      <h3 className="text-sm font-semibold">Upload History</h3>
      <div className="grid grid-cols-2 gap-2">
        {images.map((img) => (
          <div key={img.id} className="relative">
            <img
              src={img.url}
              alt={img.original_name}
              className="rounded border max-h-32 object-contain"
            />
            <div className="absolute bottom-1 left-1 bg-black bg-opacity-50 text-white text-xs px-1 rounded">
              {img.type}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

'use client';

import { useState } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { format, formatDistance } from 'date-fns';
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from '@/components/ui/tooltip';

interface TimelineEvent {
  type: string;
  source: 'guest' | 'log';
  created_at: string;
  trigger_reason?: string;
  referrer?: string;
  page_url?: string;
  platform?: string | null;
  device_type?: string | null;
  ip_address?: string | null;
  user_agent?: string | null;
  [key: string]: any; // optional fallback for dynamic props
}

interface Props {
  open: boolean;
  onClose: () => void;
  userId: string;
  userLabel: string;
  guestEvents: TimelineEvent[];
  userLogs: TimelineEvent[];
}

export function UserTimelineDialog({
  open,
  onClose,
  userId,
  userLabel,
  guestEvents,
  userLogs,
}: Props) {
  const [showAbsolute, setShowAbsolute] = useState(false);

  const allEvents = [...guestEvents, ...userLogs]
    .map((e) => ({
      ...e,
      timestamp: new Date(e.created_at),
    }))
    .sort((a, b) => a.timestamp.getTime() - b.timestamp.getTime());

  const origin = allEvents[0]?.timestamp ?? new Date();

  const emojiMap: Record<string, string> = {
    view: 'üëÅÔ∏è',
    click: 'üöÄ',
    signup_complete: '‚úÖ',
    site_published: 'üì§',
  };

  const descriptions: Record<string, string> = {
    view: 'User opened the upgrade modal',
    click: 'User clicked ‚ÄúSign Up‚Äù button',
    signup_complete: 'User completed sign up',
    site_published: 'User published a site',
  };

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="max-w-lg">
        <DialogHeader>
          <DialogTitle className="text-lg font-bold">Timeline for {userLabel}</DialogTitle>
          {guestEvents.length > 0 &&
            (() => {
              const firstView = guestEvents.find((e) => e.type === 'view');
              if (!firstView) return null;

              const ref = (firstView as any).referrer || '';
              const host = typeof window !== 'undefined' ? window.location.origin : '';
              const isInternal = ref.startsWith(host) || ref === '';

              const refColor = ref
                ? isInternal
                  ? 'text-green-600'
                  : 'text-red-600'
                : 'text-muted-foreground';

              const utmFields = [
                'utm_source',
                'utm_medium',
                'utm_campaign',
                'utm_term',
                'utm_content',
              ];

              return (
                <div className="text-sm text-muted-foreground space-y-1 border p-2 rounded bg-muted">
                  {firstView.trigger_reason && (
                    <div>
                      <strong>Trigger:</strong> {firstView.trigger_reason}
                    </div>
                  )}
                  <div>
                    <strong>Referrer:</strong> <span className={refColor}>{ref || 'none'}</span>
                  </div>
                  {(firstView as any).page_url && (
                    <div>
                      <strong>Page:</strong>{' '}
                      <a
                        href={(firstView as any).page_url}
                        target="_blank"
                        rel="noreferrer"
                        className="underline"
                      >
                        {(firstView as any).page_url}
                      </a>
                    </div>
                  )}
                  {utmFields
                    .filter((key) => (firstView as any)[key])
                    .map((key) => (
                      <div key={key}>
                        <strong>{key.replace('utm_', '').toUpperCase()}:</strong>{' '}
                        {(firstView as any)[key]}
                      </div>
                    ))}
                  {(firstView as any).page_url && (
                    <Button
                      size="sm"
                      variant="secondary"
                      className="mt-2"
                      onClick={() => window.open((firstView as any).page_url, '_blank')}
                    >
                      Open Page Context ‚Üó
                    </Button>
                  )}
                </div>
              );
            })()}
        </DialogHeader>

        <div className="mb-2">
          <Button variant="outline" size="sm" onClick={() => setShowAbsolute(!showAbsolute)}>
            Toggle {showAbsolute ? 'Relative' : 'Absolute'} Timestamps
          </Button>
        </div>

        <div className="space-y-2 text-sm max-h-[400px] overflow-y-auto">
          <TooltipProvider>
            {allEvents.map((e, i) => {
              const prev = allEvents[i - 1]?.timestamp;
              const delta =
                prev && e.timestamp
                  ? formatDistance(e.timestamp, prev, { addSuffix: false })
                  : null;

              const deviceInfo =
                e.source === 'guest' && (e.user_agent || e.platform || e.device_type)
                  ? [e.platform, e.device_type].filter(Boolean).join(' ‚Ä¢ ') +
                    (e.user_agent ? `\n${e.user_agent}` : '')
                  : null;

              return (
                <div key={i} className="border-b pb-1">
                  <div className="flex justify-between items-start">
                    <Tooltip>
                      <TooltipTrigger asChild>
                        <span>
                          {emojiMap[e.type] || 'üîπ'} {descriptions[e.type] || e.type}
                        </span>
                      </TooltipTrigger>
                      <TooltipContent>
                        <p>{descriptions[e.type] || 'Event in user journey'}</p>
                      </TooltipContent>
                    </Tooltip>

                    <span className="text-muted-foreground text-xs">
                      {showAbsolute
                        ? format(e.timestamp, 'MMM d, yyyy HH:mm:ss')
                        : formatDistance(e.timestamp, origin, {
                            addSuffix: true,
                          })}
                    </span>
                  </div>

                  {delta && (
                    <div className="text-xs text-muted-foreground pl-4">
                      +{delta} since previous
                    </div>
                  )}

                  {deviceInfo && (
                    <div className="text-xs text-muted-foreground mt-1 pl-4 whitespace-pre-wrap">
                      {deviceInfo}
                    </div>
                  )}
                </div>
              );
            })}
          </TooltipProvider>

          {allEvents.length === 0 && (
            <div className="text-muted-foreground italic">No events found for this user.</div>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

