'use client';

import { useEffect, useState } from 'react';
import { supabase } from '@/admin/lib/supabaseClient';
import { Button } from '@/components/ui/button';
import { Template } from '@/types/template';

export default function TemplateUserViewer() {
  type Assignment = {
    user_id: string;
    email: string;
    template_id: string;
    template: string;
  };
  type TemplateOption = { id: string; name: string };
  const [templates, setTemplates] = useState<TemplateOption[]>([]);
  const [selectedTemplate, setSelectedTemplate] = useState<string>('');
  const [assignments, setAssignments] = useState<Assignment[]>([]);
  const [filtered, setFiltered] = useState<Assignment[]>([]);
  const [search, setSearch] = useState('');
  const [filterTemplate, setFilterTemplate] = useState('');
  const [selected, setSelected] = useState('');

  useEffect(() => {
    loadData();
  }, []);

  useEffect(() => {
    setFiltered(
      assignments.filter(
        (a) =>
          (!search || a.email.toLowerCase().includes(search.toLowerCase())) &&
          (!filterTemplate || a.template === filterTemplate)
      )
    );
  }, [search, filterTemplate, assignments]);

  const loadData = async () => {
    const { data: map } = await supabase
      .from('dashboard_user_layouts')
      .select('user_id, template_id');

    const { data: users } = await supabase.from('auth.users').select('id, email');

    const { data: templates } = await supabase
      .from('dashboard_layout_templates')
      .select('id, name');

    const joined =
      map?.map((row) => ({
        user_id: row.user_id,
        email: users?.find((u) => u.id === row.user_id)?.email || '‚Äî',
        template_id: row.template_id,
        template: templates?.find((t) => t.id === row.template_id)?.name || '(deleted)',
      })) || [];

    setAssignments(joined);
    setTemplates(templates || []);
    setFiltered(joined);
  };

  const bulkAssign = async () => {
    const { data: users } = await supabase.from('auth.users').select('id');
    const inserts = users?.map((u) => ({
      user_id: u.id,
      template_id: selected,
    }));
    await supabase.from('dashboard_user_layouts').upsert(inserts || []);
    loadData();
    alert('All users assigned!');
  };

  const revertUser = async (user_id: string) => {
    await supabase.from('dashboard_user_layouts').delete().eq('user_id', user_id);
    loadData();
  };

  return (
    <div className="p-4 border rounded bg-white shadow max-w-3xl mt-8">
      <h2 className="text-lg font-semibold mb-3">User ‚Üí Template Assignments</h2>

      <div className="flex flex-wrap gap-3 items-center mb-4">
        <input
          type="text"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          placeholder="Search user email..."
          className="border p-2 text-sm"
        />
        <select
          value={filterTemplate}
          onChange={(e) => setFilterTemplate(e.target.value)}
          className="border p-2 text-sm"
        >
          <option value="">All templates</option>
          {templates.map((tpl) => (
            <option key={tpl.id} value={tpl.name}>
              {tpl.name}
            </option>
          ))}
        </select>
        <select
          value={selected}
          onChange={(e) => setSelected(e.target.value)}
          className="border p-2 text-sm"
        >
          <option value="">Select template to assign all</option>
          {templates.map((tpl) => (
            <option key={tpl.id} value={tpl.id}>
              {tpl.name}
            </option>
          ))}
        </select>
        <Button onClick={bulkAssign}>Assign to All</Button>
      </div>

      <table className="w-full text-sm border">
        <thead className="bg-gray-100">
          <tr>
            <th className="text-left p-2">User Email</th>
            <th className="text-left p-2">Template</th>
            <th className="text-left p-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {filtered.map((a) => (
            <tr key={a.user_id} className="border-b">
              <td className="p-2">{a.email}</td>
              <td className="p-2">{a.template}</td>
              <td className="p-2">
                <Button variant="ghost" size="sm" onClick={() => revertUser(a.user_id)}>
                  Revert
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

import { useEffect, useState } from 'react';
import { Dialog, DialogContent } from '@/components/ui/dialog';
import { BlocksEditor } from '@/components/admin/templates/blocks-editor';
import toast from 'react-hot-toast';
import { Button } from '@/components/ui/button';
import { BlockEditorModalProps } from '@/types/blocks';

const schemaHints: Record<string, string> = {
  type: 'Block type (e.g. hero, services)',
  content: 'Main content object (text, image, etc.)',
  style: 'Optional custom style or class',
};

export default function BlockEditorModal({
  open,
  onClose,
  block,
  onSave,
}: {
  open: boolean;
  onClose: () => void;
  block: any;
  onSave: (newBlock: any) => void;
}) {
  const [value, setValue] = useState<string>('');
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    setValue(JSON.stringify(block, null, 2));
    setError(null);
  }, [block]);

  const handleSave = () => {
    try {
      const parsed = JSON.parse(value);
      onSave(parsed);
      toast.success('Block updated');
      onClose();
    } catch (e: any) {
      setError(e.message || 'Invalid JSON');
    }
  };

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="bg-gray-900 text-white max-w-2xl max-h-screen overflow-y-auto">
        <h2 className="text-lg font-bold mb-2">Edit Block JSON</h2>
        <BlocksEditor
          blocks={[
            {
              id: crypto.randomUUID(),
              type: 'hero',
              content: {
                title: 'Hello',
                description: 'World',
              },
            },
          ]}
          onChange={() => {}}
        />
        {error && <p className="text-sm text-red-400 mt-2">{error}</p>}

        <div className="mt-4">
          <p className="text-xs font-semibold text-gray-400">Block Schema Tips:</p>
          <ul className="text-xs mt-1 space-y-1 pl-4 list-disc text-gray-400">
            {Object.entries(schemaHints).map(([key, hint]) => (
              <li key={key}>
                <span className="text-white">{key}:</span> {hint}
              </li>
            ))}
          </ul>
        </div>

        <div className="flex justify-end mt-6 gap-2">
          <Button variant="secondary" onClick={onClose}>
            Cancel
          </Button>
          <Button onClick={handleSave}>Save</Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}

// BlockEditor.tsx
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import { Button } from '@/components/ui/button';
import { Plus, Trash2 } from 'lucide-react';

type Block = {
  id: string;
  type: string;
  [key: string]: any;
};

type Props = {
  block: Block;
  onChange: (block: Block) => void;
};

export default function BlockEditor({ block, onChange }: Props) {
  const updateField = (key: string, value: any) => {
    onChange({ ...block, [key]: value });
  };

  const updateListItem = (index: number, value: string) => {
    const items = [...(block.items || [])];
    items[index] = value;
    updateField('items', items);
  };

  const addListItem = () => {
    const items = [...(block.items || []), ''];
    updateField('items', items);
  };

  const deleteListItem = (index: number) => {
    const items = [...(block.items || [])];
    items.splice(index, 1);
    updateField('items', items);
  };

  switch (block.type) {
    case 'text':
      return (
        <div>
          <Label>Text</Label>
          <Textarea
            value={block.value || ''}
            onChange={(e) => updateField('value', e.target.value)}
          />
        </div>
      );

    case 'image':
      return (
        <div className="space-y-2">
          <Label>Image URL</Label>
          <Input value={block.url || ''} onChange={(e) => updateField('url', e.target.value)} />
          <Label>Alt Text</Label>
          <Input value={block.alt || ''} onChange={(e) => updateField('alt', e.target.value)} />
          {block.url && <img src={block.url} alt={block.alt} className="w-full rounded" />}
        </div>
      );

    case 'cta':
      return (
        <div className="space-y-2">
          <Label>CTA Label</Label>
          <Input value={block.label || ''} onChange={(e) => updateField('label', e.target.value)} />
          <Label>Link</Label>
          <Input value={block.link || ''} onChange={(e) => updateField('link', e.target.value)} />
        </div>
      );

    case 'quote':
      return (
        <div className="space-y-2">
          <Label>Quote</Label>
          <Textarea
            value={block.text || ''}
            onChange={(e) => updateField('text', e.target.value)}
          />
          <Label>Attribution</Label>
          <Input
            value={block.attribution || ''}
            onChange={(e) => updateField('attribution', e.target.value)}
          />
        </div>
      );

    case 'video':
      return (
        <div className="space-y-2">
          <Label>Embed URL (YouTube, Vimeo...)</Label>
          <Input
            value={block.embed_url || ''}
            onChange={(e) => updateField('embed_url', e.target.value)}
          />
          <Label>Caption</Label>
          <Input
            value={block.caption || ''}
            onChange={(e) => updateField('caption', e.target.value)}
          />
          {block.embed_url && (
            <iframe src={block.embed_url} className="w-full aspect-video rounded" allowFullScreen />
          )}
        </div>
      );

    case 'divider':
      return <div className="my-4 border-t border-muted" />;

    case 'list':
      return (
        <div className="space-y-2">
          <Label>List Items</Label>
          {(block.items || []).map((item: string, i: number) => (
            <div key={i} className="flex gap-2 items-center">
              <Input value={item} onChange={(e) => updateListItem(i, e.target.value)} />
              <Button variant="ghost" size="sm" onClick={() => deleteListItem(i)}>
                <Trash2 className="w-4 h-4" />
              </Button>
            </div>
          ))}
          <Button onClick={addListItem} variant="outline" size="sm">
            <Plus className="w-4 h-4 mr-2" /> Add Item
          </Button>
        </div>
      );

    default:
      return (
        <div className="text-sm text-muted-foreground">Unsupported block type: {block.type}</div>
      );
  }
}

// BlockSidebar.tsx
import { useState } from 'react';
import { X } from 'lucide-react';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import { Button } from '@/components/ui/button';
import {
  Block,
  HeroBlockContent,
  ServicesBlockContent,
  TestimonialBlockContent,
  TextBlockContent,
  CtaBlockContent,
  QuoteBlockContent,
  BlockSidebarProps,
} from '@/types/blocks';

type Props = {
  block: Block;
  onChange: (block: Block) => void;
  onClose: () => void;
};

const blockTypes = ['hero', 'services', 'testimonial', 'text', 'cta', 'quote'];

export default function BlockSidebar({ block, onChange, onClose }: Props) {
  if (!block) return null;

  const updateContent = (key: string, value: any) => {
    const updatedContent = { ...(block.content || {}), [key]: value };
    onChange({ ...block, content: updatedContent });
  };

  const inputClass = 'bg-zinc-800 border-zinc-700 text-white placeholder-zinc-400';

  const content = block.content || {};

  return (
    <div className="fixed right-0 top-0 h-full w-[340px] bg-zinc-900 text-white shadow-lg border-l border-zinc-800 z-50 p-4 space-y-4 overflow-y-auto">
      <div className="flex justify-between items-center">
        <h2 className="text-lg font-semibold capitalize">{block.type || 'Block'} Block</h2>
        <Button variant="ghost" onClick={onClose}>
          <X className="w-4 h-4 text-white" />
        </Button>
      </div>

      <div>
        <Label>Block Type</Label>
        <select
          value={block.type}
          onChange={(e) => onChange({ ...block, type: e.target.value })}
          className="w-full mt-1 rounded bg-zinc-800 text-white border border-zinc-700 px-2 py-1"
        >
          {blockTypes.map((t) => (
            <option key={t} value={t}>
              {t.charAt(0).toUpperCase() + t.slice(1)}
            </option>
          ))}
        </select>
      </div>

      {block.type === 'hero' && (
        <>
          <Label>Headline</Label>
          <Input
            className={inputClass}
            value={(content as HeroBlockContent).headline || ''}
            onChange={(e) => updateContent('headline', e.target.value)}
          />
          <Label>Subheadline</Label>
          <Input
            className={inputClass}
            value={(content as HeroBlockContent).subheadline || ''}
            onChange={(e) => updateContent('subheadline', e.target.value)}
          />
          <Label>CTA Text</Label>
          <Input
            className={inputClass}
            value={(content as HeroBlockContent).cta_text || ''}
            onChange={(e) => updateContent('cta_text', e.target.value)}
          />
          <Label>CTA Link</Label>
          <Input
            className={inputClass}
            value={(content as HeroBlockContent).cta_link || ''}
            onChange={(e) => updateContent('cta_link', e.target.value)}
          />
        </>
      )}

      {block.type === 'services' && (
        <>
          <Label>Service Items (comma-separated)</Label>
          <Textarea
            className={inputClass}
            value={(content as ServicesBlockContent).items?.join(', ') || ''}
            onChange={(e) =>
              updateContent(
                'items',
                e.target.value.split(',').map((s) => s.trim())
              )
            }
          />
        </>
      )}

      {block.type === 'testimonial' && (
        <>
          <Label>Quote</Label>
          <Textarea
            className={inputClass}
            value={(content as TestimonialBlockContent).quote || ''}
            onChange={(e) => updateContent('quote', e.target.value)}
          />
          <Label>Attribution</Label>
          <Input
            className={inputClass}
            value={(content as TestimonialBlockContent).attribution || ''}
            onChange={(e) => updateContent('attribution', e.target.value)}
          />
        </>
      )}

      {block.type === 'text' && (
        <>
          <Label>Text</Label>
          <Textarea
            className={inputClass}
            value={(content as TextBlockContent).value || ''}
            onChange={(e) => updateContent('value', e.target.value)}
          />
        </>
      )}

      {block.type === 'cta' && (
        <>
          <Label>Label</Label>
          <Input
            className={inputClass}
            value={(content as CtaBlockContent).label || ''}
            onChange={(e) => updateContent('label', e.target.value)}
          />
          <Label>Link</Label>
          <Input
            className={inputClass}
            value={(content as CtaBlockContent).link || ''}
            onChange={(e) => updateContent('link', e.target.value)}
          />
        </>
      )}

      {block.type === 'quote' && (
        <>
          <Label>Quote</Label>
          <Textarea
            className={inputClass}
            value={(content as QuoteBlockContent).text || ''}
            onChange={(e) => updateContent('text', e.target.value)}
          />
          <Label>Attribution</Label>
          <Input
            className={inputClass}
            value={(content as QuoteBlockContent).attribution || ''}
            onChange={(e) => updateContent('attribution', e.target.value)}
          />
        </>
      )}

      {!blockTypes.includes(block.type) && (
        <div className="text-sm text-muted-foreground">
          Unsupported block type: <code>{block.type}</code>
        </div>
      )}
    </div>
  );
}

