import type { Block } from '@/types/block';

export default function RenderBlock({ block }: { block: Block }) {
  switch (block.type) {
    case 'text':
      return <p className="mb-4 text-base text-gray-800">{block.value}</p>;

    case 'image':
      return (
        <img
          src={block.value.url}
          alt={block.value.alt}
          className="mb-4 rounded shadow-md max-w-full"
        />
      );

    case 'video':
      return (
        <div className="mb-4">
          <video controls className="max-w-full rounded">
            <source src={block.value.url} />
          </video>
          {block.value.caption && (
            <p className="text-sm text-gray-500 mt-1">{block.value.caption}</p>
          )}
        </div>
      );

    case 'audio':
      return (
        <div className="mb-4">
          <iframe
            className="w-full"
            style={{ height: 80 }}
            src={block.value.url}
            title={block.value.title || block.value.provider}
            allow="autoplay; encrypted-media"
            loading="lazy"
          />
        </div>
      );

    case 'quote':
      return (
        <blockquote className="border-l-4 pl-4 italic text-gray-600 mb-4">
          ‚Äú{block.value.text}‚Äù
          {block.value.author && (
            <footer className="mt-1 text-sm text-right">‚Äî {block.value.author}</footer>
          )}
        </blockquote>
      );

    case 'button':
      return (
        <a
          href={block.value.href}
          className={`inline-block px-4 py-2 rounded text-white text-sm mb-4
            ${block.value.style === 'ghost' ? 'bg-transparent border' : block.value.style === 'secondary' ? 'bg-gray-600' : 'bg-blue-600'}`}
        >
          {block.value.label}
        </a>
      );

    case 'grid':
      return (
        <div className={`grid grid-cols-${block.value.columns} gap-4 mb-4`}>
          {block.value.items.map((b, i) => (
            <RenderBlock key={i} block={b} />
          ))}
        </div>
      );

    default:
      return null;
  }
}

import {
  DndContext,
  closestCenter,
  KeyboardSensor,
  PointerSensor,
  useSensor,
  useSensors,
} from '@dnd-kit/core';
import {
  arrayMove,
  SortableContext,
  useSortable,
  verticalListSortingStrategy,
} from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import { useState } from 'react';
import TemplatePreview from './template-preview';
import { ReorderableBlockListProps } from '@/types/blocks';

// type ReorderableBlockListProps = {
//   data: any;
//   colorScheme: string;
//   onBlockClick?: (block: any) => void;
//   onReorder: (updatedData: any) => void;
// };

export default function ReorderableBlockList({
  data,
  colorScheme,
  onBlockClick,
  onReorder,
}: {
  data: any;
  colorScheme: string;
  onBlockClick?: (block: any) => void;
  onReorder: (updatedData: any) => void;
}) {
  const sensors = useSensors(useSensor(PointerSensor), useSensor(KeyboardSensor));

  const [activePageIdx] = useState(0); // assuming only 1 page for now
  const blocks = data?.pages?.[activePageIdx]?.content_blocks || [];

  return (
    <DndContext
      sensors={sensors}
      collisionDetection={closestCenter}
      onDragEnd={({ active, over }) => {
        if (!over || active.id === over.id) return;
        const oldIndex = blocks.findIndex((b: any) => b._id === active.id);
        const newIndex = blocks.findIndex((b: any) => b._id === over.id);
        const reordered = arrayMove(blocks, oldIndex, newIndex);
        const updatedData = {
          ...data,
          pages: data.pages.map((p: any, i: number) =>
            i === activePageIdx ? { ...p, content_blocks: reordered } : p
          ),
        };
        onReorder(updatedData);
      }}
    >
      <SortableContext items={blocks.map((b: any) => b._id)} strategy={verticalListSortingStrategy}>
        <TemplatePreview
          data={data}
          colorScheme={colorScheme}
          // onBlockClick={onBlockClick}
        />
      </SortableContext>
    </DndContext>
  );
}

import { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';
import { Button } from '@/components/ui/button';
import { saveAsTemplate } from '@/admin/lib/saveAsTemplate';
import { createSharedPreview } from '@/admin/lib/createSharedPreview';
import { supabase } from '@/admin/lib/supabaseClient';
import toast from 'react-hot-toast';

export default function TemplateActionToolbar({
  template,
  autosaveStatus,
  onSaveDraft,
}: {
  template: any;
  autosaveStatus?: string;
  onSaveDraft?: () => void;
}) {
  const router = useRouter();
  const [status, setStatus] = useState('Draft');
  const [versions, setVersions] = useState<any[]>([]);

  useEffect(() => {
    if (template?.template_name) {
      supabase
        .from('template_versions')
        .select('id, commit_message, created_at')
        .eq('template_name', template.template_name)
        .order('created_at', { ascending: false })
        .then(({ data }) => {
          if (data) setVersions(data);
        });

      setStatus(template?.published_at ? 'Published' : 'Draft');
    }
  }, [template?.template_name]);

  useEffect(() => {
    const handleHotkey = (e: KeyboardEvent) => {
      if (e.metaKey && e.key === 's') {
        e.preventDefault();
        onSaveDraft?.();
      }
      if (e.metaKey && e.shiftKey && e.key.toLowerCase() === 's') {
        e.preventDefault();
        handleShare();
      }
      if (e.metaKey && e.shiftKey && e.key.toLowerCase() === 'c') {
        e.preventDefault();
        toast.success('Copy preview (not implemented)');
      }
    };
    window.addEventListener('keydown', handleHotkey);
    return () => window.removeEventListener('keydown', handleHotkey);
  }, [template]);

  const handleSaveAs = async () => {
    const newId = await saveAsTemplate(template);
    if (newId) {
      toast.success('Template copied');
      router.push(`/admin/templates?selected=${newId}`);
    } else {
      toast.error('Failed to copy template');
    }
  };

  const handleShare = async () => {
    const id = await createSharedPreview(template.id);
    if (id) {
      toast.success('Preview shared!');
      router.push(`/shared/${id}`);
    } else {
      toast.error('Share failed');
    }
  };

  return (
    <div className="fixed bottom-0 left-0 w-full z-40 border-t border-gray-700 bg-gray-900 px-6 py-3 flex justify-between items-center text-white">
      <div className="text-sm font-medium flex gap-4 items-center">
        <span>üìÑ {template.template_name}</span>
        <span
          className={`text-xs px-2 py-1 rounded ${
            status === 'Published' ? 'bg-green-600' : 'bg-yellow-600'
          }`}
        >
          {status}
        </span>
        {autosaveStatus && (
          <span className="text-xs text-gray-400 italic">üíæ {autosaveStatus}</span>
        )}
      </div>

      <div className="flex items-center gap-3">
        <select
          className="bg-gray-800 border border-gray-600 text-sm text-white rounded px-2 py-1"
          onChange={(e) => toast(`Selected version: ${e.target.value}`)}
        >
          <option>View Version</option>
          {versions.map((v) => (
            <option key={v.id}>
              {v.commit_message || 'Untitled'} ‚Äî {new Date(v.created_at).toLocaleDateString()}
            </option>
          ))}
        </select>

        <Button size="sm" variant="secondary" onClick={onSaveDraft}>
          Save Draft
        </Button>
        <Button size="sm" variant="secondary" onClick={handleSaveAs}>
          Save As Template
        </Button>
        <Button size="sm" variant="secondary" onClick={handleShare}>
          Share Snapshot
        </Button>
      </div>
    </div>
  );
}

import { useEffect, useState } from 'react';
import { toast } from 'react-hot-toast';
import { createClient } from '@supabase/supabase-js';

const supabase = createClient(
  process.env.NEXT_PUBLIC_SUPABASE_URL!,
  process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!
);

export function useTemplateLoader(templateName: string) {
  const [template, setTemplate] = useState<any | null>(null);

  useEffect(() => {
    const loadTemplate = async () => {
      const { data, error } = await supabase
        .from('templates')
        .select('*')
        .eq('template_name', templateName)
        .single();

      if (error || !data) {
        toast.error('Failed to load template from DB');
        return;
      }

      const draftKey = `draft-${data.id}`;
      const savedDraft = localStorage.getItem(draftKey);

      if (savedDraft) {
        try {
          const parsed = JSON.parse(savedDraft);
          if (parsed && parsed.pages && parsed.pages.length > 0) {
            toast((t) => (
              <span>
                üìù A draft was found.
                <button
                  className="ml-2 underline text-blue-400"
                  onClick={() => {
                    toast.dismiss(t.id);
                    setTemplate({ ...data, data: parsed });
                  }}
                >
                  Restore it?
                </button>
              </span>
            ));
            setTemplate({ ...data }); // Don't restore by default
            return;
          }
        } catch (err) {
          console.warn('Failed to parse draft JSON');
        }
      }

      setTemplate(data);
    };

    loadTemplate();
  }, [templateName]);

  return template;
}

