import { useContext, useEffect, useState } from 'react';
import {
  CurrentUserContext,
  CurrentUserContextType,
} from '@/components/admin/context/current-user-provider';
import { supabase } from '@/admin/lib/supabaseClient';

export function useCurrentUser(): CurrentUserContextType & {
  role: string;
  roleSource: string;
  isLoading: boolean;
  ready: boolean;
} {
  const context = useContext(CurrentUserContext);

  const [fetchedRole, setFetchedRole] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [roleSource, setRoleSource] = useState<'session' | 'db' | 'cache'>('session');

  useEffect(() => {
    const fetchRole = async () => {
      if (!context.user?.email) {
        setIsLoading(false);
        return;
      }

      const cacheKey = `cached-role-${context.user.email}`;

      // Check localStorage first
      const cached = localStorage.getItem(cacheKey);
      if (cached) {
        console.debug('üì¶ Using cached fallback role:', cached);
        setFetchedRole(cached);
        setRoleSource('cache');
        setIsLoading(false);
        return;
      }

      // Otherwise query Supabase
      const { data, error } = await supabase
        .from('user_roles')
        .select('new_role')
        .eq('user_email', context.user.email)
        .order('updated_at', { ascending: false })
        .limit(1)
        .single();

      if (error) {
        console.warn('üîç Role fetch error', error.message);
      } else if (data?.new_role?.trim()) {
        console.debug('‚úÖ Fallback role from DB:', data.new_role);
        setFetchedRole(data.new_role);
        setRoleSource('db');
        localStorage.setItem(cacheKey, data.new_role);
      }

      setIsLoading(false);
    };

    fetchRole();
  }, [context.user?.email]);

  const role: string =
    (fetchedRole as 'viewer' | 'admin' | 'editor' | 'owner') ||
    context.user?.role ||
    'viewer';

  return {
    ...context,
    role: role as 'viewer' | 'admin' | 'editor' | 'owner',
    roleSource,
    isLoading,
    ready: !isLoading,
  };
}

// hooks/useDashboardLayout.ts

import { useEffect, useState } from 'react';
import { supabase } from '../admin/lib/supabaseClient';

type Block = { id: string; title: string };
type Settings = Record<string, Record<string, any>>;

export function useDashboardLayout(userId: string | null, dashboardId?: string) {
  const [order, setOrder] = useState<Block[]>([]);
  const [hidden, setHidden] = useState<string[]>([]);
  const [settings, setSettings] = useState<Settings>({});
  const [dashboards, setDashboards] = useState<any[]>([]);
  const [activeDashboardId, setActiveDashboardId] = useState<string | null>(dashboardId || null);
  const [loaded, setLoaded] = useState(false);
  const [loading, setLoading] = useState(false);

  // Fetch dashboards for the user
  useEffect(() => {
    if (!userId) return;
    setLoading(true);
    supabase
      .from('dashboard_user_layouts')
      .select('*')
      .eq('user_id', userId)
      .then(({ data }) => {
        setDashboards(data || []);
        if (!activeDashboardId && data?.[0]) {
          setActiveDashboardId(data[0].dashboard_id);
        }
        setLoading(false);
      });
  }, [userId]);

  // Load selected layout
  useEffect(() => {
    if (!userId || !activeDashboardId) return;
    setLoading(true);
    supabase
      .from('dashboard_user_layouts')
      .select('layout, hidden, settings')
      .eq('user_id', userId)
      .eq('dashboard_id', activeDashboardId)
      .single()
      .then(({ data }) => {
        if (data?.layout) setOrder(data.layout);
        if (data?.hidden) setHidden(data.hidden);
        if (data?.settings) setSettings(data.settings);
        setLoaded(true);
        setLoading(false);
      });

    const channel = supabase
      .channel('dashboard_sync')
      .on(
        'postgres_changes',
        {
          event: 'UPDATE',
          schema: 'public',
          table: 'dashboard_user_layouts',
          filter: `user_id=eq.${userId}`,
        },
        (payload) => {
          const updated = payload.new;
          if (updated.dashboard_id === activeDashboardId) {
            if (updated.layout) setOrder(updated.layout);
            if (updated.hidden) setHidden(updated.hidden);
            if (updated.settings) setSettings(updated.settings);
          }
        }
      )
      .subscribe();

    return () => {
      supabase.removeChannel(channel);
    };
  }, [userId, activeDashboardId]);

  const save = async (layout: Block[], hiddenList: string[], newSettings = settings) => {
    if (!userId || !activeDashboardId) return;
    setLoading(true);
    await supabase.from('dashboard_user_layouts').upsert({
      user_id: userId,
      dashboard_id: activeDashboardId,
      layout,
      hidden: hiddenList,
      settings: newSettings,
      updated_at: new Date().toISOString(),
    });
    setOrder(layout);
    setHidden(hiddenList);
    setSettings(newSettings);
    setLoading(false);
  };

  const updateBlockSetting = (blockId: string, key: string, value: any) => {
    const newSettings = {
      ...settings,
      [blockId]: {
        ...(settings[blockId] || {}),
        [key]: value,
      },
    };
    save(order, hidden, newSettings);
  };
  const createDashboard = async (name: string) => {
    if (!userId) return;
    setLoading(true);
    const { data } = await supabase
      .from('dashboard_user_layouts')
      .insert({
        user_id: userId,
        name,
        layout: 'DEFAULT_LAYOUT',
        hidden: [],
        settings: {},
      })
      .select()
      .single();

    if (data) {
      setDashboards((prev) => [...prev, data]);
      setActiveDashboardId(data.dashboard_id);
      setOrder(data.layout);
      setHidden([]);
      setSettings({});
      setLoading(false);
    }
  };

  return {
    order,
    hidden,
    settings,
    dashboards,
    activeDashboardId,
    setActiveDashboardId,
    loaded,
    save,
    updateBlockSetting,
    createDashboard,
    loading,
  };
}

'use client';
import { useRouter, useSearchParams } from 'next/navigation';
import { useMemo } from 'react';

export function useDateRange() {
  const searchParams = useSearchParams();

  const start = useMemo(() => {
    if (typeof searchParams?.get('start') === 'string') return searchParams.get('start');
    return '';
  }, [searchParams?.get('start')]);

  const end = useMemo(() => {
    if (typeof searchParams?.get('end') === 'string') return searchParams.get('end');
    return '';
  }, [searchParams?.get('end')]);

  return { start, end };
}

import { useEffect } from 'react';
import { useRouter, useSearchParams } from 'next/navigation';
import { toast, ToastOptions } from 'react-hot-toast';

type ToastType = 'success' | 'error' | 'loading' | 'custom';

type UseFlashToastOptions = {
  type?: ToastType;
  prefix?: string;
  duration?: number;
  icon?: string;
  className?: string;
};

export function useFlashToast(
  key: string,
  { type = 'success', prefix = '', duration = 3000, icon, className }: UseFlashToastOptions = {}
) {
  const searchParams = useSearchParams();
  const value = searchParams?.get(key);

  useEffect(() => {
    if (value && typeof value === 'string') {
      const message = `${prefix}${value}`;

      const toastOptions: ToastOptions = {
        duration,
        icon,
        className,
      };

      switch (type) {
        case 'error':
          toast.error(message, toastOptions);
          break;
        case 'loading':
          toast.loading(message, toastOptions);
          break;
        case 'custom':
          toast(message, toastOptions);
          break;
        default:
          toast.success(message, toastOptions);
      }

      // Remove the flash param from the URL
      const rest = new URLSearchParams(searchParams?.toString() || '');
      rest.delete(key);
      window.location.href = `${window.location.pathname}?${rest.toString()}`;
    }
  }, [value]);
}

// hooks/useGeoLocation.ts
import { useEffect, useState } from 'react';
import { getCachedGeo, setCachedGeo } from '../lib/geoCache.js';
import staticGeo from '../public/staticGeo.json';

// USAGE:
// const { lat, lon, loading, error } = useGeoLocation('Franklin', 'WI');
// if (loading) return <p>Loading map...</p>;
// if (error) return <p>Error: {error}</p>;

function getStaticFallback(city: string, state: string): { lat: number; lon: number } | undefined {
  const key = `${city.trim().toLowerCase()},${state.trim().toLowerCase()}`;
  return staticGeo[key as keyof typeof staticGeo];
}

export function useGeoLocation(city: string, state: string) {
  const [coords, setCoords] = useState<{ lat: number; lon: number } | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    if (!city || !state) return;

    const tryGeoSources = async () => {
      setLoading(true);
      const key = `${city}, ${state}`;

      // 1. Local memory cache
      const memoryHit = getCachedGeo(city, state);
      if (memoryHit) {
        setCoords(memoryHit);
        setLoading(false);
        return;
      }

      // 2. Static fallback JSON
      const staticHit = getStaticFallback(city, state);
      if (staticHit) {
        setCachedGeo(city, state, staticHit.lat, staticHit.lon);
        setCoords(staticHit);
        setLoading(false);
        return;
      }

      // 3. Remote API
      try {
        const res = await fetch(
          `/api/geocode?city=${encodeURIComponent(city)}&state=${encodeURIComponent(state)}`
        );
        if (!res.ok) throw new Error(`API failed: ${res.status}`);
        const data = await res.json();
        const lat = Number(data.lat || 0);
        const lon = Number(data.lon || 0);
        setCachedGeo(city, state, lat, lon);
        setCoords({ lat, lon });
      } catch (err: any) {
        setError(err.message || 'Unknown error');
        setCoords({ lat: 0, lon: 0 });
      } finally {
        setLoading(false);
      }
    };

    tryGeoSources();
  }, [city, state]);

  return { lat: coords?.lat, lon: coords?.lon, loading, error };
}

import { useState } from 'react';
import { supabase } from '../admin/lib/supabaseClient';

interface UseImageUploaderOptions {
  siteId: string;
  templateId: string;
  dbField: string;
  folder?: string;
  label?: string;
  initialUrl?: string;
  onChange?: (url: string | null) => void;
}

export function useImageUploader({
  siteId,
  templateId,
  dbField,
  folder = 'uploads',
  label,
  initialUrl,
  onChange,
}: UseImageUploaderOptions) {
  const [preview, setPreview] = useState<string | null>(initialUrl ?? null);
  const [uploading, setUploading] = useState(false);

  function sanitizeFilename(filename: string): string {
    const parts = filename.split('.');
    const ext = parts.pop(); // file extension
    const base = parts.join('-').replace(/\s+/g, '-');
    const timestamp = Date.now(); // or use dayjs() for formatted date
    return `${base}-${timestamp}.${ext}`.toLowerCase();
  }

  const uploadImage = async (file: File) => {
    if (!file) return;

    const cleanName = sanitizeFilename(file.name);
    const filePath = `${folder}/${siteId}/${cleanName}`;
    setUploading(true);
    setPreview(URL.createObjectURL(file));

    const { error: uploadError } = await supabase.storage
      .from('site-images')
      .upload(filePath, file, { upsert: true });

    if (uploadError) {
      console.error('Upload error:', uploadError.message);
      setUploading(false);
      return;
    }

    const { data: publicData } = supabase.storage.from('site-images').getPublicUrl(filePath);

    const publicUrl = publicData?.publicUrl;
    if (!publicUrl) return;

    const { error: updateError } = await supabase
      .from('templates')
      .update({
        [dbField]: publicUrl,
        [`${dbField}_meta`]: {
          originalName: file.name,
          path: filePath,
          uploadedAt: new Date().toISOString(),
        },
      })
      .eq('id', templateId);

    if (updateError) {
      console.error('DB update error:', updateError.message);
    } else {
      setPreview(publicUrl);
      onChange?.(publicUrl);
    }

    setUploading(false);
  };

  const removeImage = async () => {
    if (!preview) return;

    const { data: bucketInfo } = supabase.storage.from('site-images').getPublicUrl('');
    const bucketUrl = bucketInfo?.publicUrl;
    const path = preview.replace(`${bucketUrl}/`, '');

    const { error: deleteError } = await supabase.storage.from('site-images').remove([path]);

    if (deleteError) {
      console.error('Storage delete error:', deleteError.message);
      return;
    }

    const { error: updateError } = await supabase
      .from('templates')
      .update({ [dbField]: null })
      .eq('id', templateId);

    if (updateError) {
      console.error('DB remove error:', updateError.message);
    } else {
      setPreview(null);
      onChange?.(null);
    }
  };

  return {
    preview,
    uploading,
    uploadImage,
    removeImage,
  };
}

import { useEffect, useState } from 'react';
import { supabase } from '../admin/lib/supabaseClient';

type Filter = { column: keyof any; operator: 'eq' | 'neq'; value: any };
type Sort = { column: keyof any; ascending?: boolean };

export function useLiveTable<T extends { id: string | number }>(
  table: string,
  filter?: Filter,
  sort?: Sort
): T[] {
  const [rows, setRows] = useState<T[]>([]);

  useEffect(() => {
    let mounted = true;

    const fetchInitial = async () => {
      let query = supabase.from(table).select('*');

      if (filter) {
        query = query.filter(filter.column as string, filter.operator, filter.value);
      }

      if (sort) {
        query = query.order(sort.column as string, {
          ascending: sort.ascending ?? true,
        });
      }

      const { data, error } = await query;
      if (!error && mounted) setRows((data as T[]) || []);
    };

    fetchInitial();

    const subscription = supabase
      .channel(`realtime:${table}`)
      .on('postgres_changes', { event: '*', schema: 'public', table }, (payload) => {
        const applyFilter = (row: T) => {
          if (!filter) return true;
          switch (filter.operator) {
            case 'eq':
              return row[filter.column as keyof T] === filter.value;
            case 'neq':
              return row[filter.column as keyof T] !== filter.value;
            default:
              return true;
          }
        };

        setRows((prev) => {
          const updated = [...prev];
          const index = updated.findIndex((r) => r.id === (payload.new as T)?.id);

          switch (payload.eventType) {
            case 'INSERT':
              return applyFilter(payload.new as T) ? [...updated, payload.new as T] : updated;
            case 'UPDATE':
              if (index !== -1 && applyFilter(payload.new as T)) {
                updated[index] = payload.new as T;
              }
              return updated;
            case 'DELETE':
              return updated.filter((r) => r.id !== (payload.old as T)?.id);
            default:
              return updated;
          }
        });
      })
      .subscribe();

    return () => {
      mounted = false;
      supabase.removeChannel(subscription);
    };
  }, [table, filter?.column, filter?.operator, filter?.value, sort?.column, sort?.ascending]);

  return rows;
}

'use client';

import { useEffect, useState } from 'react';

export function useLiveTableWrapper<T>(table: string, filter: any, sort: any): T[] {
  const [data, setData] = useState<T[]>([]);

  useEffect(() => {
    let mounted = true;

    const fetchData = async () => {
      const mod = await import('./useLiveTable.jsx');
      const result = mod.useLiveTable<{ id: string | number } & T>(table, filter, sort);
      if (mounted) setData(result);
    };

    fetchData();

    return () => {
      mounted = false;
    };
  }, [table, JSON.stringify(filter), JSON.stringify(sort)]);

  return data;
}

import { useEffect, useState } from 'react';
import { useRouter, useSearchParams } from 'next/navigation';

export function useQueryFlash(key: string): string | null {
  const searchParams = useSearchParams();
  const [value, setValue] = useState<string | null>(null);

  useEffect(() => {
    const param = searchParams?.get(key);
    if (param && typeof param === 'string') {
      setValue(param);

      // Strip it from the URL without full reload
      const rest = new URLSearchParams(searchParams?.toString() || '');
      rest.delete(key);
      window.location.href = `${window.location.pathname}?${rest.toString()}`;
    }
  }, [searchParams?.get(key)]); // re-run if query changes

  return value;
}

import { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';
import { supabase } from '@/lib/supabase/client';

export function useResolvedRole(): [string | null, () => Promise<void>] {
  const [role, setRole] = useState<string | null>(null);

  const resolveRole = async () => {
    const { data: sessionData } = await supabase.auth.getSession();
    const user = sessionData.session?.user;

    if (!user) {
      setRole(null);
      return;
    }

    const email = user.email ?? '';
    const cacheKey = `cached-role-${email}`;
    let resolvedRole = localStorage.getItem(cacheKey) || 'viewer';

    const { data: profile } = await supabase
      .from('user_profiles')
      .select('role')
      .eq('user_id', user.id)
      .maybeSingle();

    if (profile?.role) {
      resolvedRole = profile.role;
      localStorage.setItem(cacheKey, resolvedRole);
    }

    setRole(resolvedRole);
  };

  useEffect(() => {
    resolveRole();
  }, []);

  return [role, resolveRole];
}

export function useRoleAccessGuard(allowedRoles: string[]) {
  const [role, resolveRole] = useResolvedRole();
  const router = useRouter();

  useEffect(() => {
    if (!role) return;

    const email = localStorage.getItem('cached-email') ?? '';
    const isOwner = email === 'sandonjurowski@gmail.com';

    if (!allowedRoles.includes(role) && !isOwner) {
      router.push('/unauthorized');
    }
  }, [role, allowedRoles, router]);

  return role;
}

export async function getServerResolvedRole(userId: string): Promise<string> {
  const { data: profile } = await supabase
    .from('user_profiles')
    .select('role')
    .eq('user_id', userId)
    .maybeSingle();

  return profile?.role ?? 'viewer';
}

